/**
 * Phase B - Task 23: Multi-Sig Attestation (‚â§220 LOC)
 * Threshold-based multi-signature verification and emission
 */

import { toB64u, fromB64u } from '../crypto/base64url';
import type { Sparkpack } from '../domain/pack/types';

// Mock dependencies for now - will integrate properly
const getSigner = async (_ns: string, kid: string) => ({
  kid,
  status: 'ACTIVE',
  pubB64u: 'AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA', // Valid Ed25519 public key (32 bytes)
  privB64u: 'AAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAAA', // Valid Ed25519 private key (32 bytes)
  retiredAt: null,
});
const AuditApi = { log: async (_event: string, _data: any) => {} };

// Types
export interface PackAttestV2 {
  v: 2;
  sigs: Array<{
    kid?: string;
    pubB64u?: string;
    sigB64u: string;
  }>;
}

export interface PackAttestV1 {
  v: 1;
  manifest: {
    content: { eventsHash: string; eventsCount: number };
    bytes: number;
    meta: any;
  };
  att: {
    alg: 'Ed25519';
    signer: string;
    sig: string;
    ts: string;
    kid?: string;
  };
}

export type PackAttest = PackAttestV1 | PackAttestV2;

export interface ThresholdPolicy {
  min: number;
  allowLegacy?: boolean;
  bannedKids?: string[];
  requireKids?: string[];
  retiredGraceMs?: number;
}

export interface MultiSigResult {
  ok: boolean;
  count: number;
  reasons?: string[];
}

// Canonical bytes (reuse Task 18)
function canonicalize(obj: any): string {
  if (obj === null || obj === undefined) return JSON.stringify(obj);
  if (typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canonicalize).join(',') + ']';
  const keys = Object.keys(obj).sort();
  const pairs = keys.map(k => `"${k}":${canonicalize(obj[k])}`);
  return '{' + pairs.join(',') + '}';
}

function getCanonicalBytes(pack: Sparkpack): ArrayBuffer {
  const canonical = canonicalize({ meta: pack.meta, events: pack.events });
  return new TextEncoder().encode(canonical).buffer;
}

/**
 * Emit multi-sig attestation using local signer keys
 */
export async function attestPackMulti(
  ns: string,
  kids: string[],
  pack: Sparkpack
): Promise<PackAttestV2> {
  const canonicalBytes = getCanonicalBytes(pack);
  const sigs: PackAttestV2['sigs'] = [];

  for (const kid of kids) {
    const signer = await getSigner(ns, kid);
    if (!signer) throw new Error(`Signer not found: ${kid}`);
    if (signer.status === 'REVOKED')
      throw new Error(`Cannot use revoked signer: ${kid}`);

    const privateKey = await crypto.subtle.importKey(
      'raw',
      fromB64u(signer.privB64u),
      { name: 'Ed25519' },
      false,
      ['sign']
    );
    const signature = await crypto.subtle.sign(
      'Ed25519',
      privateKey,
      canonicalBytes
    );
    sigs.push({ kid, sigB64u: toB64u(signature) });
  }

  await AuditApi.log('ATTEST_MULTI_EMIT', {
    namespace: ns,
    kids,
    count: kids.length,
  });
  return { v: 2, sigs };
}

/**
 * Verify multi-sig threshold under policy
 */
export async function verifyPackMulti(
  pack: Sparkpack,
  attest: PackAttest,
  ns: string,
  policy: ThresholdPolicy
): Promise<MultiSigResult> {
  const canonicalBytes = getCanonicalBytes(pack);

  // V1 backwards compatibility - reject if min > 1
  if (attest.v === 1) {
    if (policy.min > 1) {
      const reasons = [`threshold_not_met:min=${policy.min},count=1`];
      await AuditApi.log('ATTEST_MULTI_VERIFY', {
        namespace: ns,
        min: policy.min,
        count: 0,
        ok: false,
        reasons,
      });
      return { ok: false, count: 0, reasons };
    }

    // Verify single v1 signature
    let pubKey: CryptoKey;
    let effectiveKid = attest.att.kid;

    if (attest.att.kid) {
      const signer = await getSigner(ns, attest.att.kid);
      if (!signer)
        return {
          ok: false,
          count: 0,
          reasons: [`signer_not_found:${attest.att.kid}`],
        };
      if (signer.status === 'REVOKED')
        return {
          ok: false,
          count: 0,
          reasons: [`revoked_kid:${attest.att.kid}`],
        };

      if (
        signer.status === 'RETIRED' &&
        (!policy.retiredGraceMs ||
          !signer.retiredAt ||
          Date.now() - new Date(signer.retiredAt).getTime() >
            policy.retiredGraceMs)
      ) {
        return {
          ok: false,
          count: 0,
          reasons: [`retired_out_of_grace:${attest.att.kid}`],
        };
      }

      pubKey = await crypto.subtle.importKey(
        'raw',
        fromB64u(signer.pubB64u),
        { name: 'Ed25519' },
        false,
        ['verify']
      );
    } else {
      if (!policy.allowLegacy)
        return { ok: false, count: 0, reasons: ['legacy_not_allowed'] };
      effectiveKid = `legacy:${attest.att.signer.slice(0, 16)}`;
      pubKey = await crypto.subtle.importKey(
        'raw',
        fromB64u(attest.att.signer),
        { name: 'Ed25519' },
        false,
        ['verify']
      );
    }

    const isValid = await crypto.subtle.verify(
      'Ed25519',
      pubKey,
      fromB64u(attest.att.sig),
      canonicalBytes
    );
    const result = {
      ok: isValid,
      count: isValid ? 1 : 0,
      ...(!isValid && { reasons: [`sig_verify_failed:${effectiveKid}`] }),
    };

    await AuditApi.log('ATTEST_MULTI_VERIFY', {
      namespace: ns,
      min: policy.min,
      count: result.count,
      ok: result.ok,
    });
    return result;
  }

  // V2 multi-sig verification
  let valid = 0;
  const seenKids = new Set<string>();
  const reasons: string[] = [];
  const validKids = new Set<string>();

  for (const sig of attest.sigs) {
    if (sig.kid && seenKids.has(sig.kid)) continue; // Dedup by kid

    let pubKey: CryptoKey;
    let effectiveKid = sig.kid;

    if (sig.kid) {
      seenKids.add(sig.kid);
      const signer = await getSigner(ns, sig.kid);
      if (!signer) {
        reasons.push(`signer_not_found:${sig.kid}`);
        continue;
      }
      if (signer.status === 'REVOKED') {
        reasons.push(`revoked_kid:${sig.kid}`);
        continue;
      }

      if (
        signer.status === 'RETIRED' &&
        (!policy.retiredGraceMs ||
          !signer.retiredAt ||
          Date.now() - new Date(signer.retiredAt).getTime() >
            policy.retiredGraceMs)
      ) {
        reasons.push(`retired_out_of_grace:${sig.kid}`);
        continue;
      }

      try {
        pubKey = await crypto.subtle.importKey(
          'raw',
          fromB64u(signer.pubB64u),
          { name: 'Ed25519' },
          false,
          ['verify']
        );
      } catch {
        reasons.push(`key_import_failed:${sig.kid}`);
        continue;
      }
    } else if (sig.pubB64u) {
      if (!policy.allowLegacy) {
        reasons.push(`legacy_not_allowed`);
        continue;
      }
      effectiveKid = `legacy:${sig.pubB64u.slice(0, 16)}`;
      try {
        pubKey = await crypto.subtle.importKey(
          'raw',
          fromB64u(sig.pubB64u),
          { name: 'Ed25519' },
          false,
          ['verify']
        );
      } catch {
        reasons.push(`legacy_key_import_failed`);
        continue;
      }
    } else {
      reasons.push('missing_kid_and_pub');
      continue;
    }

    try {
      const isValid = await crypto.subtle.verify(
        'Ed25519',
        pubKey,
        fromB64u(sig.sigB64u),
        canonicalBytes
      );
      if (isValid) {
        valid++;
        if (effectiveKid) validKids.add(effectiveKid);
      } else {
        reasons.push(`sig_verify_failed:${effectiveKid || 'unknown'}`);
      }
    } catch {
      reasons.push(`sig_verify_error:${effectiveKid || 'unknown'}`);
    }
  }

  // Policy enforcement
  let ok = true;

  if (policy.bannedKids) {
    for (const banned of policy.bannedKids) {
      if (validKids.has(banned)) {
        reasons.push(`banned_kid:${banned}`);
        ok = false;
      }
    }
  }

  if (policy.requireKids) {
    for (const required of policy.requireKids) {
      if (!validKids.has(required)) {
        reasons.push(`require_kid_missing:${required}`);
        ok = false;
      }
    }
  }

  if (valid < policy.min) {
    reasons.push(`threshold_not_met:min=${policy.min},count=${valid}`);
    ok = false;
  }

  await AuditApi.log('ATTEST_MULTI_VERIFY', {
    namespace: ns,
    min: policy.min,
    count: valid,
    ok,
    ...(reasons.length > 0 && { reasons }),
  });
  return { ok, count: valid, ...(reasons.length > 0 && { reasons }) };
}

/**
 * Phase B - Task 23: Multi-Sig Attestation Types
 * Threshold-based multi-signature verification for Sparkpacks
 */

// Multi-signature attestation (v2)
export interface PackAttestV2 {
  v: 2;
  sigs: Array<{
    kid?: string; // preferred: bound to local signer registry
    pubB64u?: string; // legacy/federated path (policy-gated)
    sigB64u: string; // Ed25519 signature over canonical bytes
  }>;
}

// Policy for threshold verification
export interface ThresholdPolicy {
  min: number; // M in M-of-N threshold
  allowLegacy?: boolean; // permit sigs without kid if pubB64u trusted
  bannedKids?: string[]; // disallow specific signers
  requireKids?: string[]; // require all of these kids to be present & valid
  retiredGraceMs?: number; // allow RETIRED within grace period
}

// Verification result
export interface MultiSigResult {
  ok: boolean;
  count: number; // number of valid signatures
  reasons?: string[]; // error reasons if verification fails
}

// Union type for backwards compatibility
export type PackAttest = PackAttestV1 | PackAttestV2;

// Re-export v1 for compatibility
export interface PackAttestV1 {
  v: 1;
  manifest: {
    content: {
      eventsHash: string;
      eventsCount: number;
    };
    bytes: number;
    meta: any;
  };
  att: {
    alg: 'Ed25519';
    signer: string;
    sig: string;
    ts: string;
    kid?: string;
  };
}

/**
 * Phase B - Task 23: Multi-Sig Attestation Engine
 * Threshold-based multi-signature verification and emission
 */

import * as AuditApi from '../audit/api';
import { toB64u, fromB64u } from '../crypto/base64url';
import type { Sparkpack } from '../domain/sparkpack/types';
import { checkPolicy } from '../policy/engine';
import { getSigner } from '../signer/registry';

import type {
  PackAttestV1,
  PackAttestV2,
  ThresholdPolicy,
  MultiSigResult,
  PackAttest,
} from './multi-sig-types';

// Canonical JSON serialization (reused from Task 18)
function canonicalize(obj: any): string {
  if (obj === null || obj === undefined) return JSON.stringify(obj);
  if (typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canonicalize).join(',') + ']';

  const keys = Object.keys(obj).sort();
  const pairs = keys.map(k => `"${k}":${canonicalize(obj[k])}`);
  return '{' + pairs.join(',') + '}';
}

// Generate canonical bytes for signature (reused from Task 18)
function getCanonicalBytes(pack: Sparkpack): Uint8Array {
  const canonical = canonicalize({
    meta: pack.meta,
    events: pack.events,
  });
  return new TextEncoder().encode(canonical);
}

/**
 * Emit multi-sig attestation using local signer keys
 */
export async function attestPackMulti(
  ns: string,
  kids: string[],
  pack: Sparkpack
): Promise<PackAttestV2> {
  const canonicalBytes = getCanonicalBytes(pack);
  const sigs: PackAttestV2['sigs'] = [];

  // Check emission policy
  const policyCheck = await checkPolicy(ns, 'attestation.emit.multi', {
    kids,
    count: kids.length,
  });
  if (!policyCheck.allowed) {
    throw new Error(
      `Multi-sig emission denied by policy: ${policyCheck.reason}`
    );
  }

  for (const kid of kids) {
    // Fetch signer from registry
    const signer = await getSigner(ns, kid);
    if (!signer) {
      throw new Error(`Signer not found: ${kid}`);
    }

    if (signer.status === 'REVOKED') {
      throw new Error(`Cannot use revoked signer: ${kid}`);
    }

    // Sign canonical bytes
    const privateKey = await crypto.subtle.importKey(
      'raw',
      fromB64u(signer.privB64u),
      { name: 'Ed25519' },
      false,
      ['sign']
    );

    const signature = await crypto.subtle.sign(
      'Ed25519',
      privateKey,
      canonicalBytes
    );

    sigs.push({
      kid,
      sigB64u: toB64u(new Uint8Array(signature)),
    });
  }

  // Audit emission
  await AuditApi.log('ATTEST_MULTI_EMIT', {
    namespace: ns,
    kids,
    count: kids.length,
    eventsHash: pack.meta.eventsHash,
  });

  return { v: 2, sigs };
}

/**
 * Verify multi-sig threshold under policy
 */
export async function verifyPackMulti(
  pack: Sparkpack,
  attest: PackAttest,
  ns: string,
  policy: ThresholdPolicy
): Promise<MultiSigResult> {
  const canonicalBytes = getCanonicalBytes(pack);

  // Handle v1 backwards compatibility
  if (attest.v === 1) {
    return await verifyV1Compat(pack, attest, ns, policy, canonicalBytes);
  }

  // V2 multi-sig verification
  let valid = 0;
  const seenKids = new Set<string>();
  const reasons: string[] = [];
  const validKids = new Set<string>();

  for (const sig of attest.sigs) {
    // Deduplication by kid
    if (sig.kid && seenKids.has(sig.kid)) {
      continue; // Skip duplicate kid
    }

    let pubKey: CryptoKey | null = null;
    let effectiveKid = sig.kid;

    // Resolve public key
    if (sig.kid) {
      seenKids.add(sig.kid);

      // Look up signer in registry
      const signer = await getSigner(ns, sig.kid);
      if (!signer) {
        reasons.push(`signer_not_found:${sig.kid}`);
        continue;
      }

      // Check signer status
      if (signer.status === 'REVOKED') {
        reasons.push(`revoked_kid:${sig.kid}`);
        continue;
      }

      if (signer.status === 'RETIRED') {
        if (!policy.retiredGraceMs || !signer.retiredAt) {
          reasons.push(`retired_out_of_grace:${sig.kid}`);
          continue;
        }

        const retiredTime = new Date(signer.retiredAt).getTime();
        const now = Date.now();
        if (now - retiredTime > policy.retiredGraceMs) {
          reasons.push(`retired_out_of_grace:${sig.kid}`);
          continue;
        }
      }

      // Import public key
      try {
        pubKey = await crypto.subtle.importKey(
          'raw',
          fromB64u(signer.pubB64u),
          { name: 'Ed25519' },
          false,
          ['verify']
        );
      } catch (error) {
        reasons.push(`key_import_failed:${sig.kid}`);
        continue;
      }
    } else if (sig.pubB64u) {
      // Legacy path - policy gated
      if (!policy.allowLegacy) {
        reasons.push(`legacy_not_allowed:${sig.pubB64u.slice(0, 8)}...`);
        continue;
      }

      // For legacy sigs, create pseudo-kid for policy checks
      effectiveKid = `legacy:${sig.pubB64u.slice(0, 16)}`;

      try {
        pubKey = await crypto.subtle.importKey(
          'raw',
          fromB64u(sig.pubB64u),
          { name: 'Ed25519' },
          false,
          ['verify']
        );
      } catch (error) {
        reasons.push(`legacy_key_import_failed:${sig.pubB64u.slice(0, 8)}...`);
        continue;
      }
    } else {
      reasons.push('missing_kid_and_pub');
      continue;
    }

    // Verify signature
    try {
      const isValid = await crypto.subtle.verify(
        'Ed25519',
        pubKey,
        fromB64u(sig.sigB64u),
        canonicalBytes
      );

      if (isValid) {
        valid++;
        if (effectiveKid) {
          validKids.add(effectiveKid);
        }
      } else {
        reasons.push(`sig_verify_failed:${effectiveKid || 'unknown'}`);
      }
    } catch (error) {
      reasons.push(`sig_verify_error:${effectiveKid || 'unknown'}`);
    }
  }

  // Policy checks
  let ok = true;

  // Check banned kids
  if (policy.bannedKids) {
    for (const bannedKid of policy.bannedKids) {
      if (validKids.has(bannedKid)) {
        reasons.push(`banned_kid:${bannedKid}`);
        ok = false;
      }
    }
  }

  // Check required kids
  if (policy.requireKids) {
    for (const requiredKid of policy.requireKids) {
      if (!validKids.has(requiredKid)) {
        reasons.push(`require_kid_missing:${requiredKid}`);
        ok = false;
      }
    }
  }

  // Check threshold
  if (valid < policy.min) {
    reasons.push(`threshold_not_met:min=${policy.min},count=${valid}`);
    ok = false;
  }

  // Final result
  if (ok && reasons.length === 0) {
    ok = true;
  } else if (reasons.length > 0) {
    ok = false;
  }

  // Audit verification
  await AuditApi.log('ATTEST_MULTI_VERIFY', {
    namespace: ns,
    min: policy.min,
    count: valid,
    ok,
    eventsHash: pack.meta.eventsHash,
    ...(reasons.length > 0 && { reasons }),
  });

  return { ok, count: valid, ...(reasons.length > 0 && { reasons }) };
}

/**
 * Handle v1 backwards compatibility
 */
async function verifyV1Compat(
  pack: Sparkpack,
  attest: PackAttestV1,
  ns: string,
  policy: ThresholdPolicy,
  canonicalBytes: Uint8Array
): Promise<MultiSigResult> {
  // V1 can only satisfy min=1 or less
  if (policy.min > 1) {
    await AuditApi.log('ATTEST_MULTI_VERIFY', {
      namespace: ns,
      min: policy.min,
      count: 0,
      ok: false,
      eventsHash: pack.meta.eventsHash,
      reasons: [`v1_incompatible_with_threshold:min=${policy.min}`],
    });

    return {
      ok: false,
      count: 0,
      reasons: [`v1_incompatible_with_threshold:min=${policy.min}`],
    };
  }

  // Verify v1 signature (reuse existing logic)
  let pubKey: CryptoKey | null = null;
  let effectiveKid = attest.att.kid;

  if (attest.att.kid) {
    const signer = await getSigner(ns, attest.att.kid);
    if (!signer || signer.status === 'REVOKED') {
      const reason = signer
        ? `revoked_kid:${attest.att.kid}`
        : `signer_not_found:${attest.att.kid}`;
      return { ok: false, count: 0, reasons: [reason] };
    }

    if (signer.status === 'RETIRED') {
      if (!policy.retiredGraceMs || !signer.retiredAt) {
        return {
          ok: false,
          count: 0,
          reasons: [`retired_out_of_grace:${attest.att.kid}`],
        };
      }

      const retiredTime = new Date(signer.retiredAt).getTime();
      const now = Date.now();
      if (now - retiredTime > policy.retiredGraceMs) {
        return {
          ok: false,
          count: 0,
          reasons: [`retired_out_of_grace:${attest.att.kid}`],
        };
      }
    }

    pubKey = await crypto.subtle.importKey(
      'raw',
      fromB64u(signer.pubB64u),
      { name: 'Ed25519' },
      false,
      ['verify']
    );
  } else {
    if (!policy.allowLegacy) {
      return { ok: false, count: 0, reasons: ['legacy_not_allowed'] };
    }

    effectiveKid = `legacy:${attest.att.signer.slice(0, 16)}`;
    pubKey = await crypto.subtle.importKey(
      'raw',
      fromB64u(attest.att.signer),
      { name: 'Ed25519' },
      false,
      ['verify']
    );
  }

  // Verify signature
  const isValid = await crypto.subtle.verify(
    'Ed25519',
    pubKey,
    fromB64u(attest.att.sig),
    canonicalBytes
  );

  const result = {
    ok: isValid,
    count: isValid ? 1 : 0,
    ...(!isValid && {
      reasons: [`sig_verify_failed:${effectiveKid || 'unknown'}`],
    }),
  };

  await AuditApi.log('ATTEST_MULTI_VERIFY', {
    namespace: ns,
    min: policy.min,
    count: result.count,
    ok: result.ok,
    eventsHash: pack.meta.eventsHash,
    version: 1,
  });

  return result;
}

/**
 * Audit Log API - Phase B Task 12
 * Tamper-evident, encrypted, append-only audit trail
 */

import { toB64u } from '../crypto/base64url';
import type { StorageDriver } from '../storage/types';

import type { AuditEntry, AuditType, Query, Page } from './types';

let storage: StorageDriver;
let namespace: string;

export function configureAudit(storageDriver: StorageDriver, ns: string): void {
  storage = storageDriver;
  namespace = ns;
}

// Canonical JSON for stable hashing
function canon(obj: any): string {
  if (obj === null || typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canon).join(',') + ']';
  const keys = Object.keys(obj).sort();
  return '{' + keys.map(k => `"${k}":${canon(obj[k])}`).join(',') + '}';
}

// Hash function using WebCrypto
async function hash(data: string): Promise<string> {
  const buffer = await crypto.subtle.digest(
    'SHA-256',
    new TextEncoder().encode(data)
  );
  return toB64u(buffer);
}

// Generate ULID-like ID (timestamp + random)
function generateId(): string {
  return Date.now().toString(36) + Math.random().toString(36).slice(2, 8);
}

// Redact sensitive fields from context
function redactSensitive(
  ctx?: Record<string, unknown>
): Record<string, unknown> | undefined {
  if (!ctx) return undefined;
  const redacted = { ...ctx };
  const sensitive = [
    'passcode',
    'ctB64u',
    'ivB64u',
    'wrapped',
    'privateKey',
    'secret',
  ];
  for (const key of sensitive) {
    if (key in redacted) redacted[key] = '***';
  }
  for (const [key, value] of Object.entries(redacted)) {
    if (typeof value === 'string' && value.length > 200) {
      redacted[key] = value.slice(0, 200) + '...';
    }
  }
  return redacted;
}

// Get/set head hash
async function getHead(): Promise<string | null> {
  try {
    return await storage.getItem(`audit:${namespace}:head`);
  } catch {
    return null;
  }
}

async function setHead(hash: string): Promise<void> {
  await storage.setItem(`audit:${namespace}:head`, hash);
}

// Generate entry key for lexical ordering
function entryKey(ts: string, id: string): string {
  return `audit:${namespace}:e:${ts}:${id}`;
}

export async function log(
  type: AuditType,
  ctx?: Record<string, unknown>,
  actor?: string
): Promise<AuditEntry> {
  if (!storage || !namespace) {
    throw new Error('Audit not configured. Call configureAudit() first.');
  }

  const id = generateId();
  const ts = new Date().toISOString();
  const prev = await getHead();
  const redactedCtx = redactSensitive(ctx);

  // Build entry without hash
  const entryWithoutHash = {
    v: 1 as const,
    id,
    ts,
    ...(actor && { actor }),
    type,
    ...(redactedCtx && { ctx: redactedCtx }),
    ...(prev && { prev }),
  };

  // Compute hash: SHA256(prev || canon(entry))
  const entryHash = await hash((prev || '') + canon(entryWithoutHash));
  const entry: AuditEntry = { ...entryWithoutHash, hash: entryHash };

  // Store and update head
  await storage.setItem(entryKey(ts, id), JSON.stringify(entry));
  await setHead(entryHash);

  return entry;
}

export async function list(q?: Query): Promise<Page> {
  if (!storage || !namespace) {
    throw new Error('Audit not configured. Call configureAudit() first.');
  }

  const prefix = `audit:${namespace}:e:`;
  const limit = q?.limit || 50;

  // Build range constraints
  let startKey = prefix;
  let endKey = prefix + '\uFFFF';
  if (q?.since) startKey = prefix + q.since;
  if (q?.until) endKey = prefix + q.until + '\uFFFF';
  if (q?.cursor) startKey = q.cursor;

  // Get keys in range
  const filteredKeys = (await storage.listKeys(prefix))
    .filter(key => key >= startKey && key < endKey)
    .sort()
    .slice(0, limit + 1);

  // Fetch entries
  const items: AuditEntry[] = [];
  for (let i = 0; i < Math.min(filteredKeys.length, limit); i++) {
    try {
      const data = await storage.getItem(filteredKeys[i]);
      if (data) items.push(JSON.parse(data));
    } catch {
      // Skip corrupted entries
    }
  }

  return {
    items,
    ...(filteredKeys.length > limit && { nextCursor: filteredKeys[limit] }),
  };
}

export async function exportAll(): Promise<{
  items: AuditEntry[];
  valid: boolean;
}> {
  if (!storage || !namespace) {
    throw new Error('Audit not configured. Call configureAudit() first.');
  }

  const prefix = `audit:${namespace}:e:`;
  const entryKeys = (await storage.listKeys(prefix))
    .filter(key => key.startsWith(prefix))
    .sort();

  // Fetch all entries
  const allEntries: AuditEntry[] = [];
  for (const key of entryKeys) {
    try {
      const data = await storage.getItem(key);
      if (data) allEntries.push(JSON.parse(data));
    } catch {
      // Skip corrupted entries
    }
  }

  // Sort entries in hash chain order (follow prev pointers)
  const items = sortByChainOrder(allEntries);

  return { items, valid: await verifyChain(items) };
}

// Helper function to sort entries by hash chain order
function sortByChainOrder(entries: AuditEntry[]): AuditEntry[] {
  if (entries.length === 0) return [];

  const entryMap = new Map<string, AuditEntry>();
  const childMap = new Map<string | undefined, AuditEntry>();

  for (const entry of entries) {
    entryMap.set(entry.hash, entry);
    childMap.set(entry.prev, entry);
  }

  const first = childMap.get();
  if (!first) {
    return [...entries].sort(
      (a, b) => a.ts.localeCompare(b.ts) || a.id.localeCompare(b.id)
    );
  }

  const result: AuditEntry[] = [];
  let current: AuditEntry | undefined = first;
  while (current) {
    result.push(current);
    current = childMap.get(current.hash);
  }
  return result;
}

export async function verifyChain(items: AuditEntry[]): Promise<boolean> {
  if (items.length === 0) return true;

  let expectedPrev: string | undefined = undefined;
  for (const entry of items) {
    if (entry.prev !== expectedPrev) return false;
    const { hash: _, ...entryWithoutHash } = entry;
    const computedHash = await hash(
      (entry.prev || '') + canon(entryWithoutHash)
    );
    if (computedHash !== entry.hash) return false;
    expectedPrev = entry.hash;
  }
  return true;
}

import React, { useState, useMemo, useCallback } from 'react';

import { DESIGN_TOKENS } from '@/design/tokens';

// ===== TYPE DEFINITIONS =====

export interface DataTableColumn<T = any> {
  key: keyof T | string;
  header: string;
  render?: (value: any, record: T, index: number) => React.ReactNode;
  sortable?: boolean;
  filterable?: boolean;
  width?: string | number;
  align?: 'left' | 'center' | 'right';
  className?: string;
}

export interface DataTableProps<T = any> {
  data: T[];
  columns: DataTableColumn<T>[];
  loading?: boolean;
  className?: string;
  rowKey?: keyof T | ((record: T) => string | number);
  onRowClick?: (record: T, index: number) => void;
  onSort?: (key: string, direction: 'asc' | 'desc') => void;
  onFilter?: (key: string, value: string) => void;
  pagination?: {
    page: number;
    pageSize: number;
    total: number;
    onPageChange: (page: number) => void;
    onPageSizeChange: (pageSize: number) => void;
  };
  selection?: {
    selectedRowKeys: (string | number)[];
    onSelectionChange: (
      selectedRowKeys: (string | number)[],
      selectedRows: T[]
    ) => void;
    type?: 'checkbox' | 'radio';
  };
  expandable?: {
    expandedRowKeys: (string | number)[];
    onExpand: (expanded: boolean, record: T) => void;
    expandedRowRender: (record: T) => React.ReactNode;
  };
  density?: 'comfortable' | 'compact' | 'spacious';
  sticky?: boolean;
  scrollable?: boolean;
  maxHeight?: string | number;
  emptyState?: React.ReactNode;
  'data-testid'?: string;
}

interface SortState {
  key: string | null;
  direction: 'asc' | 'desc';
}

// ===== HELPER FUNCTIONS =====

function getRowKey<T>(
  record: T,
  index: number,
  rowKey?: keyof T | ((record: T) => string | number)
): string | number {
  if (typeof rowKey === 'function') {
    return rowKey(record);
  }
  if (rowKey && record[rowKey]) {
    return String(record[rowKey]);
  }
  return index;
}

function sortData<T>(
  data: T[],
  sortState: SortState,
  columns: DataTableColumn<T>[]
): T[] {
  if (!sortState.key) return data;

  const column = columns.find(col => col.key === sortState.key);
  if (!column) return data;

  return [...data].sort((a, b) => {
    const aVal = a[column.key as keyof T];
    const bVal = b[column.key as keyof T];

    let comparison = 0;
    if (aVal < bVal) comparison = -1;
    if (aVal > bVal) comparison = 1;

    return sortState.direction === 'desc' ? -comparison : comparison;
  });
}

// ===== MAIN COMPONENT =====

export function DataTable<T = any>({
  data,
  columns,
  loading = false,
  className = '',
  rowKey,
  onRowClick,
  onSort,
  pagination,
  selection,
  expandable,
  density = 'comfortable',
  scrollable = false,
  maxHeight,
  emptyState,
  'data-testid': testId = 'data-table',
}: DataTableProps<T>) {
  // ===== STATE MANAGEMENT =====

  const [sortState, setSortState] = useState<SortState>({
    key: null,
    direction: 'asc',
  });
  // TODO: Implement filtering functionality when needed
  // const [filters, setFilters] = useState<Record<string, string>>({});

  // ===== COMPUTED VALUES =====

  const processedData = useMemo(() => {
    let result = data;

    // Apply sorting
    result = sortData(result, sortState, columns);

    // Apply pagination if provided
    if (pagination) {
      const start = (pagination.page - 1) * pagination.pageSize;
      const end = start + pagination.pageSize;
      result = result.slice(start, end);
    }

    return result;
  }, [data, sortState, pagination, columns]);

  // ===== EVENT HANDLERS =====

  const handleSort = useCallback(
    (columnKey: string) => {
      const newDirection: 'asc' | 'desc' =
        sortState.key === columnKey && sortState.direction === 'asc'
          ? 'desc'
          : 'asc';
      const newSortState = { key: columnKey, direction: newDirection };

      setSortState(newSortState);
      onSort?.(columnKey, newDirection);
    },
    [sortState, onSort]
  );

  const handleRowClick = useCallback(
    (record: T, index: number) => {
      onRowClick?.(record, index);
    },
    [onRowClick]
  );

  const handleSelectionChange = useCallback(
    (recordKey: string | number, checked: boolean) => {
      if (!selection) return;

      const newSelectedKeys = checked
        ? [...selection.selectedRowKeys, recordKey]
        : selection.selectedRowKeys.filter(key => key !== recordKey);

      const newSelectedRows = data.filter(record =>
        newSelectedKeys.includes(
          getRowKey(record, data.indexOf(record), rowKey)
        )
      );

      selection.onSelectionChange(newSelectedKeys, newSelectedRows);
    },
    [selection, data, rowKey]
  );

  // ===== RENDER HELPERS =====

  const renderTableHeader = () => (
    <thead className={DESIGN_TOKENS.table.head}>
      <tr>
        {selection && (
          <th className={DESIGN_TOKENS.table.headCell}>
            <input
              type='checkbox'
              className={DESIGN_TOKENS.recipe.input.base}
              onChange={e => {
                const allKeys = data.map((record, index) =>
                  getRowKey(record, index, rowKey)
                );
                selection.onSelectionChange(
                  e.target.checked ? allKeys : [],
                  e.target.checked ? data : []
                );
              }}
              checked={
                selection.selectedRowKeys.length === data.length &&
                data.length > 0
              }
              ref={input => {
                if (input) {
                  input.indeterminate =
                    selection.selectedRowKeys.length > 0 &&
                    selection.selectedRowKeys.length < data.length;
                }
              }}
              aria-label='Select all rows'
            />
          </th>
        )}
        {expandable && (
          <th
            className={DESIGN_TOKENS.table.headCell}
            style={{ width: '48px' }}
          >
            {/* Expand/Collapse column header */}
          </th>
        )}
        {columns.map(column => (
          <th
            key={String(column.key)}
            className={`${DESIGN_TOKENS.table.headCell} ${column.sortable ? 'cursor-pointer hover:bg-slate-50 dark:hover:bg-slate-800' : ''}`}
            style={{ width: column.width, textAlign: column.align || 'left' }}
            onClick={
              column.sortable ? () => handleSort(String(column.key)) : undefined
            }
            aria-sort={
              sortState.key === column.key
                ? sortState.direction === 'asc'
                  ? 'ascending'
                  : 'descending'
                : 'none'
            }
          >
            <div className={DESIGN_TOKENS.layout.patterns.flexGap}>
              {column.header}
              {column.sortable && (
                <span className={DESIGN_TOKENS.icon.size.sm}>
                  {sortState.key === column.key
                    ? sortState.direction === 'asc'
                      ? '‚Üë'
                      : '‚Üì'
                    : '‚Üï'}
                </span>
              )}
            </div>
          </th>
        ))}
      </tr>
    </thead>
  );

  const renderTableBody = () => (
    <tbody>
      {loading ? (
        <tr>
          <td
            colSpan={
              columns.length + (selection ? 1 : 0) + (expandable ? 1 : 0)
            }
          >
            <div
              className={`${DESIGN_TOKENS.layout.patterns.flexGap} justify-center p-8`}
            >
              <div
                className={DESIGN_TOKENS.table.skeleton}
                style={{ width: '24px', height: '24px' }}
              />
              <span className={DESIGN_TOKENS.typography.body.secondary}>
                Loading...
              </span>
            </div>
          </td>
        </tr>
      ) : processedData.length === 0 ? (
        <tr>
          <td
            colSpan={
              columns.length + (selection ? 1 : 0) + (expandable ? 1 : 0)
            }
          >
            <div className='p-8 text-center'>
              {emptyState || (
                <div className={DESIGN_TOKENS.typography.body.secondary}>
                  No data available
                </div>
              )}
            </div>
          </td>
        </tr>
      ) : (
        processedData.map((record, index) => {
          const key = getRowKey(record, index, rowKey);
          const isSelected = selection?.selectedRowKeys.includes(key) || false;
          const isExpanded = expandable?.expandedRowKeys.includes(key) || false;

          return (
            <React.Fragment key={key}>
              <tr
                className={` ${DESIGN_TOKENS.table.row} ${DESIGN_TOKENS.table.rowDefault} ${onRowClick ? DESIGN_TOKENS.table.rowHoverable : ''} ${isSelected ? DESIGN_TOKENS.table.rowSelected : ''} ${DESIGN_TOKENS.density[density].rowY} `}
                onClick={() => handleRowClick(record, index)}
                role={onRowClick ? 'button' : undefined}
                tabIndex={onRowClick ? 0 : undefined}
                onKeyDown={e => {
                  if (onRowClick && (e.key === 'Enter' || e.key === ' ')) {
                    e.preventDefault();
                    handleRowClick(record, index);
                  }
                }}
              >
                {selection && (
                  <td
                    className={`${DESIGN_TOKENS.table.cell} ${DESIGN_TOKENS.density[density].cellX}`}
                  >
                    <input
                      type={selection.type || 'checkbox'}
                      checked={isSelected}
                      onChange={e =>
                        handleSelectionChange(key, e.target.checked)
                      }
                      onClick={e => e.stopPropagation()}
                      aria-label={`Select row ${index + 1}`}
                    />
                  </td>
                )}
                {expandable && (
                  <td
                    className={`${DESIGN_TOKENS.table.cell} ${DESIGN_TOKENS.density[density].cellX}`}
                  >
                    <button
                      className={DESIGN_TOKENS.recipe.button.ghost}
                      onClick={e => {
                        e.stopPropagation();
                        expandable.onExpand(!isExpanded, record);
                      }}
                      aria-label={isExpanded ? 'Collapse row' : 'Expand row'}
                    >
                      {isExpanded ? '‚àí' : '+'}
                    </button>
                  </td>
                )}
                {columns.map(column => (
                  <td
                    key={String(column.key)}
                    className={` ${DESIGN_TOKENS.table.cell} ${DESIGN_TOKENS.density[density].cellX} ${column.className || ''} `}
                    style={{ textAlign: column.align || 'left' }}
                  >
                    {column.render
                      ? column.render(
                          record[column.key as keyof T],
                          record,
                          index
                        )
                      : String(record[column.key as keyof T] || '')}
                  </td>
                ))}
              </tr>
              {expandable && isExpanded && (
                <tr>
                  <td colSpan={columns.length + (selection ? 1 : 0) + 1}>
                    <div className='bg-slate-50 p-4 dark:bg-slate-900/50'>
                      {expandable.expandedRowRender(record)}
                    </div>
                  </td>
                </tr>
              )}
            </React.Fragment>
          );
        })
      )}
    </tbody>
  );

  const renderPagination = () => {
    if (!pagination) return null;

    const { page, pageSize, total, onPageChange, onPageSizeChange } =
      pagination;
    const totalPages = Math.ceil(total / pageSize);

    return (
      <div
        className={`${DESIGN_TOKENS.layout.patterns.spaceBetween} border-t p-4`}
      >
        <div className={DESIGN_TOKENS.layout.patterns.flexGap}>
          <span className={DESIGN_TOKENS.typography.body.secondary}>
            Showing {(page - 1) * pageSize + 1} to{' '}
            {Math.min(page * pageSize, total)} of {total} entries
          </span>
        </div>
        <div className={DESIGN_TOKENS.layout.patterns.flexGap}>
          <select
            value={pageSize}
            onChange={e => onPageSizeChange(Number(e.target.value))}
            className={DESIGN_TOKENS.recipe.input.base}
            aria-label='Rows per page'
          >
            <option value={10}>10</option>
            <option value={25}>25</option>
            <option value={50}>50</option>
            <option value={100}>100</option>
          </select>
          <button
            disabled={page <= 1}
            onClick={() => onPageChange(page - 1)}
            className={DESIGN_TOKENS.recipe.button.outline}
            aria-label='Previous page'
          >
            Previous
          </button>
          <span className={DESIGN_TOKENS.typography.body.primary}>
            Page {page} of {totalPages}
          </span>
          <button
            disabled={page >= totalPages}
            onClick={() => onPageChange(page + 1)}
            className={DESIGN_TOKENS.recipe.button.outline}
            aria-label='Next page'
          >
            Next
          </button>
        </div>
      </div>
    );
  };

  // ===== MAIN RENDER =====

  const containerClassName = `
    ${DESIGN_TOKENS.table.container}
    ${scrollable ? 'overflow-auto' : ''}
    ${className}
  `.trim();

  const tableClassName = `
    ${DESIGN_TOKENS.table.base}
    ${DESIGN_TOKENS.density[density].text}
  `.trim();

  return (
    <div
      className={containerClassName}
      style={{ maxHeight }}
      data-testid={testId}
      role='region'
      aria-label='Data table'
    >
      <table className={tableClassName}>
        {renderTableHeader()}
        {renderTableBody()}
      </table>
      {renderPagination()}
    </div>
  );
}

