// ===== RESIZE HANDLE COMPONENT =====

const ResizeHandle = forwardRef<HTMLDivElement, ResizeHandleProps>(
  (
    {
      direction,
      position,
      panelIndex,
      active = false,
      size = 8,
      disabled = false,
      className,
      onMouseDown,
      onTouchStart,
      onKeyDown,
      ...props
    },
    ref
  ) => {
    const handleStyle =
      direction === 'horizontal'
        ? { width: `${size}px`, height: '100%' }
        : { width: '100%', height: `${size}px` };

    const indicatorStyle =
      direction === 'horizontal'
        ? {
            width: '2px',
            height: '40%',
            left: '50%',
            top: '50%',
            transform: 'translate(-50%, -50%)',
          }
        : {
            width: '40%',
            height: '2px',
            left: '50%',
            top: '50%',
            transform: 'translate(-50%, -50%)',
          };

    const handleKeyPress = useCallback(
      (event: React.KeyboardEvent) => {
        if (disabled) return;

        // Arrow key navigation for resize handles
        const { key } = event;
        const isRelevantKey =
          (direction === 'horizontal' &&
            (key === 'ArrowLeft' || key === 'ArrowRight')) ||
          (direction === 'vertical' &&
            (key === 'ArrowUp' || key === 'ArrowDown'));

        if (isRelevantKey) {
          event.preventDefault();
          onKeyDown?.(event, panelIndex);
        }
      },
      [direction, disabled, onKeyDown, panelIndex]
    );

    return (
      <div
        ref={ref}
        role='separator'
        aria-orientation={
          direction === 'horizontal' ? 'vertical' : 'horizontal'
        }
        aria-valuenow={panelIndex}
        aria-label={`Resize ${direction} panel ${panelIndex + 1}`}
        tabIndex={disabled ? -1 : 0}
        className={combineTokens(
          splitPanelTokens.handle[direction],
          disabled && splitPanelTokens.handle.disabled,
          active && 'ring-2 ring-primary-500',
          className
        )}
        style={handleStyle}
        onMouseDown={disabled ? undefined : e => onMouseDown?.(e, panelIndex)}
        onTouchStart={disabled ? undefined : e => onTouchStart?.(e, panelIndex)}
        onKeyDown={handleKeyPress}
        {...props}
      >
        <div
          className={splitPanelTokens.handle.indicator}
          style={indicatorStyle}
          aria-hidden='true'
        />
      </div>
    );
  }
);

ResizeHandle.displayName = 'ResizeHandle';

// ===== SPLIT PANEL COMPONENT =====

export const SplitPanel = forwardRef<HTMLDivElement, SplitPanelProps>(
  (
    {
      id,
      title,
      collapsible = false,
      collapsed = false,
      minSize,
      maxSize,
      className,
      'data-testid': testId,
      children,
      ...props
    },
    ref
  ) => {
    return (
      <div
        ref={ref}
        id={id}
        role='region'
        aria-label={title}
        aria-expanded={collapsible ? !collapsed : undefined}
        className={combineTokens(
          splitPanelTokens.panel.base,
          collapsible && splitPanelTokens.panel.collapsible,
          collapsed && splitPanelTokens.panel.collapsed,
          className
        )}
        data-testid={testId}
        data-min-size={minSize}
        data-max-size={maxSize}
        {...props}
      >
        <div className={splitPanelTokens.panel.content}>{children}</div>
      </div>
    );
  }
);

SplitPanel.displayName = 'SplitPanel';

// ===== MAIN SPLIT PANELS COMPONENT =====

export const SplitPanels = forwardRef<HTMLDivElement, SplitPanelsProps>(
  (
    {
      direction = 'horizontal',
      sizes: initialSizes = [],
      minSizes = [],
      maxSizes = [],
      resizable = true,
      collapsible = [],
      defaultCollapsed = [],
      handleSize = 8,
      resizeMode = 'smooth',
      persistSizes = false,
      storageKey = 'split-panels-sizes',
      touchEnabled = true,
      className,
      'aria-label': ariaLabel,
      'data-testid': testId,
      onSizeChange,
      onCollapse,
      onLayoutChange,
      children,
      ...props
    },
    ref
  ) => {
    const containerRef = useRef<HTMLDivElement>(null);
    const [isDragging, setIsDragging] = useState(false);
    const [dragHandle, setDragHandle] = useState<number | null>(null);
    const [dragStart, setDragStart] = useState<{ x: number; y: number } | null>(
      null
    );
    const [collapsed] = useState<boolean[]>(defaultCollapsed);

    // Convert children to array for easier manipulation
    const childArray = useMemo(() => Children.toArray(children), [children]);
    const panelCount = childArray.length;

    // Initialize panel sizes
    const [panelSizes, setPanelSizes] = useState<number[]>(() => {
      // Try to load from localStorage if persistence is enabled
      if (persistSizes && typeof globalThis !== 'undefined') {
        try {
          const saved = localStorage.getItem(storageKey);
          if (saved) {
            const parsed = JSON.parse(saved);
            if (Array.isArray(parsed) && parsed.length === panelCount) {
              return parsed;
            }
          }
        } catch (error) {
          console.warn('Failed to load persisted panel sizes:', error);
        }
      }

      // Use provided initial sizes or distribute equally
      if (initialSizes.length === panelCount) {
        return initialSizes.map(size =>
          typeof size === 'number' ? size : Number.parseFloat(size.toString())
        );
      }

      // Equal distribution
      return new Array(panelCount).fill(100 / panelCount);
    });

    // Performance monitoring hook
    const startTime = useRef<number>(0);

    const reportLayoutMetrics = useCallback(() => {
      if (!onLayoutChange || !containerRef.current) return;

      const containerSize =
        direction === 'horizontal'
          ? containerRef.current.offsetWidth
          : containerRef.current.offsetHeight;

      const metrics: LayoutMetrics = {
        containerSize,
        panelSizes: [...panelSizes],
        resizeTime: performance.now() - startTime.current,
        panelCount,
        collapsedPanels: [...collapsed],
      };

      onLayoutChange(metrics);
    }, [direction, panelSizes, panelCount, collapsed, onLayoutChange]);

    // Handle resize operations
    const handleResize = useCallback(
      (handleIndex: number, delta: number) => {
        if (!containerRef.current || !resizable) return;

        startTime.current = performance.now();

        const containerSize =
          direction === 'horizontal'
            ? containerRef.current.offsetWidth
            : containerRef.current.offsetHeight;

        const pixelSizes = panelSizes.map(size =>
          normalizeSize(size, containerSize)
        );
        const newSizes = [...pixelSizes];

        // Adjust adjacent panels
        const leftPanelIndex = handleIndex;
        const rightPanelIndex = handleIndex + 1;

        if (leftPanelIndex >= 0 && rightPanelIndex < newSizes.length) {
          newSizes[leftPanelIndex] += delta;
          newSizes[rightPanelIndex] -= delta;

          // Apply constraints
          newSizes[leftPanelIndex] = constrainSize(
            newSizes[leftPanelIndex],
            minSizes[leftPanelIndex],
            maxSizes[leftPanelIndex]
          );
          newSizes[rightPanelIndex] = constrainSize(
            newSizes[rightPanelIndex],
            minSizes[rightPanelIndex],
            maxSizes[rightPanelIndex]
          );

          // Convert back to percentages
          const percentageSizes = newSizes.map(
            size => (size / containerSize) * 100
          );

          setPanelSizes(percentageSizes);
          onSizeChange?.(percentageSizes, leftPanelIndex);

          // Persist sizes if enabled
          if (persistSizes && typeof globalThis !== 'undefined') {
            try {
              localStorage.setItem(storageKey, JSON.stringify(percentageSizes));
            } catch (error) {
              console.warn('Failed to persist panel sizes:', error);
            }
          }

          // Report metrics after a brief delay for performance measurement
          if (resizeMode === 'debounced') {
            setTimeout(reportLayoutMetrics, 100);
          } else {
            reportLayoutMetrics();
          }
        }
      },
      [
        direction,
        panelSizes,
        minSizes,
        maxSizes,
        resizable,
        onSizeChange,
        persistSizes,
        storageKey,
        resizeMode,
        reportLayoutMetrics,
      ]
    );

    // Mouse event handlers
    const handleMouseDown = useCallback(
      (event: React.MouseEvent, handleIndex: number) => {
        if (!resizable) return;

        event.preventDefault();
        setIsDragging(true);
        setDragHandle(handleIndex);
        setDragStart({ x: event.clientX, y: event.clientY });
      },
      [resizable]
    );

    const handleMouseMove = useCallback(
      (event: MouseEvent) => {
        if (!isDragging || dragHandle === null || !dragStart) return;

        const delta =
          direction === 'horizontal'
            ? event.clientX - dragStart.x
            : event.clientY - dragStart.y;

        handleResize(dragHandle, delta);
        setDragStart({ x: event.clientX, y: event.clientY });
      },
      [isDragging, dragHandle, dragStart, direction, handleResize]
    );

    const handleMouseUp = useCallback(() => {
      setIsDragging(false);
      setDragHandle(null);
      setDragStart(null);
    }, []);

    // Touch event handlers
    const handleTouchStart = useCallback(
      (event: React.TouchEvent, handleIndex: number) => {
        if (!resizable || !touchEnabled) return;

        event.preventDefault();
        const touch = event.touches[0];
        setIsDragging(true);
        setDragHandle(handleIndex);
        setDragStart({ x: touch.clientX, y: touch.clientY });
      },
      [resizable, touchEnabled]
    );

    const handleTouchMove = useCallback(
      (event: TouchEvent) => {
        if (!isDragging || dragHandle === null || !dragStart) return;

        event.preventDefault();
        const touch = event.touches[0];
        const delta =
          direction === 'horizontal'
            ? touch.clientX - dragStart.x
            : touch.clientY - dragStart.y;

        handleResize(dragHandle, delta);
        setDragStart({ x: touch.clientX, y: touch.clientY });
      },
      [isDragging, dragHandle, dragStart, direction, handleResize]
    );

    const handleTouchEnd = useCallback(() => {
      setIsDragging(false);
      setDragHandle(null);
      setDragStart(null);
    }, []);

    // Keyboard navigation for resize handles
    const handleKeyDown = useCallback(
      (event: React.KeyboardEvent, handleIndex: number) => {
        const { key, shiftKey } = event;
        const step = shiftKey ? 20 : 5; // Larger steps with Shift

        let delta = 0;

        if (direction === 'horizontal') {
          if (key === 'ArrowLeft') delta = -step;
          if (key === 'ArrowRight') delta = step;
        } else {
          if (key === 'ArrowUp') delta = -step;
          if (key === 'ArrowDown') delta = step;
        }

        if (delta !== 0) {
          event.preventDefault();
          handleResize(handleIndex, delta);
        }
      },
      [direction, handleResize]
    );

    // Panel collapse/expand functionality (available for future extension)
    // const toggleCollapse = useCallback((panelIndex: number) => {
    //   if (!collapsible[panelIndex]) return;

    //   const newCollapsed = [...collapsed];
    //   newCollapsed[panelIndex] = !newCollapsed[panelIndex];
    //   setCollapsed(newCollapsed);
    //   onCollapse?.(panelIndex, newCollapsed[panelIndex]);
    // }, [collapsed, collapsible, onCollapse]);

    // Set up global event listeners for drag operations
    useEffect(() => {
      if (isDragging) {
        document.addEventListener('mousemove', handleMouseMove);
        document.addEventListener('mouseup', handleMouseUp);
        document.addEventListener('touchmove', handleTouchMove, {
          passive: false,
        });
        document.addEventListener('touchend', handleTouchEnd);

        return () => {
          document.removeEventListener('mousemove', handleMouseMove);
          document.removeEventListener('mouseup', handleMouseUp);
          document.removeEventListener('touchmove', handleTouchMove);
          document.removeEventListener('touchend', handleTouchEnd);
        };
      }
    }, [
      isDragging,
      handleMouseMove,
      handleMouseUp,
      handleTouchMove,
      handleTouchEnd,
    ]);

    // ResizeObserver for container size changes
    useEffect(() => {
      if (!containerRef.current) return;

      const resizeObserver = new ResizeObserver(() => {
        // Trigger layout recalculation when container size changes
        reportLayoutMetrics();
      });

      resizeObserver.observe(containerRef.current);

      return () => resizeObserver.disconnect();
    }, [reportLayoutMetrics]);

    // Render panels with resize handles
    const renderPanelsWithHandles = () => {
      const elements: React.ReactNode[] = [];

      for (const [index, child] of childArray.entries()) {
        // Calculate panel size
        const sizeStyle =
          direction === 'horizontal'
            ? {
                width: collapsed[index]
                  ? '0px'
                  : `${panelSizes[index] || 100 / panelCount}%`,
                minWidth: collapsed[index]
                  ? '0px'
                  : `${minSizes[index] || 0}px`,
                maxWidth: maxSizes[index] ? `${maxSizes[index]}px` : 'none',
              }
            : {
                height: collapsed[index]
                  ? '0px'
                  : `${panelSizes[index] || 100 / panelCount}%`,
                minHeight: collapsed[index]
                  ? '0px'
                  : `${minSizes[index] || 0}px`,
                maxHeight: maxSizes[index] ? `${maxSizes[index]}px` : 'none',
              };

        // Clone child panel with additional props
        const panelElement = React.isValidElement(child)
          ? cloneElement(child as React.ReactElement<SplitPanelProps>, {
              collapsible: collapsible[index],
              collapsed: collapsed[index],
              minSize: minSizes[index],
              maxSize: maxSizes[index],
              style: {
                ...sizeStyle,
                ...child.props.style,
              },
            })
          : child;

        elements.push(
          <div key={index} style={sizeStyle}>
            {panelElement}
          </div>
        );

        // Add resize handle between panels (except after the last panel)
        if (index < childArray.length - 1 && resizable) {
          elements.push(
            <ResizeHandle
              key={`handle-${index}`}
              direction={direction}
              position='between'
              panelIndex={index}
              size={handleSize}
              active={dragHandle === index}
              onMouseDown={handleMouseDown}
              onTouchStart={handleTouchStart}
              onKeyDown={handleKeyDown}
            />
          );
        }
      }

      return elements;
    };

    return (
      <div
        ref={ref}
        role='group'
        aria-label={
          ariaLabel ||
          `Split panel container with ${panelCount} ${direction} panels`
        }
        className={combineTokens(
          splitPanelTokens.container[direction],
          isDragging && 'select-none',
          className
        )}
        data-testid={testId}
        data-direction={direction}
        data-panel-count={panelCount}
        data-resizable={resizable}
        {...props}
      >
        <div ref={containerRef} className='flex size-full'>
          {renderPanelsWithHandles()}
        </div>
      </div>
    );
  }
);

SplitPanels.displayName = 'SplitPanels';

// ===== COMPOUND COMPONENT EXPORTS =====

// Default export for convenience
export default SplitPanels;

/**
 * @fileoverview WorkspaceShell - Advanced Application Layout Component
 *
 * @description Enterprise-grade workspace shell providing comprehensive application
 * layout management with responsive design, theme integration, and accessibility compliance.
 *
 * Features:
 * - Multi-panel layout management (header, sidebar, main, footer)
 * - Responsive breakpoint handling with mobile-first design
 * - Theme integration with light/dark mode support
 * - Collapsible navigation with persistence
 * - Keyboard navigation and accessibility compliance
 * - Performance optimized with layout calculations
 * - SSOT compliance with DESIGN_TOKENS V3.2
 */

import React, {
  useState,
  useEffect,
  useCallback,
  useMemo,
  forwardRef,
} from 'react';

import { DESIGN_TOKENS } from '@/design/tokens';

// ===== TYPE DEFINITIONS =====

export type WorkspaceLayout =
  | 'default'
  | 'sidebar-left'
  | 'sidebar-right'
  | 'full-width'
  | 'split-view';
export type SidebarWidth = 'narrow' | 'normal' | 'wide' | 'auto';
export type HeaderHeight = 'compact' | 'normal' | 'tall';
export type FooterVisibility = 'hidden' | 'auto' | 'always';
export type ResponsiveBreakpoint = 'mobile' | 'tablet' | 'desktop' | 'wide';

export interface NavigationItem {
  id: string;
  label: string;
  icon?: React.ReactNode;
  href?: string;
  onClick?: () => void;
  active?: boolean;
  disabled?: boolean;
  children?: NavigationItem[];
  badge?: string | number;
}

export interface WorkspaceTheme {
  mode: 'light' | 'dark' | 'auto';
  accent?: string;
  customCss?: string;
}

export interface LayoutPreferences {
  sidebarCollapsed?: boolean;
  sidebarWidth?: SidebarWidth;
  headerHeight?: HeaderHeight;
  footerVisible?: boolean;
  compactMode?: boolean;
}

export interface WorkspaceShellProps
  extends React.HTMLAttributes<HTMLDivElement> {
  /** Layout configuration */
  layout?: WorkspaceLayout;

  /** Navigation items for sidebar */
  navigation?: NavigationItem[];

  /** Header content */
  header?: React.ReactNode;

  /** Footer content */
  footer?: React.ReactNode;

  /** Main content area */
  children: React.ReactNode;

  /** Sidebar content (overrides navigation) */
  sidebar?: React.ReactNode;

  /** Theme configuration */
  theme?: WorkspaceTheme;

  /** Layout preferences */
  preferences?: LayoutPreferences;

  /** Responsive behavior */
  responsive?: boolean;

  /** Loading state */
  loading?: boolean;

  /** Error state */
  error?: string | React.ReactNode;

  /** Event callbacks */
  onNavigationChange?: (item: NavigationItem) => void;
  onLayoutChange?: (layout: WorkspaceLayout) => void;
  onPreferencesChange?: (preferences: LayoutPreferences) => void;
  onThemeChange?: (theme: WorkspaceTheme) => void;

  /** Accessibility */
  'aria-label'?: string;

  /** Testing */
  'data-testid'?: string;
}

// ===== UTILITY FUNCTIONS =====

const getBreakpoint = (width: number): ResponsiveBreakpoint => {
  if (width < 768) return 'mobile';
  if (width < 1024) return 'tablet';
  if (width < 1440) return 'desktop';
  return 'wide';
};

const getLayoutClasses = (
  layout: WorkspaceLayout,
  sidebarCollapsed: boolean,
  breakpoint: ResponsiveBreakpoint
) => {
  const baseClasses = [
    DESIGN_TOKENS.layout.container,
    'p-0',
    'min-h-screen',
    'flex',
    'flex-col',
  ];

  const layoutSpecificClasses = {
    default: ['grid', 'grid-rows-[auto_1fr_auto]'],
    'sidebar-left': [
      'grid',
      `grid-cols-[${sidebarCollapsed ? 'auto' : '280px'}_1fr]`,
      'grid-rows-[auto_1fr_auto]',
    ],
    'sidebar-right': [
      'grid',
      `grid-cols-[1fr_${sidebarCollapsed ? 'auto' : '280px'}]`,
      'grid-rows-[auto_1fr_auto]',
    ],
    'full-width': ['flex', 'flex-col'],
    'split-view': ['grid', 'grid-cols-2', 'grid-rows-[auto_1fr_auto]'],
  };

  const responsiveClasses =
    breakpoint === 'mobile'
      ? ['flex', 'flex-col']
      : layoutSpecificClasses[layout] || layoutSpecificClasses.default;

  return [...baseClasses, ...responsiveClasses].join(' ');
};

const getSidebarClasses = (
  collapsed: boolean,
  width: SidebarWidth,
  breakpoint: ResponsiveBreakpoint
) => {
  const baseClasses = [
    DESIGN_TOKENS.theme.light.surface.subtle,
    DESIGN_TOKENS.theme.light.border.subtle,
    'border-r',
    'transition-all',
    'duration-300',
    'ease-in-out',
  ];

  if (breakpoint === 'mobile') {
    return [
      ...baseClasses,
      'fixed',
      'inset-y-0',
      'left-0',
      'z-50',
      'w-80',
      'transform',
      collapsed ? '-translate-x-full' : 'translate-x-0',
    ].join(' ');
  }

  const widthClasses = {
    narrow: 'w-16',
    normal: 'w-64',
    wide: 'w-80',
    auto: 'w-auto',
  };

  return [
    ...baseClasses,
    collapsed ? 'w-16' : widthClasses[width] || widthClasses.normal,
    'overflow-hidden',
  ].join(' ');
};

const getHeaderClasses = (height: HeaderHeight) => {
  const baseClasses = [
    DESIGN_TOKENS.theme.light.surface.base,
    DESIGN_TOKENS.theme.light.border.subtle,
    'border-b',
    'col-span-full',
    'flex',
    'items-center',
    'px-6',
    'z-40',
  ];

  const heightClasses = {
    compact: 'h-12',
    normal: 'h-16',
    tall: 'h-20',
  };

  return [...baseClasses, heightClasses[height] || heightClasses.normal].join(
    ' '
  );
};

const getMainContentClasses = (hasError: boolean, loading: boolean) => {
  const baseClasses = [
    DESIGN_TOKENS.theme.light.surface.base,
    'flex-1',
    'overflow-auto',
    'relative',
  ];

  if (hasError) {
    baseClasses.push(DESIGN_TOKENS.semantic.background.error);
  }

  if (loading) {
    baseClasses.push('pointer-events-none');
  }

  return baseClasses.join(' ');
};

// ===== NAVIGATION COMPONENT =====

interface NavigationProps {
  items: NavigationItem[];
  collapsed: boolean;
  onItemClick: (item: NavigationItem) => void;
}

const Navigation: React.FC<NavigationProps> = ({
  items,
  collapsed,
  onItemClick,
}) => {
  const renderNavigationItem = useCallback(
    (item: NavigationItem, depth = 0) => {
      const handleClick = (e: React.MouseEvent) => {
        e.preventDefault();
        if (item.disabled) return;

        if (item.onClick) {
          item.onClick();
        }

        onItemClick(item);
      };

      const itemClasses = [
        'flex',
        'items-center',
        'w-full',
        'px-4',
        'py-3',
        'text-left',
        'transition-colors',
        'duration-200',
        DESIGN_TOKENS.typography.body.medium,
        item.active
          ? DESIGN_TOKENS.semantic.background.accent
          : 'hover:' + DESIGN_TOKENS.theme.light.surface.subtle,
        item.disabled
          ? DESIGN_TOKENS.theme.light.ink.disabled
          : DESIGN_TOKENS.theme.light.ink.primary,
        depth > 0 ? 'ml-4' : '',
      ]
        .filter(Boolean)
        .join(' ');

      return (
        <li key={item.id}>
          <button
            type='button'
            className={itemClasses}
            onClick={handleClick}
            disabled={item.disabled}
            aria-current={item.active ? 'page' : undefined}
            title={collapsed ? item.label : undefined}
          >
            {item.icon && (
              <span className={`${collapsed ? 'mr-0' : 'mr-3'} shrink-0`}>
                {item.icon}
              </span>
            )}

            {!collapsed && (
              <>
                <span className='flex-1 truncate'>{item.label}</span>
                {item.badge && (
                  <span
                    className={`ml-2 rounded-full px-2 py-1 text-xs ${DESIGN_TOKENS.semantic.background.accent} ${DESIGN_TOKENS.theme.light.ink.inverse}`}
                  >
                    {item.badge}
                  </span>
                )}
              </>
            )}
          </button>

          {item.children && !collapsed && (
            <ul className='mt-1'>
              {item.children.map(child =>
                renderNavigationItem(child, depth + 1)
              )}
            </ul>
          )}
        </li>
      );
    },
    [collapsed, onItemClick]
  );

  return (
    <nav className='flex-1 overflow-y-auto py-4' aria-label='Main navigation'>
      <ul className='space-y-1'>
        {items.map(item => renderNavigationItem(item))}
      </ul>
    </nav>
  );
};

// ===== MAIN COMPONENT =====

export const WorkspaceShell = forwardRef<HTMLDivElement, WorkspaceShellProps>(
  (
    {
      layout = 'sidebar-left',
      navigation = [],
      header,
      footer,
      children,
      sidebar,
      theme = { mode: 'light' },
      preferences = {},
      responsive = true,
      loading = false,
      error,
      onNavigationChange,
      onLayoutChange,
      onPreferencesChange,
      onThemeChange,
      className,
      'aria-label': ariaLabel = 'Application workspace',
      'data-testid': testId = 'workspace-shell',
      ...props
    },
    ref
  ) => {
    // ===== STATE MANAGEMENT =====

    const [currentBreakpoint, setCurrentBreakpoint] =
      useState<ResponsiveBreakpoint>('desktop');
    const [sidebarCollapsed, setSidebarCollapsed] = useState(
      preferences.sidebarCollapsed ?? false
    );
    const [mounted, setMounted] = useState(false);

    // ===== RESPONSIVE HANDLING =====

    useEffect(() => {
      setMounted(true);

      if (!responsive) return;

      const handleResize = () => {
        const breakpoint = getBreakpoint(window.innerWidth);
        setCurrentBreakpoint(breakpoint);

        // Auto-collapse sidebar on mobile
        if (breakpoint === 'mobile' && !sidebarCollapsed) {
          setSidebarCollapsed(true);
        }
      };

      handleResize();
      window.addEventListener('resize', handleResize);

      return () => window.removeEventListener('resize', handleResize);
    }, [responsive, sidebarCollapsed]);

    // ===== EVENT HANDLERS =====

    const handleSidebarToggle = useCallback(() => {
      const newCollapsed = !sidebarCollapsed;
      setSidebarCollapsed(newCollapsed);

      const newPreferences = { ...preferences, sidebarCollapsed: newCollapsed };
      onPreferencesChange?.(newPreferences);
    }, [sidebarCollapsed, preferences, onPreferencesChange]);

    const handleNavigationItemClick = useCallback(
      (item: NavigationItem) => {
        onNavigationChange?.(item);

        // Auto-close sidebar on mobile after navigation
        if (currentBreakpoint === 'mobile') {
          setSidebarCollapsed(true);
        }
      },
      [onNavigationChange, currentBreakpoint]
    );

    const handleKeyDown = useCallback(
      (e: React.KeyboardEvent) => {
        // Toggle sidebar with Ctrl/Cmd + Backslash
        if ((e.ctrlKey || e.metaKey) && e.key === '\\') {
          e.preventDefault();
          handleSidebarToggle();
        }
      },
      [handleSidebarToggle]
    );

    // ===== COMPUTED VALUES =====

    const layoutClasses = useMemo(
      () => getLayoutClasses(layout, sidebarCollapsed, currentBreakpoint),
      [layout, sidebarCollapsed, currentBreakpoint]
    );

    const sidebarClasses = useMemo(
      () =>
        getSidebarClasses(
          sidebarCollapsed,
          preferences.sidebarWidth || 'normal',
          currentBreakpoint
        ),
      [sidebarCollapsed, preferences.sidebarWidth, currentBreakpoint]
    );

    const headerClasses = useMemo(
      () => getHeaderClasses(preferences.headerHeight || 'normal'),
      [preferences.headerHeight]
    );

    const mainContentClasses = useMemo(
      () => getMainContentClasses(!!error, loading),
      [error, loading]
    );

    const showSidebar =
      (navigation.length > 0 || sidebar) && layout !== 'full-width';
    const showFooter = footer && preferences.footerVisible !== false;

    // ===== RENDER HELPERS =====

    const renderHeader = () => {
      if (!header) return null;

      return (
        <header className={headerClasses} data-testid='workspace-header'>
          {/* Sidebar toggle for mobile */}
          {showSidebar && currentBreakpoint === 'mobile' && (
            <button
              type='button'
              className={`mr-4 rounded-md p-2 ${DESIGN_TOKENS.theme.light.surface.subtle} hover:${DESIGN_TOKENS.theme.light.surface.muted}`}
              onClick={handleSidebarToggle}
              aria-label='Toggle navigation'
              data-testid='mobile-sidebar-toggle'
            >
              <svg
                className='size-5'
                fill='none'
                stroke='currentColor'
                viewBox='0 0 24 24'
              >
                <path
                  strokeLinecap='round'
                  strokeLinejoin='round'
                  strokeWidth={2}
                  d='M4 6h16M4 12h16M4 18h16'
                />
              </svg>
            </button>
          )}

          <div className='flex flex-1 items-center justify-between'>
            {header}
          </div>

          {/* Desktop sidebar toggle */}
          {showSidebar && currentBreakpoint !== 'mobile' && (
            <button
              type='button'
              className={`ml-4 rounded-md p-2 ${DESIGN_TOKENS.theme.light.surface.subtle} hover:${DESIGN_TOKENS.theme.light.surface.muted}`}
              onClick={handleSidebarToggle}
              aria-label={
                sidebarCollapsed ? 'Expand sidebar' : 'Collapse sidebar'
              }
              data-testid='desktop-sidebar-toggle'
            >
              <svg
                className='size-5'
                fill='none'
                stroke='currentColor'
                viewBox='0 0 24 24'
              >
                <path
                  strokeLinecap='round'
                  strokeLinejoin='round'
                  strokeWidth={2}
                  d={
                    sidebarCollapsed
                      ? 'M13 5l7 7-7 7M5 5l7 7-7 7'
                      : 'M11 19l-7-7 7-7m8 14l-7-7 7-7'
                  }
                />
              </svg>
            </button>
          )}
        </header>
      );
    };

    const renderSidebar = () => {
      if (!showSidebar) return null;

      return (
        <>
          {/* Mobile overlay */}
          {currentBreakpoint === 'mobile' && !sidebarCollapsed && (
            <div
              className='fixed inset-0 z-40 bg-black bg-opacity-50'
              onClick={handleSidebarToggle}
              data-testid='mobile-sidebar-overlay'
            />
          )}

          <aside
            className={sidebarClasses}
            data-testid='workspace-sidebar'
            aria-label='Navigation sidebar'
          >
            <div className='flex h-full flex-col'>
              {sidebar || (
                <Navigation
                  items={navigation}
                  collapsed={sidebarCollapsed}
                  onItemClick={handleNavigationItemClick}
                />
              )}
            </div>
          </aside>
        </>
      );
    };

    const renderMainContent = () => (
      <main className={mainContentClasses} data-testid='workspace-main'>
        {loading && (
          <div className='absolute inset-0 z-10 flex items-center justify-center bg-white bg-opacity-75'>
            <div
              className={`size-8 animate-spin rounded-full border-b-2 ${DESIGN_TOKENS.semantic.border.accent}`}
            />
          </div>
        )}

        {error ? (
          <div className='flex h-full items-center justify-center'>
            <div
              className={`p-8 text-center ${DESIGN_TOKENS.semantic.text.error}`}
            >
              <div className='mb-2 text-xl font-semibold'>Error</div>
              <div>{typeof error === 'string' ? error : error}</div>
            </div>
          </div>
        ) : (
          children
        )}
      </main>
    );

    const renderFooter = () => {
      if (!showFooter) return null;

      return (
        <footer
          className={`${DESIGN_TOKENS.theme.light.surface.subtle} ${DESIGN_TOKENS.theme.light.border.subtle} col-span-full border-t px-6 py-3`}
          data-testid='workspace-footer'
        >
          {footer}
        </footer>
      );
    };

    // ===== MAIN RENDER =====

    if (!mounted) {
      return (
        <div className='flex min-h-screen items-center justify-center'>
          <div
            className={`size-8 animate-spin rounded-full border-b-2 ${DESIGN_TOKENS.semantic.border.accent}`}
          />
        </div>
      );
    }

    return (
      <div
        ref={ref}
        className={`${layoutClasses} ${className || ''}`}
        onKeyDown={handleKeyDown}
        aria-label={ariaLabel}
        data-testid={testId}
        {...props}
      >
        {renderHeader()}
        {renderSidebar()}
        {renderMainContent()}
        {renderFooter()}
      </div>
    );
  }
);

