/**
 * Attachment Component
 *
 * Enterprise-grade attachment component for displaying and managing file attachments and links.
 * Supports multiple attachment types, preview capabilities, and comprehensive metadata display.
 *
 * Features:
 * - Multiple attachment types (file, link, image, document)
 * - Size variants (compact, default, detailed)
 * - Interactive actions (download, preview, remove)
 * - Progress tracking for uploads
 * - Accessibility compliance (WCAG 2.1 AA)
 * - Drag and drop support
 * - Thumbnail previews
 * - File type recognition with appropriate icons
 *
 * @version 1.0.0
 * @author SparkTasks Team
 */

import {
  FileText,
  Download,
  ExternalLink,
  Eye,
  X,
  Upload,
  Link2,
  FileImage,
  FileVideo,
  FileAudio,
  Archive,
  FileSpreadsheet,
  Presentation,
  AlertCircle,
  CheckCircle2,
  Clock,
  Paperclip,
} from 'lucide-react';
import React, { forwardRef, useState } from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// Attachment types
export type AttachmentType =
  | 'file'
  | 'link'
  | 'image'
  | 'document'
  | 'video'
  | 'audio'
  | 'archive'
  | 'spreadsheet'
  | 'presentation'
  | 'unknown';

// Attachment status types
export type AttachmentStatus = 'uploading' | 'complete' | 'error' | 'pending';

// Attachment size variants
export type AttachmentSize = 'compact' | 'default' | 'detailed';

// Attachment metadata interface
export interface AttachmentMetadata {
  fileName?: string;
  fileSize?: number;
  uploadedAt?: Date;
  uploadedBy?: string;
  description?: string;
  url?: string;
  thumbnailUrl?: string;
  mimeType?: string;
  isSecure?: boolean;
  expiresAt?: Date;
  downloadCount?: number;
}

// Main component props
export interface AttachmentProps {
  /** Attachment title/name */
  title: string;

  /** Attachment type */
  type?: AttachmentType;

  /** Attachment status */
  status?: AttachmentStatus;

  /** Display size variant */
  size?: AttachmentSize;

  /** Attachment metadata */
  metadata?: AttachmentMetadata;

  /** Upload progress (0-100) */
  progress?: number;

  /** Show preview action */
  showPreview?: boolean;

  /** Show download action */
  showDownload?: boolean;

  /** Show remove action */
  showRemove?: boolean;

  /** Show external link indicator */
  isExternal?: boolean;

  /** Disabled state */
  disabled?: boolean;

  /** Loading state */
  loading?: boolean;

  /** Custom actions */
  customActions?: React.ReactNode;

  /** Click handler for attachment */
  onClick?: () => void;

  /** Preview action handler */
  onPreview?: () => void;

  /** Download action handler */
  onDownload?: () => void;

  /** Remove action handler */
  onRemove?: () => void;

  /** Additional CSS classes */
  className?: string;

  /** Custom styles */
  style?: React.CSSProperties;
}

