 */
const getStatusIndicator = (status: AttachmentStatus): React.ReactNode => {
  const iconProps = {
    className: DESIGN_TOKENS.recipe.attachment.statusIcon.base,
  };

  switch (status) {
    case 'uploading': {
      return (
