        />
      );
    }
    case 'complete': {
      return (
        <CheckCircle2
          {...iconProps}
          className={DESIGN_TOKENS.recipe.attachment.statusIcon.complete}
        />
      );
    }
    case 'error': {
      return (
        <AlertCircle
          {...iconProps}
          className={DESIGN_TOKENS.recipe.attachment.statusIcon.error}
        />
      );
    }
    case 'pending': {
      return (
        <Clock
          {...iconProps}
