/**
 * Format file size for display
 */
const formatFileSize = (bytes: number): string => {
  if (bytes === 0) return '0 Bytes';

  const k = 1024;
  const sizes = ['Bytes', 'KB', 'MB', 'GB'];
  const i = Math.floor(Math.log(bytes) / Math.log(k));

  return (
    (Math.round((bytes / Math.pow(k, i)) * 10) / 10).toFixed(1) + ' ' + sizes[i]
  );
};

/**
 * Format date for display
 */
const formatDate = (date: Date): string => {
  return new Intl.DateTimeFormat('en-US', {
    year: 'numeric',
    month: 'short',
    day: 'numeric',
    hour: '2-digit',
    minute: '2-digit',
  }).format(date);
};

/**
 * Get attachment classes based on props
 */
const getAttachmentClasses = (
  size: AttachmentSize,
  disabled: boolean,
  loading: boolean,
  onClick?: () => void,
  hasCustomBackground?: boolean
): string => {
  const backgroundClass = hasCustomBackground ? '' : 'bg-white';
  const baseClasses = combineTokens(
    backgroundClass,
    'border border-gray-200 rounded-lg shadow-sm',
    DESIGN_TOKENS.spacing.section
  );

  const sizeClasses = {
    compact: DESIGN_TOKENS.spacing.stack,
    default: DESIGN_TOKENS.spacing.section,
    detailed: DESIGN_TOKENS.spacing.section,
  }[size];

  const interactiveClasses = onClick
    ? 'cursor-pointer hover:shadow-md hover:border-gray-300 transition-all duration-200'
    : '';

  const stateClasses = disabled
    ? 'opacity-50 cursor-not-allowed'
    : loading
      ? 'opacity-75'
      : '';

  return combineTokens(
    baseClasses,
    sizeClasses,
    interactiveClasses,
    stateClasses
  );
};

/**
 * Attachment Component
 *
 * Enterprise-grade attachment display component with comprehensive features.
 */
const Attachment = forwardRef<HTMLDivElement, AttachmentProps>(
  (
    {
      title,
      type = 'file',
      status = 'complete',
      size = 'default',
      metadata,
      progress,
      showPreview = true,
      showDownload = true,
      showRemove = false,
      isExternal = false,
      disabled = false,
      loading = false,
      customActions,
      onClick,
      onPreview,
      onDownload,
      onRemove,
      className,
      style,
      ...props
    },
    ref
  ) => {
    const [isHovered, setIsHovered] = useState(false);

    const hasCustomBackground = style?.backgroundColor !== undefined;
    const attachmentClasses = getAttachmentClasses(
      size,
      disabled,
      loading,
      onClick,
      hasCustomBackground
    );

    const handleClick = () => {
      if (!disabled && !loading && onClick) {
        onClick();
      }
    };

    const handleKeyDown = (event: React.KeyboardEvent) => {
      if (event.key === 'Enter' || event.key === ' ') {
        event.preventDefault();
        handleClick();
      }
    };

    const handleActionClick = (
      event: React.MouseEvent,
      action?: () => void
    ) => {
      event.stopPropagation();
      if (action && !disabled && !loading) {
        action();
      }
    };

    return (
      <div
        ref={ref}
        className={combineTokens(attachmentClasses, className)}
        style={style}
        onClick={handleClick}
        onKeyDown={handleKeyDown}
        onMouseEnter={() => setIsHovered(true)}
        onMouseLeave={() => setIsHovered(false)}
        tabIndex={onClick ? 0 : undefined}
        role={onClick ? 'button' : 'article'}
        aria-label={`Attachment: ${title}`}
        aria-disabled={disabled}
        {...props}
      >
        {/* Header Section */}
        <div className={DESIGN_TOKENS.recipe.attachment.container}>
          {/* Attachment Icon */}
          <div
            className={combineTokens(
              'mt-1',
              DESIGN_TOKENS.layout.flex.shrinkNone
            )}
          >
            {getTypeIcon(type)}
          </div>

          {/* Attachment Information */}
          <div className={DESIGN_TOKENS.recipe.attachment.content}>
            <div className={DESIGN_TOKENS.recipe.attachment.header}>
              {/* Title and Status */}
              <div className={DESIGN_TOKENS.recipe.attachment.content}>
                <h3
                  className={combineTokens(
                    DESIGN_TOKENS.typography.heading.h6,
                    'truncate'
                  )}
                >
                  {title}
                  {isExternal && (
                    <ExternalLink
                      className={combineTokens(
                        'ml-1 inline-block',
                        DESIGN_TOKENS.icon.size.xs,
                        DESIGN_TOKENS.semantic.text.muted
                      )}
                      aria-hidden='true'
                    />
                  )}
                  {metadata?.isSecure && (
                    <CheckCircle2
                      className={combineTokens(
                        'ml-1 inline-block',
                        DESIGN_TOKENS.icon.size.xs,
                        DESIGN_TOKENS.semantic.text.success
                      )}
                      aria-label='Secure'
                    />
                  )}
                </h3>

                {/* Status and Type */}
                <div className={DESIGN_TOKENS.recipe.attachment.metadata}>
                  {getStatusIndicator(status)}
                  <span
                    className={combineTokens(
                      DESIGN_TOKENS.typography.body.small,
                      DESIGN_TOKENS.semantic.text.muted
                    )}
                  >
                    {status.charAt(0).toUpperCase() + status.slice(1)}
                  </span>
                  <span className={DESIGN_TOKENS.semantic.text.muted}>‚Ä¢</span>
                  <span
                    className={combineTokens(
                      DESIGN_TOKENS.typography.body.small,
                      DESIGN_TOKENS.semantic.text.muted
                    )}
                  >
                    {type.toUpperCase()}
                  </span>
                </div>
              </div>

              {/* Action Buttons */}
              <div
                className={combineTokens(
                  'flex shrink-0 items-center gap-1',
                  isHovered ? 'opacity-100' : 'opacity-0',
                  'transition-opacity duration-200'
                )}
              >
                {showPreview && onPreview && (
                  <button
                    onClick={e => handleActionClick(e, onPreview)}
                    className={combineTokens(
                      DESIGN_TOKENS.recipe.button.ghost,
                      DESIGN_TOKENS.icon.size.md,
                      'p-0'
                    )}
                    aria-label='Preview attachment'
                    title='Preview'
                    type='button'
                  >
                    <Eye
                      className={combineTokens('size-4')}
                      aria-hidden='true'
                    />
                  </button>
                )}

                {showDownload && onDownload && (
                  <button
                    onClick={e => handleActionClick(e, onDownload)}
                    className={combineTokens(
                      DESIGN_TOKENS.recipe.button.ghost,
                      DESIGN_TOKENS.icon.size.md,
                      'p-0'
                    )}
                    aria-label='Download attachment'
                    title='Download'
                    type='button'
                  >
                    <Download
                      className={combineTokens('size-4')}
                      aria-hidden='true'
                    />
                  </button>
                )}

                {showRemove && onRemove && (
                  <button
                    onClick={e => handleActionClick(e, onRemove)}
                    className={combineTokens(
                      DESIGN_TOKENS.recipe.button.ghost,
                      DESIGN_TOKENS.icon.size.md,
                      'p-0 text-red-600 hover:text-red-700'
                    )}
                    aria-label='Remove attachment'
                    title='Remove'
                    type='button'
                  >
                    <X className={combineTokens('size-4')} aria-hidden='true' />
                  </button>
                )}

                {customActions}
              </div>
            </div>

            {/* Progress Bar (for uploading status) */}
            {status === 'uploading' && progress !== undefined && (
              <div className={combineTokens('mt-2')}>
                <div
                  className={combineTokens(
                    'h-1.5 w-full rounded-full bg-gray-200',
                    DESIGN_TOKENS.theme.light.radius.sm
                  )}
                >
                  <div
                    className={combineTokens(
                      'h-1.5 rounded-full bg-blue-600 transition-all duration-300',
                      DESIGN_TOKENS.theme.light.radius.sm
                    )}
                    style={{ width: `${progress}%` }}
                    aria-label={`Upload progress: ${progress}%`}
                  />
                </div>
                <div className={DESIGN_TOKENS.recipe.attachment.text.progress}>
                  {progress}% complete
                </div>
              </div>
            )}

            {/* Metadata Section */}
            {size !== 'compact' && (
              <div
                className={combineTokens(
                  'mt-2 flex items-center gap-4 text-xs',
                  DESIGN_TOKENS.semantic.text.muted
                )}
              >
                {/* File Size */}
                {metadata?.fileSize !== undefined && (
                  <span>{formatFileSize(metadata.fileSize)}</span>
                )}

                {/* Upload Date */}
                {metadata?.uploadedAt && (
                  <span>{formatDate(metadata.uploadedAt)}</span>
                )}

                {/* Uploaded By */}
                {metadata?.uploadedBy && <span>by {metadata.uploadedBy}</span>}

                {/* Download Count */}
                {metadata?.downloadCount !== undefined && (
                  <span>{metadata.downloadCount} downloads</span>
                )}
              </div>
            )}

            {/* Description (detailed view only) */}
            {size === 'detailed' && metadata?.description && (
              <div
                className={combineTokens(
                  'mt-2 text-sm',
                  DESIGN_TOKENS.semantic.text.muted
                )}
              >
                {metadata.description}
              </div>
            )}

            {/* Expiration Warning */}
            {metadata?.expiresAt && metadata.expiresAt < new Date() && (
              <div
                className={combineTokens(
                  'mt-2 flex items-center gap-1 text-xs',
                  DESIGN_TOKENS.semantic.text.error
                )}
              >
                <AlertCircle
                  className={combineTokens('size-3')}
                  aria-hidden='true'
                />
                <span>Expired on {formatDate(metadata.expiresAt)}</span>
              </div>
            )}
          </div>

          {/* Thumbnail (detailed view only) */}
          {size === 'detailed' && metadata?.thumbnailUrl && (
            <div className={combineTokens('mt-1', 'shrink-0')}>
              <img
                src={metadata.thumbnailUrl}
                alt={`Preview of ${title}`}
                className={combineTokens(
                  'h-12 w-12 rounded object-cover',
                  DESIGN_TOKENS.theme.light.radius.sm
                )}
              />
            </div>
          )}
        </div>
      </div>
    );
  }
);

Attachment.displayName = 'Attachment';

export { Attachment };

/**
 * @fileoverview Audio Component - Enterprise-grade audio player wrapper
 *
 * A comprehensive audio component that provides responsive behavior,
 * controls, waveform visualization, error handling, and accessibility.
 * Built for modern web applications with performance and UX in mind.
 *
 * Features:
 * - 5 size variants (xs, sm, md, lg, xl)
 * - 4 visual variants (default, rounded, minimal, compact)
 * - Album art/cover image support with fallback
 * - Error state with fallback content
 * - Loading state with skeleton placeholder
 * - Responsive behavior with multiple sources
 * - Progressive audio enhancement
 * - Full accessibility (WCAG 2.1 AA)
 * - Controls customization (play/pause, seek, volume, progress)
 * - Auto-play control
 * - Loop control
 * - Muted/unmuted states
 * - Audio quality selection
 * - Preload strategies
 * - Track information display
 * - Playback speed control
 * - Keyboard navigation support
 * - Waveform visualization (optional)
 *
 * @version 1.0.0
 * @author Spark Tasks Team
 * @since 2024
 */

import React, {
  forwardRef,
  useState,
  useCallback,
  useRef,
  useEffect,
  useMemo,
} from 'react';

import { DESIGN_TOKENS } from '@/design/tokens';

// ===== TYPES & INTERFACES =====

/**
 * Size variants for the audio player
 */
export type AudioSize =
  | 'xs' // Extra small - 240px base (compact player)
  | 'sm' // Small - 320px base
  | 'md' // Medium - 480px base
  | 'lg' // Large - 640px base
  | 'xl'; // Extra large - 800px base

/**
 * Visual style variants for the audio player
 */
export type AudioVariant =
  | 'default' // Standard appearance with all controls
  | 'rounded' // Rounded corners design
  | 'minimal' // Simplified controls
  | 'compact'; // Single-line compact player

/**
 * Audio source with different quality options
 */
export interface AudioSource {
  src: string;
  type?: string;
  quality?: 'low' | 'medium' | 'high' | 'lossless';
}

/**
 * Track information for the audio
 */
export interface AudioTrack {
  title?: string;
  artist?: string;
  album?: string;
  duration?: number;
  coverArt?: string;
}

/**
 * Preload strategy for audio loading
 */
export type AudioPreload =
  | 'none' // No preloading
  | 'metadata' // Preload only metadata
  | 'auto'; // Preload entire audio

/**
 * Props for the Audio component
 */
export interface AudioProps
  extends Omit<
    React.AudioHTMLAttributes<HTMLAudioElement>,
    'src' | 'preload' | 'onTimeUpdate' | 'onVolumeChange'
  > {
  // ===== CONTENT =====
  /** Audio source URL or array of sources for different qualities */
  src: string | AudioSource[];
  /** Alternative text for accessibility */
  alt?: string;
  /** Track information */
  track?: AudioTrack;

  // ===== LAYOUT & APPEARANCE =====
  /** Size variant */
  size?: AudioSize;
  /** Visual style variant */
  variant?: AudioVariant;
  /** Custom width (overrides size) */
  width?: number | string;
  /** Custom height (overrides size) */
  height?: number | string;

  // ===== BEHAVIOR =====
  /** Show/hide audio controls */
  controls?: boolean;
  /** Auto-play the audio */
  autoPlay?: boolean;
  /** Loop the audio */
  loop?: boolean;
  /** Start muted */
  muted?: boolean;
  /** Preload strategy */
  preload?: AudioPreload;
  /** Playback rate */
  playbackRate?: number;
  /** Show track information */
  showTrackInfo?: boolean;
  /** Show waveform visualization */
  showWaveform?: boolean;
  /** Enable keyboard shortcuts */
  enableKeyboard?: boolean;

  // ===== STATE =====
  /** Loading state override */
  loading?: boolean;
  /** Error message to display */
  error?: string;
  /** Disabled state */
  disabled?: boolean;

  // ===== CUSTOM COMPONENTS =====
  /** Custom loading component */
  loadingComponent?: React.ReactNode;
  /** Custom error fallback component */
  fallback?: React.ReactNode;

  // ===== STYLING =====
  /** Custom CSS class for audio element */
  className?: string;
  /** Custom CSS class for container */
  containerClassName?: string;
  /** Custom styles */
  style?: React.CSSProperties;

  // ===== EVENT HANDLERS =====
  /** Callback when audio starts playing */
  onPlay?: () => void;
  /** Callback when audio is paused */
  onPause?: () => void;
  /** Callback when audio ends */
  onEnded?: () => void;
  /** Callback when metadata loads */
  onLoadedMetadata?: (event: React.SyntheticEvent<HTMLAudioElement>) => void;
  /** Callback when audio can start playing */
  onCanPlay?: (event: React.SyntheticEvent<HTMLAudioElement>) => void;
  /** Callback during time updates */
  onTimeUpdate?: (currentTime: number, duration: number) => void;
  /** Callback when volume changes */
  onVolumeChange?: (volume: number, muted: boolean) => void;
  /** Callback when an error occurs */
  onError?: (event: React.SyntheticEvent<HTMLAudioElement>) => void;
}

// ===== HELPER FUNCTIONS =====

/**
 * Get aspect ratio classes based on the aspect ratio prop
 */
const getAspectRatioClasses = (variant: AudioVariant): string => {
  const aspectRatioMap: Record<AudioVariant, string> = {
    default: '', // Auto height based on content
    rounded: '',
    minimal: '',
    compact: 'h-16', // Fixed compact height
  };

  return aspectRatioMap[variant];
};

/**
 * Get size classes based on the size prop
 */
const getSizeClasses = (size: AudioSize): string => {
  const sizeMap: Record<AudioSize, string> = {
    xs: 'w-60', // 240px
    sm: 'w-80', // 320px
    md: 'w-[30rem]', // 480px
    lg: 'w-[40rem]', // 640px
    xl: 'w-[50rem]', // 800px
  };

  return sizeMap[size];
};

/**
 * Get variant classes based on the variant prop
 */
const getVariantClasses = (variant: AudioVariant): string => {
  const variantMap: Record<AudioVariant, string> = {
    default: DESIGN_TOKENS.theme.light.radius.md,
    rounded: DESIGN_TOKENS.theme.light.radius.lg,
    minimal: DESIGN_TOKENS.theme.light.radius.sm,
    compact: DESIGN_TOKENS.theme.light.radius.md,
  };

  return variantMap[variant];
};

/**
 * Get audio sources array from src prop
 */
const getAudioSources = (src: string | AudioSource[]): AudioSource[] => {
  if (typeof src === 'string') {
    return [{ src, type: 'audio/mpeg' }];
  }
  return src;
};

/**
 * Format time in MM:SS format
 */
const formatTime = (seconds: number): string => {
  if (isNaN(seconds)) return '0:00';
  const mins = Math.floor(seconds / 60);
  const secs = Math.floor(seconds % 60);
  return `${mins}:${secs.toString().padStart(2, '0')}`;
};

/**
 * Default loading component
 */
const DefaultLoadingComponent: React.FC = () => (
  <div className={`${DESIGN_TOKENS.utils.centerAll} h-full`}>
    <div
      className={`${DESIGN_TOKENS.semantic.background.muted} ${DESIGN_TOKENS.theme.light.radius.md} p-4`}
