// ===== MAIN COMPONENT =====

const Callout = forwardRef<HTMLDivElement, CalloutProps>(
  (
    {
      variant = 'info',
      size = 'md',
      title,
      icon,
      showIcon = true,
      dismissible = false,
      onDismiss,
      children,
      className,
      ...props
    },
    ref
  ) => {
    const [isDismissed, setIsDismissed] = useState(false);

    const handleDismiss = () => {
      setIsDismissed(true);
      onDismiss?.();
    };

    if (isDismissed) {
      return null;
    }

    const calloutClasses = getCalloutClasses(variant, size);
    const titleClasses = getTitleClasses(size);
