  ) => {
    const tokens = useMemo(() => getCarouselTokens(), []);

    // Process items from children or props
    const processedItems = useMemo(() => {
      if (items.length > 0) {
        return items;
      }

      if (children) {
        return (
          Children.map(children, (child, index) => ({
            id: `slide-${index}`,
            content: child,
          })) || []
        );
      }

      return [];
    }, [items, children]);

