    }, [isAutoPlaying, totalSlides, infinite, onSlideChange, autoPlayInterval]);

    const stopAutoPlay = useCallback(() => {
      if (autoPlayRef.current) {
        clearInterval(autoPlayRef.current);
        autoPlayRef.current = undefined;
      }
