        type: 'log' | 'error' | 'warn';
        message: string;
        timestamp: number;
      }>
    >([]);

    // Listen for messages from preview iframe
    useEffect(() => {
      const handleMessage = (event: MessageEvent) => {
        if (event.data.type === 'console') {
          // Add console message to state for direct display
          const messageText =
            typeof event.data.data === 'string'
              ? event.data.data
              : JSON.stringify(event.data.data);

          setConsoleMessages(prev => [
            ...prev,
            {
              type: 'log',
              message: messageText,
              timestamp: Date.now(),
            },
          ]);

          // Also handle console output from preview - add to execution result
          setExecutionResult(prev => {
            if (!prev) return null;
            return {
              ...prev,
              logs: [...(prev.logs || []), messageText],
            };
          });
        } else if (event.data.type === 'error') {
          // Add error message to state for direct display
          const errorText =
            typeof event.data.data === 'string'
              ? event.data.data
              : JSON.stringify(event.data.data);

          setConsoleMessages(prev => [
            ...prev,
            {
              type: 'error',
              message: errorText,
              timestamp: Date.now(),
            },
          ]);

          // Handle errors from preview
          setExecutionResult(prev => {
            if (!prev) return null;
            return {
              ...prev,
              error: errorText,
            };
          });
          // Bubble error to consumer for observability/tests
          onError?.(new Error(errorText));
        }
      };

      window.addEventListener('message', handleMessage);
      return () => window.removeEventListener('message', handleMessage);
    }, [onError]);

    // Cleanup timers on unmount
    useEffect(() => {
