            >
              <Copy className={DESIGN_TOKENS.layout.spacing.fine.size4} />
            </button>

            {/* Share button */}
            {enableSharing && (
              <button
                type='button'
                onClick={handleShare}
                className={DESIGN_TOKENS.recipe.iconButtonDefault}
                aria-label='Share code'
              >
                <Share2 className={DESIGN_TOKENS.layout.spacing.fine.size4} />
              </button>
            )}

            {/* Fullscreen toggle */}
            {enableFullscreen && (
              <button
                type='button'
