            {!executionResult && !isRunning && consoleMessages.length === 0 && (
              <div
                className={combineTokens(
                  DESIGN_TOKENS.recipe.codePlayground.consoleEmpty
                )}
              >
                Run code to see output...
              </div>
            )}
          </div>
        </div>
      );
    };

    return (
      <div
        ref={ref}
        className={containerClasses}
        role='region'
        aria-label={ariaLabel}
        {...props}
      >
        {renderToolbar()}

        <div
          className={combineTokens(
            DESIGN_TOKENS.recipe.codePlayground.layoutContainer
          )}
        >
          <div
            className={combineTokens(
              DESIGN_TOKENS.recipe.codePlayground.layoutInner,
              layout === 'vertical'
                ? DESIGN_TOKENS.recipe.codePlayground.layoutVertical
                : DESIGN_TOKENS.layout.flex.row
            )}
          >
            {renderEditor()}
            {renderPreview()}
          </div>

          {layout !== 'tabs' && renderConsole()}
          {layout === 'tabs' && activePane === 'console' && renderConsole()}
        </div>

        {/* Loading indicator */}
        {isRunning && (
          <div
            className={combineTokens(
              DESIGN_TOKENS.recipe.codePlayground.loadingOverlay
            )}
          >
            <div
              className={combineTokens(
                DESIGN_TOKENS.recipe.codePlayground.loadingCard
              )}
            >
              <div
                className={combineTokens(
                  DESIGN_TOKENS.recipe.codePlayground.loadingSpinner
                )}
              />
              <span
                className={combineTokens(
                  DESIGN_TOKENS.recipe.codePlayground.loadingText
                )}
              >
                Executing code...
              </span>
            </div>
          </div>
        )}

        {/* Success notification */}
        {copied && (
          <div
            className={combineTokens(
              DESIGN_TOKENS.recipe.codePlayground.successNotification
            )}
          >
            Copied to clipboard!
          </div>
        )}
      </div>
    );
  }
);

CodePlayground.displayName = 'CodePlayground';

export default CodePlayground;

import React, {
  createContext,
  useContext,
  useState,
  useRef,
  useEffect,
  useCallback,
  type ReactNode,
  type MouseEvent,
  type KeyboardEvent,
} from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// ===== TYPES =====
interface MenuItem {
  id: string;
  label?: string;
  icon?: ReactNode;
  disabled?: boolean;
  danger?: boolean;
  divider?: boolean;
  submenu?: MenuItem[];
  onClick?: () => void;
}

interface ContextMenuContextType {
  showMenu: (event: MouseEvent, items: MenuItem[]) => void;
  hideMenu: () => void;
  isVisible: boolean;
}

interface ContextMenuProviderProps {
  children: ReactNode;
}

interface ContextMenuTriggerProps {
  children: ReactNode;
  items: MenuItem[];
  disabled?: boolean;
}

interface ContextMenuProps {
  x: number;
  y: number;
  items: MenuItem[];
  onClose: () => void;
  visible: boolean;
}

interface ContextMenuItemProps {
  item: MenuItem;
  onSelect: (item: MenuItem) => void;
  onClose: () => void;
  isActive?: boolean;
  onActivate?: () => void;
}

// ===== CONTEXT =====
const ContextMenuContext = createContext<ContextMenuContextType | null>(null);

// ===== HOOK =====
export const useContextMenu = () => {
  const context = useContext(ContextMenuContext);
  if (!context) {
    throw new Error('useContextMenu must be used within a ContextMenuProvider');
  }
  return context;
};

// ===== CONTEXT MENU ITEM COMPONENT =====
const ContextMenuItem: React.FC<ContextMenuItemProps> = ({
  item,
  onSelect,
  onClose,
  isActive = false,
  onActivate,
}) => {
  const [showSubmenu, setShowSubmenu] = useState(false);
  const [submenuSide, setSubmenuSide] = useState<'right' | 'left'>('right');
  const itemRef = useRef<HTMLButtonElement>(null);
  const submenuRef = useRef<HTMLDivElement>(null);
  const closeTimerRef = useRef<number>();

  const hasSubmenu = !!(item.submenu && item.submenu.length > 0);

  // Focus this item when it becomes active
  useEffect(() => {
    if (isActive && itemRef.current) {
      itemRef.current.focus();
    }
  }, [isActive]);

  // Submenu positioning collision detection
  useEffect(() => {
    if (!showSubmenu || !submenuRef.current) return;

    const rect = submenuRef.current.getBoundingClientRect();
    if (rect.right > window.innerWidth) {
      setSubmenuSide('left');
    } else {
      setSubmenuSide('right');
    }
  }, [showSubmenu]);

  const handleClick = (event: MouseEvent) => {
    event.preventDefault();
    event.stopPropagation();

    if (item.disabled) return;

    if (hasSubmenu) {
      setShowSubmenu(!showSubmenu);
    } else {
      item.onClick?.();
      onSelect(item);
      onClose();
    }
  };

  const handleKeyDown = (event: KeyboardEvent) => {
    switch (event.key) {
      case 'Enter':
      case ' ': {
        event.preventDefault();
        if (!item.disabled) {
          if (hasSubmenu) {
            setShowSubmenu(!showSubmenu);
          } else {
            item.onClick?.();
            onSelect(item);
            onClose();
          }
        }
        break;
      }
      case 'ArrowRight': {
        if (hasSubmenu) {
          event.preventDefault();
          setShowSubmenu(true);
        }
        break;
      }
      case 'ArrowLeft': {
        if (hasSubmenu && showSubmenu) {
          event.preventDefault();
          setShowSubmenu(false);
          itemRef.current?.focus();
        }
        break;
      }
      // Note: Escape is handled at the menu level, not here
    }
  };

  const handleMouseEnter = () => {
    onActivate?.();
    if (closeTimerRef.current) {
      clearTimeout(closeTimerRef.current);
    }
    if (hasSubmenu) {
      setShowSubmenu(true);
    }
  };

  const handleMouseLeave = () => {
    if (hasSubmenu) {
      closeTimerRef.current = globalThis.setTimeout(() => {
        setShowSubmenu(false);
      }, 150); // Pointer intent grace period
    }
  };

  // Cleanup timer on unmount
  useEffect(() => {
    return () => {
      if (closeTimerRef.current) {
        clearTimeout(closeTimerRef.current);
      }
    };
  }, []);

  if (item.divider) {
    return (
      <div
        className={`my-1 h-px ${DESIGN_TOKENS.semantic.border.muted}`}
        role='separator'
        data-slot='divider'
      />
    );
  }

  return (
    <div className={combineTokens('relative')}>
      <button
        ref={itemRef}
        type='button'
        className={`w-full text-left ${DESIGN_TOKENS.recipe.dropdown.item} ${item.disabled ? 'cursor-not-allowed opacity-50' : ''} ${item.danger ? `${DESIGN_TOKENS.semantic.text.error} hover:${DESIGN_TOKENS.semantic.background.error}` : ''} ${showSubmenu ? DESIGN_TOKENS.theme.light.surface.subtle : ''} `}
        role='menuitem'
        disabled={!!item.disabled}
        tabIndex={isActive ? 0 : -1}
        aria-disabled={!!item.disabled}
        aria-haspopup={hasSubmenu ? 'menu' : undefined}
        aria-expanded={hasSubmenu ? showSubmenu : undefined}
        data-slot='item'
        data-item-id={item.id}
        onClick={handleClick}
        onKeyDown={handleKeyDown}
        onMouseEnter={handleMouseEnter}
        onMouseLeave={handleMouseLeave}
      >
        <div className={combineTokens(DESIGN_TOKENS.layout.patterns.flexGapSm)}>
          {item.icon && (
            <span
              className={`shrink-0 ${DESIGN_TOKENS.icon.size.sm}`}
              aria-hidden='true'
            >
              {item.icon}
            </span>
          )}
          <span className={combineTokens(DESIGN_TOKENS.layout.flex.flex1)}>
            {item.label}
          </span>
          {hasSubmenu && (
            <span
              className={`${DESIGN_TOKENS.icon.size.sm} ${DESIGN_TOKENS.semantic.text.muted}`}
              aria-hidden='true'
            >
              ‚ñ∂
            </span>
          )}
        </div>
      </button>

      {/* Submenu */}
      {showSubmenu && hasSubmenu && (
        <div
          ref={submenuRef}
          className={`absolute ${submenuSide === 'right' ? 'left-full ml-1' : 'right-full mr-1'} top-0 ${DESIGN_TOKENS.recipe.dropdown.content} ${DESIGN_TOKENS.theme.light.surface.base} ${DESIGN_TOKENS.theme.light.elevation.dropdown} ${DESIGN_TOKENS.zIndex.popover} ${DESIGN_TOKENS.motion.smooth} `}
          role='menu'
          aria-label={`${item.label} submenu`}
          data-slot='submenu'
          data-parent-id={item.id}
          tabIndex={-1}
          onMouseEnter={() => {
            if (closeTimerRef.current) {
              clearTimeout(closeTimerRef.current);
            }
          }}
        >
          {item.submenu!.map((subItem, index) => (
            <ContextMenuItem
              key={subItem.id || index}
              item={subItem}
              onSelect={onSelect}
              onClose={onClose}
            />
          ))}
        </div>
      )}
    </div>
  );
};

// ===== CONTEXT MENU COMPONENT =====
const ContextMenu: React.FC<ContextMenuProps> = ({
  x,
  y,
  items,
  onClose,
  visible,
}) => {
  const menuRef = useRef<HTMLDivElement>(null);
  const [position, setPosition] = useState({ x, y });
  const [activeIdx, setActiveIdx] = useState<number>(0);
  const [typeaheadBuffer, setTypeaheadBuffer] = useState('');

  // Filter enabled items for roving focus
  const enabledItems = items.filter(item => !item.divider && !item.disabled);

  // Navigation helpers
  const move = (direction: 1 | -1) => {
    setActiveIdx(current => {
      const newIdx =
        (current + direction + enabledItems.length) % enabledItems.length;
      return newIdx;
    });
  };

  // Typeahead search
  const handleTypeahead = (character: string) => {
    const newBuffer = typeaheadBuffer + character.toLowerCase();
    setTypeaheadBuffer(newBuffer);

    const matchIdx = enabledItems.findIndex(item =>
      item.label?.toLowerCase().startsWith(newBuffer)
    );

    if (matchIdx !== -1) {
      setActiveIdx(matchIdx);
    }
  };

  // Clear typeahead buffer after delay
  useEffect(() => {
    if (!typeaheadBuffer) return;
    const timer = setTimeout(() => setTypeaheadBuffer(''), 500);
    return () => clearTimeout(timer);
  }, [typeaheadBuffer]);

  // Reset active index when menu opens
  useEffect(() => {
    if (visible) {
      setActiveIdx(0);
      setTypeaheadBuffer('');
    }
  }, [visible]);

  // Focus management - focus first item when menu opens
  useEffect(() => {
    if (!visible || !menuRef.current) return;

    // Small delay to ensure menu is rendered
    const timer = setTimeout(() => {
      const firstButton = menuRef.current?.querySelector(
        'button[data-slot="item"]:not([disabled])'
      ) as HTMLButtonElement;
      firstButton?.focus();
    }, 10);

    return () => clearTimeout(timer);
  }, [visible]);

  // Keyboard navigation at menu level
  const handleMenuKeyDown = (event: KeyboardEvent) => {
    switch (event.key) {
      case 'ArrowDown': {
        event.preventDefault();
        move(1);
        break;
      }
      case 'ArrowUp': {
        event.preventDefault();
        move(-1);
        break;
      }
      case 'Home': {
        event.preventDefault();
        setActiveIdx(0);
        break;
      }
      case 'End': {
        event.preventDefault();
        setActiveIdx(enabledItems.length - 1);
        break;
      }
      case 'Escape': {
        event.preventDefault();
        onClose();
        break;
      }
      default: {
        // Typeahead
        if (
          event.key.length === 1 &&
          !event.ctrlKey &&
          !event.metaKey &&
          !event.altKey
        ) {
          event.preventDefault();
          handleTypeahead(event.key);
        }
        break;
      }
    }
  };

  // Adjust position to keep menu within viewport
  useEffect(() => {
    if (!menuRef.current || !visible) return;

    const rect = menuRef.current.getBoundingClientRect();
    const viewportWidth = window.innerWidth;
    const viewportHeight = window.innerHeight;

    let adjustedX = x;
    let adjustedY = y;

    // Adjust horizontal position
    if (x + rect.width > viewportWidth) {
      adjustedX = viewportWidth - rect.width - 8;
    }

    // Adjust vertical position
    if (y + rect.height > viewportHeight) {
      adjustedY = viewportHeight - rect.height - 8;
    }

    // Ensure minimum distance from edges
    adjustedX = Math.max(8, adjustedX);
    adjustedY = Math.max(8, adjustedY);

    setPosition({ x: adjustedX, y: adjustedY });
  }, [x, y, visible]);

  // Handle clicks outside menu, scroll, and resize (escape handled at menu level)
  useEffect(() => {
    if (!visible) return;

    const handleClickOutside = (event: Event) => {
      if (menuRef.current && !menuRef.current.contains(event.target as Node)) {
        onClose();
      }
    };

    const handleDismiss = () => onClose();

    // Small delay to prevent immediate closure from the triggering right-click
    const timer = setTimeout(() => {
      document.addEventListener('click', handleClickOutside, true);
      document.addEventListener('contextmenu', handleClickOutside, true);
      window.addEventListener('resize', handleDismiss, { passive: true });
      window.addEventListener('scroll', handleDismiss, { passive: true });
    }, 10);

    return () => {
      clearTimeout(timer);
      document.removeEventListener('click', handleClickOutside, true);
      document.removeEventListener('contextmenu', handleClickOutside, true);
      window.removeEventListener('resize', handleDismiss);
      window.removeEventListener('scroll', handleDismiss);
    };
  }, [visible, onClose]);

  const handleItemSelect = () => {
    // Item selection is handled in ContextMenuItem
    // This handler can be extended for additional logic if needed
  };

  if (!visible || items.length === 0) {
    return null;
  }

  return (
    <div
      ref={menuRef}
      className={`fixed ${DESIGN_TOKENS.recipe.dropdown.content} ${DESIGN_TOKENS.theme.light.surface.base} ${DESIGN_TOKENS.theme.light.elevation.dropdown} ${DESIGN_TOKENS.zIndex.popover} ${DESIGN_TOKENS.motion.semantic.modalEnter} `}
      style={{
        left: position.x,
        top: position.y,
        minWidth: '12rem',
      }}
      role='menu'
      aria-label='Context menu'
      data-slot='menu'
      tabIndex={-1}
      onKeyDown={handleMenuKeyDown}
    >
      {items.map((item, index) => {
        // Calculate if this item is active (for non-dividers)
        const enabledIndex = enabledItems.indexOf(item);
        const isActive = enabledIndex !== -1 && enabledIndex === activeIdx;

        return (
          <ContextMenuItem
            key={item.id || index}
            item={item}
            onSelect={handleItemSelect}
            onClose={onClose}
            isActive={isActive}
            onActivate={() => setActiveIdx(enabledIndex)}
          />
        );
      })}
    </div>
  );
};

// ===== CONTEXT MENU TRIGGER =====
export const ContextMenuTrigger: React.FC<ContextMenuTriggerProps> = ({
  children,
  items,
  disabled = false,
}) => {
  const { showMenu } = useContextMenu();

  const handleContextMenu = (event: MouseEvent) => {
    if (disabled) return;

    event.preventDefault();
    event.stopPropagation();
    showMenu(event, items);
  };

  return (
    <div onContextMenu={handleContextMenu} style={{ userSelect: 'none' }}>
      {children}
    </div>
  );
};

// ===== CONTEXT MENU PROVIDER =====
export const ContextMenuProvider: React.FC<ContextMenuProviderProps> = ({
  children,
}) => {
  const [isVisible, setIsVisible] = useState(false);
  const [menuPosition, setMenuPosition] = useState({ x: 0, y: 0 });
  const [menuItems, setMenuItems] = useState<MenuItem[]>([]);

  const showMenu = useCallback((event: MouseEvent, items: MenuItem[]) => {
    event.preventDefault();
    event.stopPropagation();

    setMenuPosition({ x: event.clientX, y: event.clientY });
    setMenuItems(items);
    setIsVisible(true);
  }, []);

  const hideMenu = useCallback(() => {
    setIsVisible(false);
    setMenuItems([]);
  }, []);

  const contextValue: ContextMenuContextType = {
    showMenu,
    hideMenu,
    isVisible,
  };

  return (
    <ContextMenuContext.Provider value={contextValue}>
      {children}
      {isVisible && (
        <ContextMenu
          x={menuPosition.x}
          y={menuPosition.y}
          items={menuItems}
          onClose={hideMenu}
          visible={isVisible}
        />
      )}
    </ContextMenuContext.Provider>
  );
};

// ===== EXPORTS =====
export { ContextMenu };
export type { MenuItem, ContextMenuContextType };

/**
 * DescriptionList Component System - Enterprise Key-Value Pairs
 *
 * A comprehensive description list component system providing structured key-value
 * data presentation with enterprise-grade functionality including:
 * - Multiple layout patterns (horizontal, vertical, two-column)
 * - Interactive capabilities (selection, hover states, copyable content)
 * - Semantic variants (success, warning, error, info states)
 * - Accessibility compliance (ARIA patterns, semantic HTML)
 * - Content type support (text, code, badges, lists, links, status indicators)
 * - Compound component architecture (DescriptionList + Item + Term + Details)
 *
 * @version 1.0.0
 * @author SparkTasks Enterprise UI Team
 */

import React, { createContext, useContext, forwardRef } from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// ===== TYPE DEFINITIONS =====

export type DescriptionListVariant =
  | 'default'
  | 'bordered'
  | 'flush'
  | 'spaced'
  | 'relaxed';
export type DescriptionListLayout = 'horizontal' | 'vertical' | 'twoColumn';
export type DescriptionListSize = 'sm' | 'md' | 'lg' | 'xl';
export type DescriptionItemVariant =
  | 'default'
  | 'success'
  | 'warning'
  | 'error'
  | 'info';
export type DescriptionItemSpacing = 'compact' | 'comfortable' | 'spacious';
export type DescriptionItemLayout = 'horizontal' | 'vertical' | 'stacked';
export type DescriptionTermAlignment = 'top' | 'center' | 'baseline';
export type DescriptionDetailsType =
  | 'text'
  | 'code'
  | 'badge'
  | 'list'
  | 'link'
  | 'status';

export interface DescriptionListProps
  extends React.HTMLAttributes<HTMLDListElement> {
  /** Visual style variant */
  variant?: DescriptionListVariant;
  /** Layout pattern for the description list */
  layout?: DescriptionListLayout;
  /** Size variant affecting text and spacing */
  size?: DescriptionListSize;
  /** Whether list has background styling */
  background?: 'none' | 'subtle' | 'elevated';
  /** Whether list items are interactive */
  interactive?: boolean;
  /** Whether list items have hover effects */
  hoverable?: boolean;
  /** Loading state */
  loading?: boolean;
  /** Empty state content */
  emptyContent?: React.ReactNode;
  /** ARIA label for the description list */
  'aria-label'?: string;
  children?: React.ReactNode;
}

export interface DescriptionItemProps
  extends React.HTMLAttributes<HTMLDivElement> {
  /** Visual style variant */
  variant?: DescriptionItemVariant;
  /** Layout pattern for content arrangement */
  layout?: DescriptionItemLayout;
  /** Spacing variant */
  spacing?: DescriptionItemSpacing;
  /** Whether item is interactive */
  interactive?: boolean;
  /** Whether item is selected */
  selected?: boolean;
  /** Click handler for interactive items */
  onSelect?: () => void;
  children: React.ReactNode;
}

export interface DescriptionTermProps
  extends React.HTMLAttributes<HTMLElement> {
  /** Whether the term is required */
  required?: boolean;
  /** Whether the term is optional */
  optional?: boolean;
  /** Alignment relative to the details */
  align?: DescriptionTermAlignment;
  /** Color variant */
  variant?: 'default' | 'muted' | 'emphasized';
  children: React.ReactNode;
}

export interface DescriptionDetailsProps
  extends Omit<React.HTMLAttributes<HTMLElement>, 'onCopy'> {
  /** Content type for appropriate styling */
  type?: DescriptionDetailsType;
  /** Whether content is copyable */
  copyable?: boolean;
  /** Whether content should be truncated */
  truncated?: boolean;
  /** Whether content is expandable */
  expandable?: boolean;
  /** Color variant */
  variant?: 'default' | 'muted' | 'emphasized';
  /** Status type for status indicators */
  status?: 'success' | 'warning' | 'error' | 'info';
  /** Copy handler for copyable content */
  onCopy?: (text: string) => void;
  children: React.ReactNode;
}

// ===== CONTEXT =====

interface DescriptionListContextValue {
  size: DescriptionListSize;
  layout: DescriptionListLayout;
  interactive: boolean;
  hoverable: boolean;
}

const DescriptionListContext = createContext<DescriptionListContextValue>({
  size: 'md',
  layout: 'horizontal',
  interactive: false,
  hoverable: false,
});

// ===== UTILITY FUNCTIONS =====

const getDescriptionListClasses = (
  variant: DescriptionListVariant,
  layout: DescriptionListLayout,
  size: DescriptionListSize,
  background: 'none' | 'subtle' | 'elevated',
  interactive: boolean,
  hoverable: boolean
) => {
  const baseClasses = DESIGN_TOKENS.recipe.descriptionList.base;
  const variantClasses =
    variant !== 'default' && variant in DESIGN_TOKENS.recipe.descriptionList
      ? DESIGN_TOKENS.recipe.descriptionList[
          variant as keyof typeof DESIGN_TOKENS.recipe.descriptionList
        ]
      : '';
  const layoutClasses =
    layout in DESIGN_TOKENS.recipe.descriptionList
      ? DESIGN_TOKENS.recipe.descriptionList[
          layout as keyof typeof DESIGN_TOKENS.recipe.descriptionList
        ]
      : '';
  const sizeClasses =
    size in DESIGN_TOKENS.recipe.descriptionList
      ? DESIGN_TOKENS.recipe.descriptionList[
          size as keyof typeof DESIGN_TOKENS.recipe.descriptionList
        ]
      : '';
  const backgroundClasses =
    background !== 'none' && background in DESIGN_TOKENS.recipe.descriptionList
      ? DESIGN_TOKENS.recipe.descriptionList[
          background as keyof typeof DESIGN_TOKENS.recipe.descriptionList
        ]
      : '';
  const interactiveClasses = interactive
    ? DESIGN_TOKENS.recipe.descriptionList.interactive
    : '';
  const hoverableClasses = hoverable
    ? DESIGN_TOKENS.recipe.descriptionList.hoverable
    : '';

  return combineTokens(
    baseClasses,
    variantClasses,
    layoutClasses,
    sizeClasses,
    backgroundClasses,
    interactiveClasses,
    hoverableClasses
  );
};

const getDescriptionItemClasses = (
  variant: DescriptionItemVariant,
  layout: DescriptionItemLayout,
  spacing: DescriptionItemSpacing,
  interactive: boolean,
  selected: boolean
) => {
  const baseClasses =
    layout in DESIGN_TOKENS.recipe.descriptionItem
      ? DESIGN_TOKENS.recipe.descriptionItem[
          layout as keyof typeof DESIGN_TOKENS.recipe.descriptionItem
        ]
      : DESIGN_TOKENS.recipe.descriptionItem.base;
  const variantClasses =
    variant !== 'default' && variant in DESIGN_TOKENS.recipe.descriptionItem
      ? DESIGN_TOKENS.recipe.descriptionItem[
          variant as keyof typeof DESIGN_TOKENS.recipe.descriptionItem
        ]
      : '';
  const spacingClasses =
    spacing in DESIGN_TOKENS.recipe.descriptionItem
      ? DESIGN_TOKENS.recipe.descriptionItem[
          spacing as keyof typeof DESIGN_TOKENS.recipe.descriptionItem
        ]
      : '';
  const interactiveClasses = interactive
    ? DESIGN_TOKENS.recipe.descriptionItem.interactive
    : '';
  const selectedClasses = selected
    ? DESIGN_TOKENS.recipe.descriptionItem.selected
    : '';

  return combineTokens(
    baseClasses,
    variantClasses,
    spacingClasses,
    interactiveClasses,
    selectedClasses
  );
};

const getDescriptionTermClasses = (
  size: DescriptionListSize,
  align: DescriptionTermAlignment,
  variant: 'default' | 'muted' | 'emphasized',
  required: boolean,
  optional: boolean
) => {
  const baseClasses = DESIGN_TOKENS.recipe.descriptionTerm.base;
  const sizeClasses =
    size in DESIGN_TOKENS.recipe.descriptionTerm
      ? DESIGN_TOKENS.recipe.descriptionTerm[
          size as keyof typeof DESIGN_TOKENS.recipe.descriptionTerm
        ]
      : '';
  const alignClasses =
    align !== 'baseline' &&
    `align${align.charAt(0).toUpperCase() + align.slice(1)}` in
      DESIGN_TOKENS.recipe.descriptionTerm
      ? DESIGN_TOKENS.recipe.descriptionTerm[
          `align${align.charAt(0).toUpperCase() + align.slice(1)}` as keyof typeof DESIGN_TOKENS.recipe.descriptionTerm
        ]
      : '';
  const variantClasses =
    variant !== 'default' && variant in DESIGN_TOKENS.recipe.descriptionTerm
      ? DESIGN_TOKENS.recipe.descriptionTerm[
          variant as keyof typeof DESIGN_TOKENS.recipe.descriptionTerm
        ]
      : '';
  const requiredClasses = required
    ? DESIGN_TOKENS.recipe.descriptionTerm.required
    : '';
  const optionalClasses = optional
    ? DESIGN_TOKENS.recipe.descriptionTerm.optional
    : '';

  return combineTokens(
    baseClasses,
    sizeClasses,
    alignClasses,
    variantClasses,
    requiredClasses,
    optionalClasses
  );
};

const getDescriptionDetailsClasses = (
  type: DescriptionDetailsType,
  variant: 'default' | 'muted' | 'emphasized',
  truncated: boolean,
  expandable: boolean,
  copyable: boolean,
  status?: 'success' | 'warning' | 'error' | 'info'
) => {
  const baseClasses = DESIGN_TOKENS.recipe.descriptionDetails.base;
  const typeClasses =
    type !== 'text' && type in DESIGN_TOKENS.recipe.descriptionDetails
      ? DESIGN_TOKENS.recipe.descriptionDetails[
          type as keyof typeof DESIGN_TOKENS.recipe.descriptionDetails
        ]
      : '';
  const variantClasses =
    variant !== 'default' && variant in DESIGN_TOKENS.recipe.descriptionDetails
      ? DESIGN_TOKENS.recipe.descriptionDetails[
          variant as keyof typeof DESIGN_TOKENS.recipe.descriptionDetails
        ]
      : '';
  const truncatedClasses = truncated
    ? DESIGN_TOKENS.recipe.descriptionDetails.truncated
    : '';
  const expandableClasses = expandable
    ? DESIGN_TOKENS.recipe.descriptionDetails.expandable
    : '';
  const copyableClasses = copyable
    ? DESIGN_TOKENS.recipe.descriptionDetails.copyable
    : '';
  const statusClasses =
    status &&
    `status${status.charAt(0).toUpperCase() + status.slice(1)}` in
      DESIGN_TOKENS.recipe.descriptionDetails
      ? DESIGN_TOKENS.recipe.descriptionDetails[
          `status${status.charAt(0).toUpperCase() + status.slice(1)}` as keyof typeof DESIGN_TOKENS.recipe.descriptionDetails
        ]
      : '';

  return combineTokens(
    baseClasses,
    typeClasses,
    variantClasses,
    truncatedClasses,
    expandableClasses,
    copyableClasses,
    statusClasses
  );
};

