const DescriptionListComponent = forwardRef<
  HTMLDListElement,
  DescriptionListProps
>(
  (
    {
      variant = 'default',
      layout = 'horizontal',
      size = 'md',
      background = 'none',
      interactive = false,
      hoverable = false,
      loading = false,
      emptyContent,
      className,
      children,
      'aria-label': ariaLabel,
      ...props
    },
    ref
  ) => {
    const listClasses = getDescriptionListClasses(
      variant,
      layout,
      size,
      background,
      interactive,
      hoverable
    );

    // Handle loading state
    if (loading) {
      return (
        <dl
          ref={ref}
          className={combineTokens(listClasses, className)}
          aria-label={ariaLabel}
          role='list'
          {...props}
        >
          <DescriptionListSkeleton />
        </dl>
      );
    }

    // Handle empty state
    const childrenArray = React.Children.toArray(children);
    if (childrenArray.length === 0 && emptyContent) {
      return (
        <DescriptionListEmptyState>{emptyContent}</DescriptionListEmptyState>
      );
    }

    return (
      <DescriptionListContext.Provider
        value={{ size, layout, interactive, hoverable }}
      >
        <dl
          ref={ref}
          className={combineTokens(listClasses, className)}
          aria-label={ariaLabel}
          role='list'
          {...props}
        >
          {children}
        </dl>
      </DescriptionListContext.Provider>
    );
  }
);

DescriptionListComponent.displayName = 'DescriptionList';

const DescriptionItemComponent = forwardRef<
  HTMLDivElement,
  DescriptionItemProps
>(
  (
    {
      variant = 'default',
      layout,
      spacing = 'comfortable',
      interactive,
      selected = false,
      onSelect,
      className,
      children,
      onClick,
      onKeyDown,
      tabIndex,
      ...props
    },
    ref
  ) => {
    const context = useContext(DescriptionListContext);

    // Determine layout: explicit prop takes priority over context, but ensure compatibility
    const itemLayout =
      layout ||
      (context.layout === 'twoColumn' ? 'horizontal' : context.layout);

    // Determine if interactive: explicit prop takes priority over context
    const isInteractive =
      interactive === undefined
        ? context.interactive || !!onSelect || !!onClick
        : interactive || !!onSelect || !!onClick;

    const itemClasses = getDescriptionItemClasses(
      variant,
      itemLayout,
      spacing,
      isInteractive,
      selected
    );

    const handleClick = (event: React.MouseEvent<HTMLDivElement>) => {
      if (!isInteractive) return;
      onClick?.(event);
      onSelect?.();
    };

    const handleKeyDown = (event: React.KeyboardEvent<HTMLDivElement>) => {
      if (!isInteractive) return;
      onKeyDown?.(event);

      if ((event.key === 'Enter' || event.key === ' ') && onSelect) {
        event.preventDefault();
        onSelect();
      }
    };

    return (
      <div
        ref={ref}
        className={combineTokens(itemClasses, className)}
        role={isInteractive ? 'button' : undefined}
        aria-pressed={isInteractive ? selected : undefined}
        tabIndex={isInteractive ? (tabIndex ?? 0) : undefined}
        onClick={handleClick}
        onKeyDown={handleKeyDown}
        {...props}
      >
        {children}
      </div>
    );
  }
);

