/**
 * Gallery component props
 */
export interface GalleryProps
  extends Omit<HTMLAttributes<HTMLDivElement>, 'onSelect'> {
  /** Array of gallery items */
  items: GalleryItem[];
  /** Layout variant */
  layout?: GalleryLayout;
  /** Size variant */
  size?: GallerySize;
  /** Number of columns for grid layout */
