              <path
                d='M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z'
                fill='currentColor'
              />
            </svg>
          </span>
        )}
      </button>
    );
  }
);

IconButton.displayName = 'IconButton';

/**
 * @fileoverview Image Component - Enterprise-grade responsive image with aspect ratio
 *
 * A comprehensive image component that provides responsive behavior,
 * aspect ratio management, lazy loading, error handling, and accessibility.
 * Built for modern web applications with performance and UX in mind.
 *
 * Features:
 * - Multiple aspect ratios (square, video, portrait, landscape, custom)
 * - 5 size variants (xs, sm, md, lg, xl)
 * - 4 visual variants (default, rounded, circular, thumbnail)
 * - Lazy loading with intersection observer
 * - Error state with fallback content
 * - Loading state with skeleton placeholder
 * - Responsive behavior with srcSet support
 * - Progressive image enhancement
 * - Full accessibility (WCAG 2.1 AA)
 * - Object-fit positioning control
 * - Border radius and shadow styling
 *
 * @version 1.0.0
 * @author Spark Tasks Team
 * @since 2024
 */

import React, {
  forwardRef,
  useState,
  useCallback,
  useRef,
  useEffect,
  useMemo,
} from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// ===== TYPES & INTERFACES =====

/**
 * Aspect ratio options for the image
 */
export type ImageAspectRatio =
  | 'square' // 1:1 aspect ratio
  | 'video' // 16:9 aspect ratio
  | 'portrait' // 3:4 aspect ratio
  | 'landscape' // 4:3 aspect ratio
  | 'wide' // 21:9 aspect ratio
  | 'auto'; // Natural image dimensions

/**
 * Size variants for the image
 */
export type ImageSize = 'xs' | 'sm' | 'md' | 'lg' | 'xl';

/**
 * Visual variants for the image
 */
export type ImageVariant = 'default' | 'rounded' | 'circular' | 'thumbnail';

/**
 * Object fit options for image positioning
 */
export type ImageFit = 'cover' | 'contain' | 'fill' | 'scale-down' | 'none';

/**
 * Loading strategy options
 */
export type ImageLoading = 'lazy' | 'eager';

/**
 * Image component props
 */
export interface ImageProps
  extends Omit<
    React.ImgHTMLAttributes<HTMLImageElement>,
    'src' | 'loading' | 'children'
  > {
  /** Image source URL */
  src: string;

  /** Alternative text for accessibility */
  alt: string;

  /** Aspect ratio of the image container */
  aspectRatio?: ImageAspectRatio;

  /** Size variant */
  size?: ImageSize;

  /** Visual variant */
  variant?: ImageVariant;

  /** Object fit behavior */
  fit?: ImageFit;

  /** Loading strategy */
  loading?: ImageLoading;

  /** Responsive image sources */
  srcSet?: string;

  /** Image sizes for responsive behavior */
  sizes?: string;

  /** Fallback content when image fails to load */
  fallback?: React.ReactNode;

  /** Loading placeholder content */
  placeholder?: React.ReactNode;

  /** Whether to show loading skeleton by default */
  showSkeleton?: boolean;

  /** Callback when image loads successfully */
  onLoad?: (event: React.SyntheticEvent<HTMLImageElement>) => void;

  /** Callback when image fails to load */
  onError?: (event: React.SyntheticEvent<HTMLImageElement>) => void;

  /** Whether the image is disabled/inactive */
  disabled?: boolean;

  /** Custom CSS classes */
  className?: string;

  /** Container props for the wrapper div */
  containerProps?: React.HTMLAttributes<HTMLDivElement>;
}

// ===== UTILITY FUNCTIONS =====

/**
 * Get aspect ratio classes from design tokens
 */
const getAspectRatioClasses = (aspectRatio: ImageAspectRatio): string => {
  const aspectMap = {
    square: 'aspect-square', // 1:1 aspect ratio
    video: 'aspect-video', // 16:9 aspect ratio
    portrait: 'aspect-[3/4]', // 3:4 portrait
    landscape: 'aspect-[4/3]', // 4:3 landscape
    wide: 'aspect-[21/9]', // 21:9 ultra-wide
    auto: '', // Natural dimensions
  };

  return aspectMap[aspectRatio] || '';
};

/**
 * Get size classes from design tokens
 */
const getSizeClasses = (size: ImageSize): string => {
  const sizeMap = {
    xs: 'w-16 h-16', // 64px - Small thumbnails
    sm: 'w-24 h-24', // 96px - Medium thumbnails
    md: 'w-48 h-48', // 192px - Default size
    lg: 'w-64 h-64', // 256px - Large images
    xl: 'w-96 h-96', // 384px - Hero images
  };

  return sizeMap[size];
};

/**
 * Get variant classes from design tokens
 */
const getVariantClasses = (variant: ImageVariant): string => {
  const variantMap = {
    default: DESIGN_TOKENS.theme.light.radius.md, // rounded-md
    rounded: DESIGN_TOKENS.theme.light.radius.lg, // rounded-lg
    circular: DESIGN_TOKENS.theme.light.radius.full, // rounded-full
    thumbnail: `${DESIGN_TOKENS.theme.light.radius.sm} ${DESIGN_TOKENS.theme.light.elevation.card} ${DESIGN_TOKENS.semantic.border.muted} border`, // Bordered thumbnail
  };

  return variantMap[variant];
};

/**
 * Get object fit classes
 */
const getFitClasses = (fit: ImageFit): string => {
  const fitMap = {
    cover: 'object-cover',
    contain: 'object-contain',
    fill: 'object-fill',
    'scale-down': 'object-scale-down',
    none: 'object-none',
  };

  return fitMap[fit];
};

// ===== HOOKS =====

/**
 * Hook for image loading state management
 */
const useImageLoading = (
  src: string,
  onLoad?: (event: React.SyntheticEvent<HTMLImageElement>) => void,
  onError?: (event: React.SyntheticEvent<HTMLImageElement>) => void
) => {
  const [isLoading, setIsLoading] = useState(true);
  const [hasError, setHasError] = useState(false);
  const [isLoaded, setIsLoaded] = useState(false);

  // Reset states when src changes
  useEffect(() => {
    setIsLoading(true);
    setHasError(false);
    setIsLoaded(false);
  }, [src]);

  const handleLoad = useCallback(
    (event: React.SyntheticEvent<HTMLImageElement>) => {
      setIsLoading(false);
      setIsLoaded(true);
      setHasError(false);
      onLoad?.(event);
    },
    [onLoad]
  );

  const handleError = useCallback(
    (event: React.SyntheticEvent<HTMLImageElement>) => {
      setIsLoading(false);
      setIsLoaded(false);
      setHasError(true);
      onError?.(event);
    },
    [onError]
  );

  return {
    isLoading,
    hasError,
    isLoaded,
    handleLoad,
    handleError,
  };
};

/**
 * Hook for lazy loading with intersection observer
 */
const useLazyLoading = (loading: ImageLoading) => {
  const [shouldLoad, setShouldLoad] = useState(loading === 'eager');
  const imgRef = useRef<HTMLImageElement>(null);

  useEffect(() => {
    if (loading === 'eager' || shouldLoad) return;

    const img = imgRef.current;
    if (!img) return;

    const observer = new IntersectionObserver(
      ([entry]) => {
        if (entry.isIntersecting) {
          setShouldLoad(true);
          observer.disconnect();
        }
      },
      {
        threshold: 0.1,
        rootMargin: '50px', // Start loading 50px before entering viewport
      }
    );

    observer.observe(img);

    return () => observer.disconnect();
  }, [loading, shouldLoad]);

  return {
    shouldLoad,
    imgRef,
  };
};

// ===== MAIN COMPONENT =====

/**
 * Enterprise-grade Image component
 */
export const Image = forwardRef<HTMLImageElement, ImageProps>(
  (
    {
      src,
      alt,
      aspectRatio = 'auto',
      size = 'md',
      variant = 'default',
      fit = 'cover',
      loading = 'lazy',
      srcSet,
      sizes,
      fallback,
      placeholder,
      showSkeleton = true,
      onLoad,
      onError,
      disabled = false,
      className = '',
      containerProps = {},
      ...props
    },
    ref
  ) => {
    // Image loading state
    const { isLoading, hasError, isLoaded, handleLoad, handleError } =
      useImageLoading(src, onLoad, onError);

    // Lazy loading
    const { shouldLoad, imgRef } = useLazyLoading(loading);

    // Combine refs
    const combinedRef = useCallback(
      (node: HTMLImageElement | null) => {
        (imgRef as React.MutableRefObject<HTMLImageElement | null>).current =
          node;
        if (typeof ref === 'function') {
          ref(node);
        } else if (ref) {
          ref.current = node;
        }
      },
      [ref, imgRef]
    );

    // Generate container classes
    const containerClasses = useMemo(() => {
      const aspectRatioClass = getAspectRatioClasses(aspectRatio);
      const sizeClass = aspectRatio === 'auto' ? getSizeClasses(size) : '';
      const variantClass = getVariantClasses(variant);

      return `
      relative overflow-hidden
      ${aspectRatioClass}
      ${sizeClass}
      ${variantClass}
      ${disabled ? 'opacity-50 grayscale cursor-not-allowed' : ''}
      ${DESIGN_TOKENS.semantic.background.muted}
      ${containerProps.className || ''}
    `.trim();
    }, [aspectRatio, size, variant, disabled, containerProps.className]);

    // Generate image classes
    const imageClasses = useMemo(() => {
      const fitClass = getFitClasses(fit);

      return `
      ${aspectRatio === 'auto' ? 'max-w-full h-auto' : 'size-full'}
      ${fitClass}
      ${DESIGN_TOKENS.motion.smooth}
      ${isLoaded ? 'opacity-100' : 'opacity-0'}
      ${disabled ? 'grayscale cursor-not-allowed' : ''}
      ${className}
    `.trim();
    }, [aspectRatio, fit, isLoaded, disabled, className]);

    // Default fallback content
    const defaultFallback = (
      <div
        className={` ${DESIGN_TOKENS.layout.flexCenter} size-full ${DESIGN_TOKENS.semantic.background.muted} ${DESIGN_TOKENS.semantic.text.muted} ${DESIGN_TOKENS.typography.body.small} `}
      >
        <div className={combineTokens('text-center')}>
          <svg
            className={`${DESIGN_TOKENS.icon.size.lg} mx-auto ${DESIGN_TOKENS.spacing.sm} opacity-50`}
            fill='none'
            stroke='currentColor'
            viewBox='0 0 24 24'
            aria-hidden='true'
          >
            <path
              strokeLinecap='round'
              strokeLinejoin='round'
              strokeWidth={1.5}
              d='M4 16l4.586-4.586a2 2 0 012.828 0L16 16m-2-2l1.586-1.586a2 2 0 012.828 0L20 14m-6-6h.01M6 20h12a2 2 0 002-2V6a2 2 0 00-2-2H6a2 2 0 00-2 2v12a2 2 0 002 2z'
            />
          </svg>
          <span>Image not available</span>
        </div>
      </div>
    );

    // Default loading placeholder
    const defaultPlaceholder = showSkeleton ? (
      <div
        className={`relative size-full animate-pulse overflow-hidden ${DESIGN_TOKENS.semantic.background.muted} `}
      >
        <div
          className={combineTokens(
            'absolute',
            'inset-0',
            'bg-gradient-to-r',
            'from-transparent',
            'via-white/60',
            'to-transparent',
            '-translate-x-full',
            'animate-[shimmer_2s_infinite]'
          )}
        />
      </div>
    ) : null;

    return (
      <div
        {...containerProps}
        className={containerClasses}
        data-testid='image-container'
      >
        {/* Loading placeholder */}
        {isLoading && !hasError && (placeholder || defaultPlaceholder)}

        {/* Error fallback */}
        {hasError && (
          <div data-testid='image-fallback'>{fallback || defaultFallback}</div>
        )}

        {/* Main image */}
        {shouldLoad && !hasError && (
          <img
            ref={combinedRef}
            src={src}
            alt={alt}
            srcSet={srcSet}
            sizes={sizes}
            className={imageClasses}
            onLoad={handleLoad}
            onError={handleError}
            loading={loading}
            data-testid='image'
            {...props}
          />
        )}
      </div>
    );
  }
);

Image.displayName = 'Image';

export default Image;

/**
 * KebabMenu Component - Enterprise-Grade Three-Dot Action Menu
 *
 * Features:
 * - Iconic three-dot button trigger with hover states
 * - Contextual action menu with advanced positioning
 * - Destructive action support with visual differentiation
 * - Keyboard navigation (Arrow keys, Enter, Escape, Tab)
 * - Accessibility compliance (WCAG 2.1 AA) with proper ARIA
 *   // Click outside detection
 * - Separator support for logical action grouping
 * - Icon integration with enterprise spacing patterns
 * - Theme-aware styling with dark mode support
 * - Link handling with external link support
 * - Mobile-friendly touch interactions
 * - Enterprise motion system integration
 * - Loading and disabled states
 */

import { MoreHorizontal, ExternalLink } from 'lucide-react';
import React, {
  useState,
  useRef,
  useEffect,
  useCallback,
  useMemo,
} from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// Type definitions
export type KebabMenuPlacement =
  | 'bottom-start'
  | 'bottom-end'
  | 'top-start'
  | 'top-end'
  | 'left-start'
  | 'left-end'
  | 'right-start'
  | 'right-end';

export interface KebabMenuItem {
  id: string;
  label: string;
  icon?: React.ReactNode;
  disabled?: boolean;
  destructive?: boolean;
  separator?: boolean; // Shows a separator before this item
  onClick?: () => void;
  href?: string; // For link items
  target?: string; // For external links
}

export interface KebabMenuProps {
  // Content
  items: KebabMenuItem[];
  children?: React.ReactNode; // For custom content instead of items

  // Trigger configuration
  buttonSize?: 'sm' | 'md' | 'lg';
  buttonVariant?: 'ghost' | 'outline' | 'secondary';
  triggerClassName?: string;

  // Menu configuration
  placement?: KebabMenuPlacement;
  contentClassName?: string;

  // Accessibility
  ariaLabel?: string;
  menuId?: string;

  // State
  disabled?: boolean;
  loading?: boolean;

  // Controlled state
  isOpen?: boolean;
  onOpenChange?: (open: boolean) => void;

  // Events
  onSelect?: (item: KebabMenuItem) => void;
}

export function KebabMenu({
  items,
  children,
  buttonSize = 'md',
  buttonVariant = 'ghost',
  triggerClassName = '',
  placement = 'bottom-end',
  contentClassName = '',
  ariaLabel = 'More actions',
  menuId,
  disabled = false,
  loading = false,
  isOpen: controlledOpen,
  onOpenChange,
  onSelect,
}: KebabMenuProps) {
  // State management
  const [internalOpen, setInternalOpen] = useState(false);
  const isOpen = controlledOpen === undefined ? internalOpen : controlledOpen;

  const setIsOpen = useCallback(
    (open: boolean) => {
      if (controlledOpen === undefined) {
        setInternalOpen(open);
      }
      onOpenChange?.(open);
    },
    [controlledOpen, onOpenChange]
  );

  // Refs for DOM manipulation
  const triggerRef = useRef<HTMLButtonElement>(null);
  const menuRef = useRef<HTMLDivElement>(null);
  const [focusedIndex, setFocusedIndex] = useState(-1);

  // Generate unique IDs
  const generatedMenuId = useRef(
    `kebab-menu-${Math.random().toString(36).slice(2, 11)}`
  );
  const actualMenuId = menuId || generatedMenuId.current;

  // Get valid menu items (exclude separators for keyboard navigation)
  const validItems = useMemo(
    () => items.filter(item => !item.separator && !item.disabled),
    [items]
  );

  // Focus management for keyboard navigation
  useEffect(() => {
    if (isOpen && focusedIndex >= 0 && menuRef.current) {
      const validItems = [
        ...menuRef.current.querySelectorAll(
          '[role="menuitem"]:not([aria-disabled="true"])'
        ),
      ];
      const itemToFocus = validItems[focusedIndex] as HTMLElement;
      if (itemToFocus) {
        itemToFocus.focus();
      }
    }
  }, [isOpen, focusedIndex]);
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        isOpen &&
        triggerRef.current &&
        menuRef.current &&
        !triggerRef.current.contains(event.target as Node) &&
        !menuRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
        setFocusedIndex(-1);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isOpen, setIsOpen]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (!isOpen) return;

      switch (event.key) {
        case 'Escape': {
          event.preventDefault();
          setIsOpen(false);
          setFocusedIndex(-1);
          triggerRef.current?.focus();
          break;
        }

        case 'ArrowDown': {
          event.preventDefault();
          setFocusedIndex(prev => {
            const nextIndex = prev < validItems.length - 1 ? prev + 1 : 0;
            return nextIndex;
          });
          break;
        }

        case 'ArrowUp': {
          event.preventDefault();
          setFocusedIndex(prev => {
            const nextIndex = prev > 0 ? prev - 1 : validItems.length - 1;
            return nextIndex;
          });
          break;
        }

        case 'Enter':
        case ' ': {
          event.preventDefault();
          if (focusedIndex >= 0 && focusedIndex < validItems.length) {
            const item = validItems[focusedIndex];
            const actualItem = items.find(i => i === item);
            if (actualItem && !actualItem.disabled) {
              if (actualItem.onClick) {
                actualItem.onClick();
              } else if (actualItem.href) {
                if (actualItem.target === '_blank') {
                  window.open(actualItem.href, '_blank', 'noopener,noreferrer');
                } else {
                  globalThis.location.href = actualItem.href;
                }
              }
              setIsOpen(false);
              setFocusedIndex(-1);
            }
          }
          break;
        }

        case 'Tab': {
          // Allow tabbing out of the menu
          setIsOpen(false);
          setFocusedIndex(-1);
          break;
        }
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleKeyDown);
      return () => document.removeEventListener('keydown', handleKeyDown);
    }
  }, [isOpen, focusedIndex, validItems, items, setIsOpen]);

  // Position calculation based on placement
  const getPositionClasses = (placement: KebabMenuPlacement): string => {
    const positions = {
      'bottom-start': 'top-full left-0 mt-1',
      'bottom-end': 'top-full right-0 mt-1',
      'top-start': 'bottom-full left-0 mb-1',
      'top-end': 'bottom-full right-0 mb-1',
      'left-start': 'right-full top-0 mr-1',
      'left-end': 'right-full bottom-0 mr-1',
      'right-start': 'left-full top-0 ml-1',
      'right-end': 'left-full bottom-0 ml-1',
    };
    return positions[placement] || positions['bottom-end'];
  };

  // Event handlers
  const handleTriggerClick = () => {
    if (disabled) return;
    const newOpen = !isOpen;
    setIsOpen(newOpen);
    if (newOpen) {
      setFocusedIndex(-1);
    }
  };

  const handleItemClick = useCallback(
    (item: KebabMenuItem) => {
      if (item.disabled) return;

      // Handle link navigation
      if (item.href) {
        if (item.target === '_blank') {
          window.open(item.href, '_blank', 'noopener,noreferrer');
        } else {
          globalThis.location.href = item.href;
        }
      }

      // Call item onClick handler
      item.onClick?.();

      // Call menu onSelect handler
      onSelect?.(item);

      // Close menu after selection
      setIsOpen(false);
      setFocusedIndex(-1);

      // Return focus to trigger
      triggerRef.current?.focus();
    },
    [onSelect, setIsOpen, setFocusedIndex]
  );

  // Styling classes
  const triggerClasses = [
    DESIGN_TOKENS.recipe.button.base,
    DESIGN_TOKENS.recipe.button[buttonVariant],
    DESIGN_TOKENS.recipe.button[buttonSize],
    DESIGN_TOKENS.recipe.button.iconOnly,
    // Focus and hover states
    'focus-visible:ring-2 focus-visible:ring-primary-500 focus-visible:ring-offset-2',
    'hover:bg-slate-100 dark:hover:bg-slate-800',
    // Rounded for better visual appeal
    'rounded-md',
    // State-based styling
    disabled ? DESIGN_TOKENS.state.disabled : '',
    loading ? 'cursor-wait' : '',
    isOpen ? 'bg-slate-100 dark:bg-slate-800' : '',
    triggerClassName,
  ]
    .filter(Boolean)
    .join(' ');

  const contentClasses = [
    DESIGN_TOKENS.recipe.dropdown.content,
    DESIGN_TOKENS.zIndex.dropdown,
    // Theme-aware background and text
    'dark:bg-slate-900 dark:text-slate-100 dark:border-slate-700',
    // Positioning based on placement
    getPositionClasses(placement),
    // Animation states
    isOpen ? 'opacity-100 scale-100' : 'opacity-0 scale-95 pointer-events-none',
    DESIGN_TOKENS.motion.smooth,
    // Custom styling
    contentClassName,
  ]
    .filter(Boolean)
    .join(' ');

  // Get item classes with proper enterprise token usage
  const getItemClasses = (item: KebabMenuItem) =>
    [
      DESIGN_TOKENS.recipe.dropdown.item,
      // Focus state
      focusedIndex === validItems.indexOf(item)
        ? 'bg-slate-100 dark:bg-slate-800'
        : '',
      // Destructive styling
      item.destructive
        ? 'text-red-600 dark:text-red-400 hover:bg-red-50 dark:hover:bg-red-950/20'
        : '',
      // Disabled state
      item.disabled ? DESIGN_TOKENS.state.disabled : 'cursor-pointer',
      // Link styling
      item.href ? 'flex items-center justify-between' : '',
    ]
      .filter(Boolean)
      .join(' ');

  return (
    <div className={combineTokens('relative', 'inline-block')}>
      {/* Trigger Button */}
      <button
        ref={triggerRef}
        onClick={handleTriggerClick}
        className={triggerClasses}
        disabled={disabled}
        aria-label={ariaLabel}
        aria-expanded={isOpen}
        aria-haspopup='menu'
        aria-controls={isOpen ? actualMenuId : undefined}
        type='button'
      >
        {loading ? (
          <div
            className={`${DESIGN_TOKENS.icon.size[buttonSize]} animate-spin rounded-full border-2 border-current border-t-transparent`}
          />
        ) : (
          <MoreHorizontal className={DESIGN_TOKENS.icon.size[buttonSize]} />
        )}
      </button>

      {/* Dropdown Menu */}
      <div
        ref={menuRef}
        id={actualMenuId}
        className={contentClasses}
        role='menu'
        aria-orientation='vertical'
        aria-labelledby={triggerRef.current?.id}
      >
        {children || (
          <>
            {items.map((item, index) => {
              // Handle separators
              if (item.separator) {
                return (
                  <div
                    key={`separator-${index}`}
                    className={combineTokens(
                      'my-1',
                      'border-t',
                      'border-slate-200',
                      'dark:border-slate-700'
                    )}
                    role='separator'
                  />
                );
              }

              const validIndex = validItems.indexOf(item);

              return (
                <div
                  key={item.id}
                  className={getItemClasses(item)}
                  role='menuitem'
                  aria-disabled={item.disabled}
                  tabIndex={focusedIndex === validIndex ? 0 : -1}
                  onClick={() => handleItemClick(item)}
                  onKeyDown={e => {
                    if (e.key === 'Enter' || e.key === ' ') {
                      e.preventDefault();
                      handleItemClick(item);
                    }
                  }}
                  onMouseEnter={() => setFocusedIndex(validIndex)}
                  onFocus={() => setFocusedIndex(validIndex)}
                >
                  <div
                    className={combineTokens(
                      'flex',
                      'items-center',
                      'gap-2',
                      'flex-1'
                    )}
                  >
                    {item.icon && (
                      <span className={DESIGN_TOKENS.icon.size.sm}>
                        {item.icon}
                      </span>
                    )}
                    <span
                      className={
                        item.destructive ? 'text-red-600 dark:text-red-400' : ''
                      }
                    >
                      {item.label}
                    </span>
                  </div>

                  {/* External link indicator */}
                  {item.href && item.target === '_blank' && (
                    <ExternalLink
                      className={`${DESIGN_TOKENS.icon.size.xs} opacity-60`}
                    />
                  )}
                </div>
              );
            })}
          </>
        )}
      </div>
    </div>
  );
}

export default KebabMenu;

/**
 * List Component System - Enterprise Structured Lists
 *
 * A comprehensive list component system providing structured data presentation
 * with enterprise-grade functionality including:
 * - Multiple layout patterns (single-line, multi-line, with icons/avatars)
 * - Interactive capabilities (selection, hover states, keyboard navigation)
 * - Semantic variants (success, warning, error, info states)
 * - Accessibility compliance (ARIA patterns, keyboard navigation)
 * - Compound component architecture (List + ListItem + Content/Action/Icon)
 *
 * @version 1.0.0
 * @author SparkTasks Enterprise UI Team
 */

import React, { createContext, useContext, forwardRef } from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// ===== TYPE DEFINITIONS =====

export type ListVariant =
  | 'default'
  | 'bordered'
  | 'flush'
  | 'spaced'
  | 'relaxed';
export type ListSize = 'sm' | 'md' | 'lg' | 'xl';
export type ListItemVariant =
  | 'default'
  | 'success'
  | 'warning'
  | 'error'
  | 'info';
export type ListItemLayout =
  | 'singleLine'
  | 'multiLine'
  | 'withIcon'
  | 'withAvatar'
  | 'withAction';
export type ListItemSpacing = 'compact' | 'comfortable' | 'spacious';

export interface ListProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Visual style variant */
  variant?: ListVariant;
  /** Size variant affecting text and spacing */
  size?: ListSize;
  /** Whether list has background styling */
  background?: 'none' | 'subtle' | 'elevated';
  /** Whether list items are interactive */
  interactive?: boolean;
  /** Whether list items have hover effects */
  hoverable?: boolean;
  /** Loading state */
  loading?: boolean;
  /** Empty state content */
  emptyContent?: React.ReactNode;
  /** ARIA label for the list */
  'aria-label'?: string;
  children?: React.ReactNode;
}

export interface ListItemProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Visual style variant */
  variant?: ListItemVariant;
  /** Layout pattern for content arrangement */
  layout?: ListItemLayout;
  /** Spacing variant */
  spacing?: ListItemSpacing;
  /** Whether item is interactive (undefined = inherit from context) */
  interactive?: boolean;
  /** Whether item is selected */
  selected?: boolean;
  /** Whether item is disabled */
  disabled?: boolean;
  /** Click handler for interactive items */
  onSelect?: () => void;
  /** ARIA role override */
  role?: string;
  children: React.ReactNode;
}

export interface ListItemContentProps
  extends React.HTMLAttributes<HTMLDivElement> {
  /** Primary title text */
  title?: string;
  /** Secondary subtitle text */
  subtitle?: string;
  /** Descriptive text content */
  description?: string;
  /** Meta information text */
  meta?: string;
  children?: React.ReactNode;
}

export interface ListItemIconProps
  extends React.HTMLAttributes<HTMLDivElement> {
  /** Icon variant affecting color */
  variant?: 'default' | 'primary' | 'success' | 'warning' | 'error' | 'info';
  children: React.ReactNode;
}

export interface ListItemActionProps
  extends React.HTMLAttributes<HTMLDivElement> {
  children: React.ReactNode;
}

// ===== CONTEXT =====

interface ListContextValue {
  size: ListSize;
  interactive: boolean;
  hoverable: boolean;
}

const ListContext = createContext<ListContextValue>({
  size: 'md',
  interactive: false,
  hoverable: false,
});

// ===== UTILITY FUNCTIONS =====

const getListClasses = (
  variant: ListVariant,
  size: ListSize,
  background: 'none' | 'subtle' | 'elevated',
  interactive: boolean,
  hoverable: boolean
) => {
  const baseClasses = DESIGN_TOKENS.recipe.list.base;
  const variantClasses =
    variant !== 'default' && variant in DESIGN_TOKENS.recipe.list
      ? DESIGN_TOKENS.recipe.list[
          variant as keyof typeof DESIGN_TOKENS.recipe.list
        ]
      : '';
  const sizeClasses =
    size in DESIGN_TOKENS.recipe.list
      ? DESIGN_TOKENS.recipe.list[
          size as keyof typeof DESIGN_TOKENS.recipe.list
        ]
      : '';
  const backgroundClasses =
    background !== 'none' && background in DESIGN_TOKENS.recipe.list
      ? DESIGN_TOKENS.recipe.list[
          background as keyof typeof DESIGN_TOKENS.recipe.list
        ]
      : '';
  const interactiveClasses = interactive
    ? DESIGN_TOKENS.recipe.list.interactive
    : '';
  const hoverableClasses = hoverable ? DESIGN_TOKENS.recipe.list.hoverable : '';

  return combineTokens(
    baseClasses,
    variantClasses,
    sizeClasses,
    backgroundClasses,
    interactiveClasses,
    hoverableClasses
  );
};

const getListItemClasses = (
  variant: ListItemVariant,
  layout: ListItemLayout,
  spacing: ListItemSpacing,
  interactive: boolean,
  selected: boolean,
  disabled: boolean,
  hoverable: boolean
) => {
  const baseClasses = DESIGN_TOKENS.recipe.listItem.base;
  const variantClasses =
    variant !== 'default' && variant in DESIGN_TOKENS.recipe.listItem
      ? DESIGN_TOKENS.recipe.listItem[
          variant as keyof typeof DESIGN_TOKENS.recipe.listItem
        ]
      : '';
  const layoutClasses =
    layout in DESIGN_TOKENS.recipe.listItem
      ? DESIGN_TOKENS.recipe.listItem[
          layout as keyof typeof DESIGN_TOKENS.recipe.listItem
        ]
      : '';
  const spacingClasses =
    spacing in DESIGN_TOKENS.recipe.listItem
      ? DESIGN_TOKENS.recipe.listItem[
          spacing as keyof typeof DESIGN_TOKENS.recipe.listItem
        ]
      : '';
  const interactiveClasses =
    interactive || hoverable ? DESIGN_TOKENS.recipe.listItem.interactive : '';
  const selectedClasses = selected
    ? DESIGN_TOKENS.recipe.listItem.selected
    : '';
  const disabledClasses = disabled
    ? DESIGN_TOKENS.recipe.listItem.disabled
    : '';

  return combineTokens(
    baseClasses,
    variantClasses,
    layoutClasses,
    spacingClasses,
    interactiveClasses,
    selectedClasses,
    disabledClasses
  );
};

