);

// ===== MAIN COMPONENTS =====

const ListComponent = forwardRef<HTMLDivElement, ListProps>(
  (
    {
      variant = 'default',
      size = 'md',
      background = 'none',
      interactive = false,
      hoverable = false,
      loading = false,
      emptyContent,
      className,
      children,
      'aria-label': ariaLabel,
      ...props
    },
    ref
  ) => {
    const listClasses = getListClasses(
      variant,
      size,
      background,
      interactive,
      hoverable
    );

    // Handle loading state
    if (loading) {
      return (
        <div
          ref={ref}
          className={combineTokens(listClasses, className)}
          role='list'
          aria-label={ariaLabel}
          {...props}
        >
          <ListSkeleton />
        </div>
      );
    }

    // Handle empty state
    const childrenArray = React.Children.toArray(children);
    if (childrenArray.length === 0 && emptyContent) {
      return (
        <div
          ref={ref}
          className={combineTokens(listClasses, className)}
          role='list'
          aria-label={ariaLabel}
          {...props}
        >
          <ListEmptyState>{emptyContent}</ListEmptyState>
        </div>
      );
    }

    return (
      <ListContext.Provider value={{ size, interactive, hoverable }}>
        <div
          ref={ref}
          className={combineTokens(listClasses, className)}
          role='list'
          aria-label={ariaLabel}
          {...props}
        >
          {children}
        </div>
      </ListContext.Provider>
    );
  }
);

ListComponent.displayName = 'List';

const ListItemComponent = forwardRef<HTMLDivElement, ListItemProps>(
  (
    {
      variant = 'default',
      layout = 'singleLine',
      spacing = 'comfortable',
      interactive,
      selected = false,
      disabled = false,
      onSelect,
      className,
      children,
      role = 'listitem',
      onClick,
      onKeyDown,
      tabIndex,
      ...props
    },
    ref
  ) => {
    const context = useContext(ListContext);

    // Determine if interactive: explicit prop takes priority over context
    const isInteractive =
      interactive === undefined
        ? context.interactive || !!onSelect
        : interactive || !!onSelect;

    const hasHover = context.hoverable && !disabled;

    const itemClasses = getListItemClasses(
      variant,
      layout,
      spacing,
      isInteractive,
      selected,
      disabled,
      hasHover
    );

    const handleClick = (event: React.MouseEvent<HTMLDivElement>) => {
      if (disabled) return;
      onClick?.(event);
      onSelect?.();
    };

    const handleKeyDown = (event: React.KeyboardEvent<HTMLDivElement>) => {
      if (disabled) return;
      onKeyDown?.(event);

      if ((event.key === 'Enter' || event.key === ' ') && onSelect) {
        event.preventDefault();
        onSelect();
      }
    };

    return (
      <div
        ref={ref}
        className={combineTokens(itemClasses, className)}
        role={role}
        tabIndex={isInteractive && !disabled ? (tabIndex ?? 0) : undefined}
        aria-selected={selected}
        aria-disabled={disabled}
        onClick={handleClick}
        onKeyDown={handleKeyDown}
        {...props}
      >
        {children}
      </div>
    );
  }
);

ListItemComponent.displayName = 'ListItem';

const ListItemContentComponent = forwardRef<
  HTMLDivElement,
  ListItemContentProps
>(
  (
    { title, subtitle, description, meta, className, children, ...props },
    ref
  ) => {
    return (
      <div
        ref={ref}
        className={combineTokens(
          DESIGN_TOKENS.recipe.listItemContent.base,
          className
        )}
        {...props}
      >
        {title && (
          <div className={DESIGN_TOKENS.recipe.listItemContent.title}>
            {title}
          </div>
        )}
        {subtitle && (
          <div className={DESIGN_TOKENS.recipe.listItemContent.subtitle}>
            {subtitle}
          </div>
        )}
        {description && (
          <div className={DESIGN_TOKENS.recipe.listItemContent.description}>
            {description}
          </div>
        )}
        {meta && (
          <div className={DESIGN_TOKENS.recipe.listItemContent.meta}>
            {meta}
          </div>
        )}
        {children}
      </div>
    );
  }
);

ListItemContentComponent.displayName = 'ListItem.Content';

const ListItemIconComponent = forwardRef<HTMLDivElement, ListItemIconProps>(
  ({ variant = 'default', className, children, ...props }, ref) => {
    const iconClasses =
      variant !== 'default' && variant in DESIGN_TOKENS.recipe.listItemIcon
        ? DESIGN_TOKENS.recipe.listItemIcon[
            variant as keyof typeof DESIGN_TOKENS.recipe.listItemIcon
          ]
        : DESIGN_TOKENS.recipe.listItemIcon.base;

    return (
      <div
        ref={ref}
        className={combineTokens(iconClasses, className)}
        {...props}
      >
        {children}
      </div>
    );
  }
);

ListItemIconComponent.displayName = 'ListItem.Icon';

const ListItemActionComponent = forwardRef<HTMLDivElement, ListItemActionProps>(
  ({ className, children, ...props }, ref) => {
    return (
      <div
        ref={ref}
        className={combineTokens(
          DESIGN_TOKENS.recipe.listItemAction.base,
          className
        )}
        {...props}
      >
        {children}
      </div>
    );
  }
);

ListItemActionComponent.displayName = 'ListItem.Action';

