 */
export const Popover: React.FC<PopoverProps> = ({
  content,
  children,
  open,
  defaultOpen = false,
  onOpenChange,
  position = 'bottom',
  size = 'md',
  variant = 'default',
  trigger = 'click',
  animation = 'scale',
  showDelay = 0,
  hideDelay = 0,
  offset = 8,
  showArrow = true,
  disabled = false,
  closeOnOutsideClick = true,
  closeOnEscape = true,
  container,
  portal = false,
  className = '',
  'aria-label': ariaLabel,
  'aria-labelledby': ariaLabelledby,
  'aria-describedby': ariaDescribedby,
  ...props
}) => {
  // State management - handled in usePopoverInteraction
  const isControlled = open !== undefined;

  // Refs
  const triggerRef = useRef<HTMLElement>(null);
  const contentRef = useRef<HTMLDivElement>(null);

  // IDs for accessibility
  const triggerId = useMemo(() => generateId('popover-trigger'), []);
  const contentId = useMemo(() => generateId('popover-content'), []);

  // Interaction handling
  const { uncontrolledOpen, setUncontrolledOpen, handlers } =
    usePopoverInteraction(
      trigger,
      showDelay,
      hideDelay,
      disabled,
      isControlled,
      onOpenChange,
      defaultOpen
    );

  const [forceClosed, setForceClosed] = useState(false);
  const isOpen = (isControlled ? open : uncontrolledOpen) && !forceClosed;

  // Reset force closed when component opens again
  useEffect(() => {
    const currentOpen = isControlled ? open : uncontrolledOpen;
    if (currentOpen) {
      setForceClosed(false);
    }
  }, [open, uncontrolledOpen, isControlled]);

  // Positioning
  const { computedPosition, coords } = usePopoverPositioning(
    triggerRef,
    contentRef,
    position,
    offset,
    isOpen
  );

  // Portal target
  const shouldPortal = portal && typeof document !== 'undefined';
  const portalTarget = shouldPortal ? container || document.body : null;

  // Determine if this is an interactive variant
  // Most variants are interactive except pure tooltip
  const isInteractive = variant !== 'tooltip';

  // Outside click handling - using capture phase for better reliability
  useEffect(() => {
    if (!isOpen || !closeOnOutsideClick) return;

    const handleOutside = (event: Event) => {
      const target = event.target as Node;
      const inTrigger = triggerRef.current?.contains(target);
      const inContent = contentRef.current?.contains(target);

      if (!inTrigger && !inContent) {
        onOpenChange?.(false);
        if (!isControlled) {
          setUncontrolledOpen(false);
        }
      }
    };

    // Use both click and pointerdown for better test compatibility
    document.addEventListener('click', handleOutside, { capture: true });
    document.addEventListener('pointerdown', handleOutside, { capture: true });
    return () => {
      document.removeEventListener('click', handleOutside, { capture: true });
      document.removeEventListener('pointerdown', handleOutside, {
        capture: true,
      });
    };
  }, [
    isOpen,
    closeOnOutsideClick,
    onOpenChange,
    isControlled,
    setUncontrolledOpen,
  ]);

  // Escape key handling
  useEffect(() => {
    if (!isOpen || !closeOnEscape) return;

    const handleEscape = (event: KeyboardEvent) => {
      if (event.key === 'Escape') {
        event.preventDefault();

        // Try to close via callback first
        if (onOpenChange) {
          onOpenChange(false);
        } else if (isControlled) {
          // Special case: controlled mode without onOpenChange handler
          // Use force close state to override the controlled state
          setForceClosed(true);
        }

        // For uncontrolled mode, always update internal state
        if (!isControlled) {
          setUncontrolledOpen(false);
        }
      }
    };

    document.addEventListener('keydown', handleEscape);
    return () => document.removeEventListener('keydown', handleEscape);
  }, [isOpen, closeOnEscape, onOpenChange, isControlled, setUncontrolledOpen]);

  // Focus management for interactive variants
  useEffect(() => {
    if (
      (variant === 'menu' || variant === 'dialog') &&
      isOpen &&
      contentRef.current
    ) {
      contentRef.current.tabIndex = -1;
      contentRef.current.focus();
    }
  }, [isOpen, variant]);

  // Enhanced trigger element
  const triggerElement = useMemo(() => {
    if (!isValidElement(children)) {
      return <span ref={triggerRef}>{children}</span>;
    }

    // Keyboard handler for Enter/Space in click mode
    const onKeyDown = (e: React.KeyboardEvent) => {
      if (trigger === 'click' && (e.key === 'Enter' || e.key === ' ')) {
        e.preventDefault();
        handlers.onClick();
      }
    };

    // More specific ARIA haspopup values
    const hasPopup = isInteractive
      ? variant === 'menu'
        ? 'menu'
        : 'dialog'
      : undefined;

    return cloneElement(children as React.ReactElement, {
      ref: triggerRef,
      id: triggerId,
      'aria-expanded': isOpen,
      'aria-haspopup': hasPopup,
      'aria-controls': isOpen ? contentId : undefined,
      onKeyDown,
      ...handlers,
      ...(children as React.ReactElement).props,
    });
  }, [
    children,
    triggerId,
    isOpen,
    contentId,
    isInteractive,
    variant,
    trigger,
    handlers,
  ]);

  // Generate popover classes
  const popoverClasses = `
      ${DESIGN_TOKENS.recipe.hoverCard.panel}
      ${DESIGN_TOKENS.zIndex.popover}
      ${getSizeClasses(size)}
      ${getVariantClasses(variant)}
      ${getAnimationClasses(animation)}
      ${DESIGN_TOKENS.motion.smooth}
      ${disabled ? DESIGN_TOKENS.recipe.listItem.disabled : ''}
      ${className}
    `.trim();

  // Generate arrow classes based on position
  const getArrowClasses = (pos: PopoverPosition) => {
    const base = DESIGN_TOKENS.recipe.hoverCard.arrow.base;

    if (pos.startsWith('top')) {
      return `${base} ${DESIGN_TOKENS.recipe.hoverCard.arrow.bottom}`;
    }
    if (pos.startsWith('bottom')) {
      return `${base} ${DESIGN_TOKENS.recipe.hoverCard.arrow.top}`;
    }
    if (pos.startsWith('left')) {
      return `${base} ${DESIGN_TOKENS.recipe.hoverCard.arrow.right}`;
    }
    if (pos.startsWith('right')) {
      return `${base} ${DESIGN_TOKENS.recipe.hoverCard.arrow.left}`;
    }

    return base;
  };

  // Popover content
  const popoverContent = isOpen ? (
    <div
      ref={contentRef}
      id={contentId}
      role={isInteractive ? 'dialog' : 'tooltip'}
      aria-modal={isInteractive ? 'false' : undefined}
      aria-label={isInteractive ? ariaLabel : undefined}
      aria-labelledby={isInteractive ? ariaLabelledby : undefined}
      aria-describedby={ariaDescribedby}
      className={popoverClasses}
      style={{
        left: coords.x,
        top: coords.y,
      }}
      data-position={position}
      data-actual-position={computedPosition}
      data-size={size}
      data-variant={variant}
      data-animation={animation}
      data-testid='popover-content'
      data-popover=''
      tabIndex={-1}
      {...props}
    >
      {/* Content wrapper with arrow inside for better test queries */}
      <div className={combineTokens('relative')}>
        {showArrow && (
          <div
            className={getArrowClasses(computedPosition)}
            aria-hidden='true'
            data-testid='popover-arrow'
            data-arrow=''
          />
        )}
        {content}
      </div>
    </div>
  ) : null;

  // Context value
  const contextValue: PopoverContextValue = {
    isOpen,
    setOpen: (open: boolean) => {
      onOpenChange?.(open);
      if (!isControlled) {
        setUncontrolledOpen(open);
      }
    },
    triggerId,
    contentId,
    variant,
    size,
  };

  return (
    <PopoverContext.Provider value={contextValue}>
      <span
        className={combineTokens('relative inline-block')}
        data-testid='popover-wrapper'
      >
        {triggerElement}
        {shouldPortal && popoverContent && portalTarget
          ? createPortal(popoverContent, portalTarget)
          : popoverContent}
      </span>
    </PopoverContext.Provider>
  );
};

Popover.displayName = 'Popover';

// Compound component type
interface PopoverCompoundComponent extends React.FC<PopoverProps> {
  Header: typeof PopoverHeader;
  Content: typeof PopoverContent;
  Footer: typeof PopoverFooter;
}

// Export compound components
(Popover as PopoverCompoundComponent).Header = PopoverHeader;
(Popover as PopoverCompoundComponent).Content = PopoverContent;
(Popover as PopoverCompoundComponent).Footer = PopoverFooter;

export default Popover;

/**
 * @fileoverview Prose Component - Markdown/Rich Text Styling System
 * @author SparkTasks
 * @version 1.0.0
 *
 * Enterprise-grade prose component for rendering markdown and rich text content
 * with consistent typography, spacing, and semantic styling.
 *
 * Features:
 * - Typography scale integration with DESIGN_TOKENS
 * - Semantic HTML structure for accessibility
 * - Dark mode support
 * - Responsive design
 * - Code syntax highlighting support
 * - Link and media handling
 * - Custom content variants (article, documentation, legal)
 * - Reading experience optimization
 *
 * @example
 * ```tsx
 * <Prose variant="article">
 *   <h1>Article Title</h1>
 *   <p>Article content with proper typography...</p>
 * </Prose>
 * ```
 */

import React from 'react';

import { DESIGN_TOKENS } from '@/design/tokens';

// ===== TYPES =====

/**
 * Prose content variants for different use cases
 */
export type ProseVariant =
  | 'default' // Standard content styling
  | 'article' // Blog posts, articles - optimized reading
  | 'documentation' // Technical docs - enhanced code blocks
  | 'legal' // Legal documents - formal typography
  | 'compact' // Reduced spacing for dense content
  | 'large'; // Larger text for accessibility

/**
 * Reading optimization modes
 */
export type ProseReadingMode =
  | 'default' // Standard line height and spacing
  | 'comfortable' // Increased spacing for better readability
  | 'dense'; // Reduced spacing for information density

/**
 * Content size scales
 */
export type ProseSize = 'sm' | 'md' | 'lg' | 'xl';

/**
 * Props for the Prose component
 */
export interface ProseProps extends React.HTMLAttributes<HTMLDivElement> {
  /** Content variant affecting typography and spacing */
  variant?: ProseVariant;

  /** Reading optimization mode */
  readingMode?: ProseReadingMode;

  /** Overall content size scale */
  size?: ProseSize;

  /** Enable syntax highlighting for code blocks */
  enableSyntaxHighlighting?: boolean;

  /** Maximum content width for optimal reading */
  maxWidth?: 'none' | 'sm' | 'md' | 'lg' | 'xl' | 'full';

  /** Children content to render with prose styles */
  children: React.ReactNode;

  /** Additional CSS classes */
  className?: string;

  /** Enable table of contents generation */
  enableToc?: boolean;

  /** Custom link handler */
  onLinkClick?: (href: string, event: React.MouseEvent) => void;
}

// ===== UTILITY FUNCTIONS =====

/**
 * Get base prose styling classes
 */
const getProseBaseClasses = (): string => {
  return `
    ${DESIGN_TOKENS.typography.body.md}
    ${DESIGN_TOKENS.semantic.text.primary}
    leading-relaxed
    antialiased
  `.trim();
};

/**
 * Get variant-specific styling classes
 */
const getProseVariantClasses = (variant: ProseVariant): string => {
  const variants = {
    default: '',
    article: `
      ${DESIGN_TOKENS.typography.body.lg}
      leading-loose
      tracking-wide
    `,
    documentation: `
      ${DESIGN_TOKENS.typography.body.md}
      font-mono
      tracking-tight
    `,
    legal: `
      ${DESIGN_TOKENS.typography.body.sm}
      leading-normal
      tracking-normal
      text-justify
    `,
    compact: `
      ${DESIGN_TOKENS.typography.body.sm}
      leading-normal
      space-y-2
    `,
    large: `
      ${DESIGN_TOKENS.typography.body.xl}
      leading-loose
      tracking-wide
    `,
  };

  return variants[variant].trim();
};

/**
 * Get reading mode styling classes
 */
const getProseReadingModeClasses = (mode: ProseReadingMode): string => {
  const modes = {
    default: 'space-y-4',
    comfortable: 'space-y-6',
    dense: 'space-y-2',
  };

  return modes[mode];
};

/**
 * Get size-specific styling classes
 */
const getProseSizeClasses = (size: ProseSize): string => {
  const sizes = {
    sm: DESIGN_TOKENS.typography.body.sm,
    md: DESIGN_TOKENS.typography.body.md,
    lg: DESIGN_TOKENS.typography.body.lg,
    xl: DESIGN_TOKENS.typography.body.xl,
  };

  return sizes[size];
};

/**
 * Get max width styling classes
 */
const getProseMaxWidthClasses = (maxWidth: ProseProps['maxWidth']): string => {
  const widths = {
    none: '',
    sm: 'max-w-sm',
    md: 'max-w-2xl',
    lg: 'max-w-4xl',
    xl: 'max-w-6xl',
    full: 'max-w-full',
  };

  return widths[maxWidth || 'md'];
};

/**
 * Get comprehensive prose typography styles
 */
const getProseTypographyStyles = (): string => {
  return `
    /* Headings */
    prose-headings:${DESIGN_TOKENS.semantic.text.primary}
