/**
 * SpeedDial Component - Enterprise-Grade Floating Action Button with Menu
 *
 * Features:
 * - Floating action button (FAB) with expandable menu
 * - 8-direction placement system (top, bottom, left, right, corners)
 * - Smooth open/close animations with staggered menu items
 * - Keyboard navigation (Arrow keys, Enter, Escape, Tab)
 * - Accessibility compliance (WCAG 2.1 AA) with proper ARIA
 * - Click outside detection and focus management
 * - Icon rotation and visual feedback on expansion
 * - Theme-aware styling with dark mode support
 * - Mobile-friendly touch interactions with proper sizing
 * - Enterprise motion system integration with reduced motion support
 * - Controlled/uncontrolled modes for flexible state management
 * - Loading and disabled states with visual feedback
 * - Custom trigger and menu item support
 */

import { Plus, X } from 'lucide-react';
import React, { useState, useRef, useEffect, useCallback } from 'react';

import { DESIGN_TOKENS } from '@/design/tokens';

// Type definitions
export type SpeedDialPlacement =
  | 'top'
  | 'top-left'
  | 'top-right'
  | 'bottom'
  | 'bottom-left'
  | 'bottom-right'
  | 'left'
  | 'right';

export interface SpeedDialAction {
  id: string;
  label: string;
  icon: React.ReactNode;
  disabled?: boolean;
  onClick?: () => void;
  href?: string;
  target?: string;
  className?: string;
}

export interface SpeedDialProps {
  // Content
  actions: SpeedDialAction[];
  children?: React.ReactNode; // Custom trigger content

  // Trigger configuration
  triggerIcon?: React.ReactNode;
  triggerSize?: 'sm' | 'md' | 'lg';
  triggerVariant?: 'primary' | 'secondary' | 'accent';

  // Menu configuration
  placement?: SpeedDialPlacement;

  // Position
  position?:
    | 'bottom-right'
    | 'bottom-left'
    | 'top-right'
    | 'top-left'
    | 'custom';
  customPosition?: string; // Custom positioning classes

  // Accessibility
  ariaLabel?: string;
  menuId?: string;

  // State
  disabled?: boolean;
  loading?: boolean;

  // Controlled state
  isOpen?: boolean;
  onOpenChange?: (open: boolean) => void;

  // Events
  onActionSelect?: (action: SpeedDialAction) => void;

  // Styling
  className?: string;
  menuClassName?: string;
}

export function SpeedDial({
  actions,
  children,
  triggerIcon,
  triggerSize = 'lg',
  triggerVariant = 'primary',
  placement = 'top',
  position = 'bottom-right',
  customPosition,
  ariaLabel = 'More actions',
  menuId,
  disabled = false,
  loading = false,
  isOpen: controlledOpen,
  onOpenChange,
  onActionSelect,
  className = '',
  menuClassName = '',
}: SpeedDialProps) {
  // State management
  const [internalOpen, setInternalOpen] = useState(false);
  const isOpen = controlledOpen === undefined ? internalOpen : controlledOpen;

  const setIsOpen = useCallback(
    (open: boolean) => {
      if (controlledOpen === undefined) {
        setInternalOpen(open);
      }
      onOpenChange?.(open);
    },
    [controlledOpen, onOpenChange]
  );

  // Refs for DOM manipulation
  const triggerRef = useRef<HTMLButtonElement>(null);
  const menuRef = useRef<HTMLDivElement>(null);
  const [focusedIndex, setFocusedIndex] = useState(-1);

  // Generate unique IDs
  const generatedMenuId = useRef(
    `speed-dial-${Math.random().toString(36).slice(2, 11)}`
  );
  const actualMenuId = menuId || generatedMenuId.current;

  // Click outside detection
  useEffect(() => {
    const handleClickOutside = (event: MouseEvent) => {
      if (
        isOpen &&
        triggerRef.current &&
        menuRef.current &&
        !triggerRef.current.contains(event.target as Node) &&
        !menuRef.current.contains(event.target as Node)
      ) {
        setIsOpen(false);
        setFocusedIndex(-1);
      }
    };

    document.addEventListener('mousedown', handleClickOutside);
    return () => document.removeEventListener('mousedown', handleClickOutside);
  }, [isOpen, setIsOpen]);

  // Keyboard navigation
  useEffect(() => {
    const handleKeyDown = (event: KeyboardEvent) => {
      if (!isOpen) return;

      switch (event.key) {
        case 'Escape': {
          event.preventDefault();
          setIsOpen(false);
          setFocusedIndex(-1);
          triggerRef.current?.focus();
          break;
        }

        case 'ArrowUp':
        case 'ArrowDown':
        case 'ArrowLeft':
        case 'ArrowRight': {
          event.preventDefault();
          const activeActions = actions.filter(action => !action.disabled);
          if (activeActions.length === 0) return;

          const isVertical =
            placement.includes('top') ||
            placement.includes('bottom') ||
            placement === 'left' ||
            placement === 'right';
          const isReverse =
            placement.includes('bottom') || placement === 'right';

          let shouldMove = false;
          shouldMove = isVertical
            ? event.key === 'ArrowUp' || event.key === 'ArrowDown'
            : event.key === 'ArrowLeft' || event.key === 'ArrowRight';

          if (shouldMove) {
            const increment =
              event.key === 'ArrowUp' || event.key === 'ArrowLeft'
                ? isReverse
                  ? 1
                  : -1
                : isReverse
                  ? -1
                  : 1;
            setFocusedIndex(prev => {
              const newIndex = prev + increment;
              if (newIndex >= activeActions.length) return 0;
              if (newIndex < 0) return activeActions.length - 1;
              return newIndex;
            });
          }
          break;
        }

        case 'Enter':
        case ' ': {
          event.preventDefault();
          if (focusedIndex >= 0 && focusedIndex < actions.length) {
            const action = actions.filter(a => !a.disabled)[focusedIndex];
            if (action && !action.disabled && action.onClick) {
              action.onClick();
              setIsOpen(false);
              setFocusedIndex(-1);
            }
          }
          break;
        }

        case 'Tab': {
          // Allow tabbing out of the menu
          setIsOpen(false);
          setFocusedIndex(-1);
          break;
        }
      }
    };

    if (isOpen) {
      document.addEventListener('keydown', handleKeyDown);
      return () => document.removeEventListener('keydown', handleKeyDown);
    }
  }, [isOpen, focusedIndex, actions, setIsOpen, placement]);

  // Get positioning classes based on placement
  const getPositionClasses = (): string => {
    if (customPosition) return customPosition;

    const positionMap: Record<string, string> = {
      'bottom-right': DESIGN_TOKENS.position.fixed.bottomRight,
      'bottom-left': DESIGN_TOKENS.position.fixed.bottomLeft,
      'top-right': DESIGN_TOKENS.position.fixed.topRight,
      'top-left': DESIGN_TOKENS.position.fixed.topLeft,
    };

    return positionMap[position] || positionMap['bottom-right'];
  };

  // Get menu positioning relative to trigger
  const getMenuPlacement = (): string => {
    const placementMap = {
      top: 'bottom-0 left-1/2 transform -translate-x-1/2 mb-4',
      'top-left': 'bottom-0 right-0 mb-4 mr-4',
      'top-right': 'bottom-0 left-0 mb-4 ml-4',
      bottom: 'top-0 left-1/2 transform -translate-x-1/2 mt-4',
      'bottom-left': 'top-0 right-0 mt-4 mr-4',
      'bottom-right': 'top-0 left-0 mt-4 ml-4',
      left: 'right-0 top-1/2 transform -translate-y-1/2 mr-4',
      right: 'left-0 top-1/2 transform -translate-y-1/2 ml-4',
    };

    return placementMap[placement] || placementMap['top'];
  };

  // Get menu layout direction
  const getMenuDirection = (): string => {
    const isHorizontal = placement === 'left' || placement === 'right';
    return isHorizontal ? 'flex-row space-x-2' : 'flex-col space-y-2';
  };

  // Event handlers
  const handleTriggerClick = () => {
    if (disabled || loading) return;
    const newOpen = !isOpen;
    setIsOpen(newOpen);
    if (newOpen) {
      setFocusedIndex(-1);
    }
  };

  const handleActionClick = (action: SpeedDialAction) => {
    if (action.disabled) return;

    // Handle link navigation
    if (action.href) {
      if (action.target === '_blank') {
        window.open(action.href, '_blank', 'noopener,noreferrer');
      } else {
        globalThis.location.href = action.href;
      }
    }

    // Call action onClick handler
    action.onClick?.();

    // Call menu onActionSelect handler
    onActionSelect?.(action);

    // Close menu after selection
    setIsOpen(false);
    setFocusedIndex(-1);

    // Return focus to trigger
    triggerRef.current?.focus();
  };

  // Get trigger size classes
  const getTriggerSizeClasses = (): string => {
    const sizeMap = {
      sm: 'w-12 h-12',
      md: 'w-14 h-14',
      lg: 'w-16 h-16',
    };
    return sizeMap[triggerSize];
  };

  // Get trigger variant classes
  const getTriggerVariantClasses = (): string => {
    const variantMap = {
      primary: DESIGN_TOKENS.recipe.button.primary,
      secondary: DESIGN_TOKENS.recipe.button.secondary,
      accent: 'bg-accent-600 hover:bg-accent-700 text-white',
    };
    return variantMap[triggerVariant];
  };

  // Get action button classes
  const getActionButtonClasses = (action: SpeedDialAction): string => {
    const activeActions = actions.filter(a => !a.disabled);
    const activeIndex = activeActions.indexOf(action);
    const isActionFocused = focusedIndex === activeIndex;

    return [
      // Base button styling
      'w-10 h-10 rounded-full shadow-lg border border-white/20',
      'flex items-center justify-center',
      'transition-all duration-200 ease-out',
      // Theme aware colors
      'bg-white dark:bg-slate-800 text-slate-700 dark:text-slate-300',
      'hover:bg-slate-50 dark:hover:bg-slate-700',
      // Focus state
      isActionFocused ? 'ring-2 ring-primary-500 ring-offset-2' : '',
      // Disabled state
      action.disabled ? DESIGN_TOKENS.state.disabled : 'cursor-pointer',
      // Hover effects
      action.disabled ? '' : DESIGN_TOKENS.motion.semantic.hoverLift,
      // Custom classes
      action.className || '',
    ]
      .filter(Boolean)
      .join(' ');
  };

  return (
    <div className={`${getPositionClasses()} ${className}`}>
      {/* Trigger Button */}
      <button
        ref={triggerRef}
        onClick={handleTriggerClick}
        className={[
          // Base styling
          getTriggerSizeClasses(),
          'rounded-full shadow-lg',
          'flex items-center justify-center',
          'transition-all duration-200 ease-out',
          // Variant styling
          getTriggerVariantClasses(),
          // State styling
          disabled ? DESIGN_TOKENS.state.disabled : '',
          loading ? 'cursor-wait' : '',
          // Interactive effects
          !disabled && !loading ? DESIGN_TOKENS.motion.semantic.hoverLift : '',
          // Z-index
          DESIGN_TOKENS.zIndex.overlay,
        ]
          .filter(Boolean)
          .join(' ')}
        disabled={disabled}
        aria-label={ariaLabel}
        aria-expanded={isOpen}
        aria-haspopup='menu'
        aria-controls={isOpen ? actualMenuId : undefined}
        type='button'
      >
        {loading ? (
          <div
            className={`${DESIGN_TOKENS.icon.size.md} animate-spin rounded-full border-2 border-current border-t-transparent`}
          />
        ) : (
          children || (
            <div
              className={`${DESIGN_TOKENS.icon.size.md} transition-transform duration-200 ${isOpen ? 'rotate-45' : ''}`}
            >
              {triggerIcon || (isOpen ? <X /> : <Plus />)}
            </div>
          )
        )}
      </button>

      {/* Action Menu */}
      {(isOpen || loading) && (
        <div
          ref={menuRef}
          id={actualMenuId}
          className={[
            // Positioning
            'absolute',
            getMenuPlacement(),
            // Layout
            'flex',
            getMenuDirection(),
            // Z-index
            DESIGN_TOKENS.zIndex.overlay,
            // Animation
            isOpen
              ? 'scale-100 opacity-100'
              : 'pointer-events-none scale-95 opacity-0',
            DESIGN_TOKENS.motion.smooth,
            // Custom styling
            menuClassName,
          ]
            .filter(Boolean)
            .join(' ')}
          role='menu'
          aria-orientation={
            placement === 'left' || placement === 'right'
              ? 'horizontal'
              : 'vertical'
          }
          aria-labelledby={triggerRef.current?.id}
        >
          {actions.map((action, index) => (
            <div
              key={action.id}
              className={getActionButtonClasses(action)}
              role='menuitem'
              tabIndex={action.disabled ? -1 : 0}
              aria-disabled={action.disabled}
              title={action.label}
              onClick={() => handleActionClick(action)}
              onKeyDown={e => {
                if (e.key === 'Enter' || e.key === ' ') {
                  e.preventDefault();
                  handleActionClick(action);
                }
              }}
              style={{
                // Staggered animation delay
                animationDelay: isOpen ? `${index * 50}ms` : '0ms',
              }}
            >
              <span className={DESIGN_TOKENS.icon.size.sm} aria-hidden='true'>
                {action.icon}
              </span>
              <span className='sr-only'>{action.label}</span>
            </div>
          ))}
        </div>
      )}
    </div>
  );
}

export default SpeedDial;

/**
 * Spinner Component - Enterprise-Grade Loading Indicator
 *
 * A sophisticated loading spinner component built with DESIGN_TOKENS V3.2:
 * - Multiple sizes (xs, sm, md, lg, xl) with icon-consistent sizing
 * - Semantic variants (primary, secondary, muted) for different contexts
 * - Accessibility-first with proper ARIA labels and screen reader support
 * - Motion-safe design respecting user's reduced motion preferences
 * - Theme-aware with light/dark mode support
 * - Customizable speed and styles while maintaining design system consistency
 *
 * @example
 * ```tsx
 * // Basic usage
 * <Spinner />
 *
 * // Large spinner with custom label
 * <Spinner size="lg" label="Loading dashboard data..." />
 *
 * // Secondary variant for subtle loading states
 * <Spinner variant="secondary" size="sm" />
 *
 * // Inline spinner with text
 * <div className="flex items-center gap-2">
 *   <Spinner size="sm" />
 *   <span>Processing...</span>
 * </div>
 * ```
 */

import React from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';
import type { ComponentSize } from '@/design/tokens';

// ===== TYPES =====

export type SpinnerVariant = 'primary' | 'secondary' | 'muted';
export type SpinnerSpeed = 'slow' | 'normal' | 'fast';

export interface SpinnerProps {
  /** Size of the spinner - matches icon sizing system */
  size?: ComponentSize;
  /** Visual variant for different contexts */
  variant?: SpinnerVariant;
  /** Animation speed */
  speed?: SpinnerSpeed;
  /** Accessible label for screen readers */
  label?: string;
  /** Additional CSS classes */
  className?: string;
  /** Whether to show as inline element */
  inline?: boolean;
  /** Custom test ID for testing */
  'data-testid'?: string;
}

// ===== SPINNER VARIANTS =====

const spinnerVariants = {
  primary: {
    circle: combineTokens(
      'text-blue-600 dark:text-blue-400',
      'stroke-current fill-none'
    ),
    track: 'text-blue-200 dark:text-blue-800 stroke-current fill-none',
  },
  secondary: {
    circle: combineTokens(
      'text-gray-600 dark:text-gray-400',
      'stroke-current fill-none'
    ),
    track: 'text-gray-200 dark:text-gray-700 stroke-current fill-none',
  },
  muted: {
    circle: combineTokens(
      'text-gray-400 dark:text-gray-500',
      'stroke-current fill-none'
    ),
    track: 'text-gray-200 dark:text-gray-800 stroke-current fill-none',
  },
} as const;

// ===== SPINNER SIZES =====

const spinnerSizes = {
  xs: {
    container: 'h-3 w-3',
    strokeWidth: '3',
    viewBox: '0 0 24 24',
  },
  sm: {
    container: 'h-4 w-4',
    strokeWidth: '2.5',
    viewBox: '0 0 24 24',
  },
  md: {
    container: 'h-5 w-5',
    strokeWidth: '2',
    viewBox: '0 0 24 24',
  },
  lg: {
    container: 'h-6 w-6',
    strokeWidth: '2',
    viewBox: '0 0 24 24',
  },
  xl: {
    container: 'h-8 w-8',
    strokeWidth: '1.5',
    viewBox: '0 0 24 24',
  },
} as const;

// ===== SPINNER SPEEDS =====

const spinnerSpeeds = {
  slow: '[animation-duration:2s]',
  normal: '[animation-duration:1s]',
  fast: '[animation-duration:0.5s]',
} as const;

// ===== SPINNER COMPONENT =====

export const Spinner = React.forwardRef<SVGSVGElement, SpinnerProps>(
  (
    {
      size = 'md',
      variant = 'primary',
      speed = 'normal',
      label = 'Loading...',
      className,
      inline = false,
      'data-testid': testId,
      ...props
    },
    ref
  ) => {
    const sizeConfig = spinnerSizes[size];
    const variantStyles = spinnerVariants[variant];
    const speedClass = spinnerSpeeds[speed];

    const containerClasses = combineTokens(
      // Base spinner animation and sizing
      DESIGN_TOKENS.motion.spin,
      sizeConfig.container,
      speedClass,

      // Display mode
      inline ? 'inline-block' : 'block',

      // Motion reduction support
      'motion-reduce:animate-none motion-reduce:opacity-50',

      // Custom classes
      className
    );

    return (
      <svg
        ref={ref}
        className={containerClasses}
        viewBox={sizeConfig.viewBox}
        role='status'
        aria-label={label}
        aria-live='polite'
        data-testid={testId}
        {...props}
      >
        {/* Background circle track */}
        <circle
          cx='12'
          cy='12'
          r='10'
          strokeWidth={sizeConfig.strokeWidth}
          className={variantStyles.track}
          opacity='0.25'
        />

        {/* Animated progress circle */}
        <circle
          cx='12'
          cy='12'
          r='10'
          strokeWidth={sizeConfig.strokeWidth}
          strokeLinecap='round'
          strokeDasharray='32'
          strokeDashoffset='24'
          className={variantStyles.circle}
          transform='rotate(-90 12 12)'
        >
          {/* Only animate if motion is not reduced */}
          <animateTransform
            attributeName='transform'
            attributeType='XML'
            type='rotate'
            from='-90 12 12'
            to='270 12 12'
            dur={speed === 'fast' ? '0.5s' : speed === 'slow' ? '2s' : '1s'}
            repeatCount='indefinite'
          />
        </circle>

        {/* Screen reader text */}
        <title>{label}</title>
      </svg>
    );
  }
);

Spinner.displayName = 'Spinner';

// ===== COMPOUND COMPONENTS =====

/**
 * Spinner with text - combines spinner with loading text
 */
export interface SpinnerWithTextProps extends SpinnerProps {
  /** Text to display alongside spinner */
  text: string;
  /** Position of text relative to spinner */
  textPosition?: 'right' | 'bottom';
}

export const SpinnerWithText: React.FC<SpinnerWithTextProps> = ({
  text,
  textPosition = 'right',
  size = 'md',
  ...spinnerProps
}) => {
  const isVertical = textPosition === 'bottom';

  return (
    <div
      className={combineTokens(
        'flex items-center',
        isVertical ? 'flex-col gap-2' : 'gap-3'
      )}
      aria-label={`${spinnerProps.label || 'Loading'}: ${text}`}
    >
      <Spinner size={size} inline={!isVertical} {...spinnerProps} />
      <span
        className={combineTokens(
          DESIGN_TOKENS.semantic.text.muted,
          DESIGN_TOKENS.typography.body.small
        )}
      >
        {text}
      </span>
    </div>
  );
};

// ===== SPECIALTY SPINNERS =====

/**
 * Page Spinner - Full page loading overlay
 */
export interface PageSpinnerProps
  extends Pick<SpinnerProps, 'label' | 'variant'> {
  /** Whether to show backdrop */
  backdrop?: boolean;
}

export const PageSpinner: React.FC<PageSpinnerProps> = ({
  label = 'Loading page...',
  variant = 'primary',
  backdrop = true,
}) => {
  return (
    <div
      className={combineTokens(
        'fixed inset-0 z-50 flex items-center justify-center',
        backdrop && 'bg-white/80 backdrop-blur-sm dark:bg-gray-900/80'
      )}
      aria-label={label}
    >
      <div
        className={combineTokens(
          DESIGN_TOKENS.layout.flex.col,
          DESIGN_TOKENS.layout.flex.itemsCenter,
          DESIGN_TOKENS.layout.spacing.gap.lg
        )}
      >
        <Spinner size='xl' variant={variant} label={label} />
        <p
          className={combineTokens(
            DESIGN_TOKENS.semantic.text.muted,
            DESIGN_TOKENS.typography.body.small
          )}
        >
          {label}
        </p>
      </div>
    </div>
  );
};

/**
 * Button Spinner - For loading states in buttons
 */
export interface ButtonSpinnerProps
  extends Pick<SpinnerProps, 'size' | 'variant'> {
  /** Whether button is in loading state */
  loading?: boolean;
  /** Content to show when not loading */
  children: React.ReactNode;
}

export const ButtonSpinner: React.FC<ButtonSpinnerProps> = ({
  loading = false,
  size = 'sm',
  variant = 'primary',
  children,
}) => {
  return (
    <div
      className={combineTokens(
        DESIGN_TOKENS.layout.flex.row,
        DESIGN_TOKENS.layout.flex.itemsCenter,
        DESIGN_TOKENS.layout.spacing.gap.sm
      )}
    >
      {loading && (
        <Spinner size={size} variant={variant} inline label='Processing...' />
      )}
      <span className={loading ? 'opacity-70' : undefined}>{children}</span>
    </div>
  );
};

// ===== EXPORTS =====

export default Spinner;

/**
 * SplitButton Component - Enterprise-Grade Button with Dropdown
 *
 * Advanced DESIGN_TOKENS V3.2 implementation showcasing:
 * - Sophisticated split-button pattern with primary action + dropdown
 * - Seamless visual integration using connected button styling
 * - Comprehensive accessibility support with proper ARIA patterns
 * - Keyboard navigation (Space/Enter for action, Arrow keys for menu)
 * - Enterprise-grade dropdown positioning and z-index management
 * - Dark mode and theme-aware styling throughout
 * - Performance-optimized rendering with proper event handling
 */

import { ChevronDown } from 'lucide-react';
import React, { useState, useRef, useEffect } from 'react';

import { Button } from '@/components/ui/Button';
import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

interface SplitButtonItem {
  id: string;
  label: string;
  icon?: React.ReactNode;
  disabled?: boolean;
  onClick?: () => void;
}

interface SplitButtonProps {
  // Main action button props
  children: React.ReactNode;
  onClick?: () => void;

  // Dropdown items
  items: SplitButtonItem[];

  // Styling props
  variant?:
    | 'primary'
    | 'secondary'
    | 'ghost'
    | 'destructive'
    | 'outline'
    | 'link';
  size?: 'sm' | 'md' | 'lg' | 'xl';

  // State props
  disabled?: boolean;
  pending?: boolean;

  // Layout props
  placement?: 'bottom-start' | 'bottom-end' | 'top-start' | 'top-end';
  fullWidth?: boolean;

  // Accessibility
  'aria-label'?: string;

  // Advanced props
  className?: string;
  dropdownClassName?: string;
}

export const SplitButton = React.forwardRef<HTMLDivElement, SplitButtonProps>(
  (
    {
      children,
      onClick,
      items = [],
      variant = 'primary',
      size = 'md',
      disabled = false,
      pending = false,
      placement = 'bottom-start',
      fullWidth = false,
      'aria-label': ariaLabel,
      className = '',
      dropdownClassName = '',
      ...props
    },
    ref
  ) => {
    const [isOpen, setIsOpen] = useState(false);
    const [focusedIndex, setFocusedIndex] = useState(-1);
    const dropdownRef = useRef<HTMLDivElement>(null);
    const triggerRef = useRef<HTMLButtonElement>(null);

    // Handle main action click
    const handleMainClick = () => {
      if (disabled || pending) return;
      onClick?.();
    };

    // Handle dropdown toggle
    const handleDropdownToggle = () => {
      if (disabled) return;
      setIsOpen(prev => !prev);
      setFocusedIndex(-1);
    };

    // Close dropdown
    const closeDropdown = () => {
      setIsOpen(false);
      setFocusedIndex(-1);
    };

    // Handle item click
    const handleItemClick = (item: SplitButtonItem) => {
      if (item.disabled) return;
      item.onClick?.();
      closeDropdown();
      triggerRef.current?.focus();
    };

    // Handle keyboard navigation
    const handleKeyDown = (event: React.KeyboardEvent) => {
      if (disabled) return;

      switch (event.key) {
        case 'ArrowDown': {
          event.preventDefault();
          if (isOpen) {
            setFocusedIndex(prev => (prev < items.length - 1 ? prev + 1 : 0));
          } else {
            setIsOpen(true);
            setFocusedIndex(0);
          }
          break;
        }

        case 'ArrowUp': {
          event.preventDefault();
          if (isOpen) {
            setFocusedIndex(prev => (prev > 0 ? prev - 1 : items.length - 1));
          }
          break;
        }

        case 'Enter':
        case ' ': {
          event.preventDefault();
          if (isOpen && focusedIndex >= 0) {
            handleItemClick(items[focusedIndex]);
          } else {
            handleDropdownToggle();
          }
          break;
        }

        case 'Escape': {
          event.preventDefault();
          closeDropdown();
          triggerRef.current?.focus();
          break;
        }
      }
    };

    // Close dropdown when clicking outside
    useEffect(() => {
      const handleClickOutside = (event: MouseEvent) => {
        if (
          dropdownRef.current &&
          !dropdownRef.current.contains(event.target as Node) &&
          !triggerRef.current?.contains(event.target as Node)
        ) {
          closeDropdown();
        }
      };

      if (isOpen) {
        document.addEventListener('mousedown', handleClickOutside);
        return () =>
          document.removeEventListener('mousedown', handleClickOutside);
      }
    }, [isOpen]);

    // Focus management for accessibility
    useEffect(() => {
      if (isOpen && focusedIndex >= 0) {
        const focusedItem = dropdownRef.current?.children[
          focusedIndex
        ] as HTMLElement;
        focusedItem?.focus();
      }
    }, [focusedIndex, isOpen]);

    // Container classes using enterprise tokens
    const containerClasses = [
      'relative inline-flex',
      fullWidth ? 'w-full' : '',
      className,
    ]
      .filter(Boolean)
      .join(' ');

    // Button group classes for seamless connection
    const buttonGroupClasses = [
      'inline-flex',
      fullWidth ? 'w-full' : '',
      // Connected styling
      '[&>button]:rounded-none',
      '[&>button:first-child]:rounded-l-md',
      '[&>button:last-child]:rounded-r-md',
      '[&>button:not(:first-child)]:border-l-0',
      '[&>button:not(:first-child)]:-ml-px',
      // Focus management for connected buttons
      '[&>button:focus]:relative',
      '[&>button:focus]:z-10',
    ]
      .filter(Boolean)
      .join(' ');

    // Dropdown trigger classes
    const dropdownTriggerClasses = [
      'aspect-square',
      'inline-flex items-center justify-center',
      // Use same variant as main button for consistency
      DESIGN_TOKENS.recipe.button.base,
      DESIGN_TOKENS.recipe.button[variant],
      DESIGN_TOKENS.recipe.button[size],
      disabled || pending ? DESIGN_TOKENS.state.disabled : '',
      // Visual indicator for dropdown state
      isOpen ? 'relative z-20' : '',
    ]
      .filter(Boolean)
      .join(' ');

    // Dropdown content classes using enterprise tokens
    const dropdownContentClasses = [
      DESIGN_TOKENS.recipe.dropdown.content,
      DESIGN_TOKENS.zIndex.dropdown,
      'shadow-lg',
      // Positioning based on placement
      'absolute mt-1',
      placement.includes('end') ? 'right-0' : 'left-0',
      placement.includes('top') ? 'bottom-full mb-1 mt-0' : '',
      // Animation classes
      isOpen ? 'opacity-100 scale-100' : 'opacity-0 scale-95',
      'transition-all duration-200 ease-out',
      // Custom dropdown styling
      dropdownClassName,
    ]
      .filter(Boolean)
      .join(' ');

    // Dropdown item classes
    const getItemClasses = (item: SplitButtonItem, index: number) =>
      [
        DESIGN_TOKENS.recipe.dropdown.item,
        item.disabled ? DESIGN_TOKENS.state.disabled : '',
        focusedIndex === index ? 'bg-slate-100 dark:bg-slate-800' : '',
        // Icon spacing
        item.icon ? 'gap-2' : '',
      ]
        .filter(Boolean)
        .join(' ');

    return (
      <div ref={ref} className={containerClasses} {...props}>
        {/* Split Button Group */}
        <div className={buttonGroupClasses}>
          {/* Main Action Button */}
          <Button
            variant={variant}
            size={size}
            disabled={disabled}
            pending={pending}
            onClick={handleMainClick}
            className={fullWidth ? 'flex-1' : ''}
            aria-label={ariaLabel}
            data-variant={variant}
            data-size={size}
          >
            {children}
          </Button>

          {/* Dropdown Trigger Button */}
          <button
            ref={triggerRef}
            type='button'
            className={dropdownTriggerClasses}
            disabled={disabled || pending}
            onClick={handleDropdownToggle}
            onKeyDown={handleKeyDown}
            aria-expanded={isOpen}
            aria-haspopup='menu'
            aria-label='Show more options'
            data-state={isOpen ? 'open' : 'closed'}
            data-variant={variant}
            data-size={size}
          >
            <ChevronDown
              size={
                size === 'sm'
                  ? 14
                  : size === 'lg'
                    ? 18
                    : size === 'xl'
                      ? 20
                      : 16
              }
              className={`transition-transform duration-200 ${isOpen ? 'rotate-180' : ''}`}
            />
          </button>
        </div>

        {/* Dropdown Menu */}
        {isOpen && (
          <div
            ref={dropdownRef}
            className={dropdownContentClasses}
            role='menu'
            aria-orientation='vertical'
            data-placement={placement}
          >
            {items.map((item, index) => (
              <button
                key={item.id}
                type='button'
                className={getItemClasses(item, index)}
                disabled={item.disabled}
                onClick={() => handleItemClick(item)}
                onKeyDown={e => {
                  switch (e.key) {
                    case 'Enter':
                    case ' ': {
                      e.preventDefault();
                      handleItemClick(item);

                      break;
                    }
                    case 'Escape': {
                      e.preventDefault();
                      closeDropdown();
                      triggerRef.current?.focus();

                      break;
                    }
                    case 'ArrowDown': {
                      e.preventDefault();
                      setFocusedIndex(prev =>
                        prev < items.length - 1 ? prev + 1 : 0
                      );

                      break;
                    }
                    case 'ArrowUp': {
                      e.preventDefault();
                      setFocusedIndex(prev =>
                        prev > 0 ? prev - 1 : items.length - 1
                      );

                      break;
                    }
                    // No default
                  }
                }}
                onMouseEnter={() => setFocusedIndex(index)}
                role='menuitem'
                tabIndex={focusedIndex === index ? 0 : -1}
                aria-disabled={item.disabled}
              >
                {item.icon && (
                  <span
                    className={combineTokens('flex-shrink-0')}
                    aria-hidden='true'
                  >
                    {item.icon}
                  </span>
                )}
                <span className={combineTokens('flex-1', 'text-left')}>
                  {item.label}
                </span>
              </button>
            ))}
          </div>
        )}
      </div>
    );
  }
);

SplitButton.displayName = 'SplitButton';

export default SplitButton;

/**
 * TableOfContents Component - Enterprise Navigation for Long Content
 *
 * A comprehensive table of contents component providing structured navigation
 * for long-form content with enterprise-grade functionality including:
 * - Automatic heading extraction from content
 * - Interactive navigation with smooth scrolling
 * - Current section highlighting with intersection observer
 * - Nested heading hierarchy support (h1-h6)
 * - Accessibility compliance (ARIA patterns, keyboard navigation)
 * - Responsive design with mobile and desktop layouts
 * - Sticky positioning with scroll progress indicator
 *
 * @version 1.0.0
 * @author SparkTasks Enterprise UI Team
 */

import React, { useState, useEffect, useRef, forwardRef } from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// ===== TYPE DEFINITIONS =====

export interface TOCHeading {
  /** Unique identifier for the heading */
  id: string;
  /** Text content of the heading */
  text: string;
  /** Heading level (1-6) */
  level: number;
  /** Optional anchor element */
  element?: HTMLElement | null;
  /** Child headings for nested structure */
  children?: TOCHeading[];
}

export interface TableOfContentsProps
  extends React.HTMLAttributes<HTMLElement> {
  /** Array of headings to display */
  headings?: TOCHeading[];
  /** CSS selector for content container to extract headings from */
  contentSelector?: string;
  /** Maximum heading level to include (1-6) */
  maxLevel?: number;
  /** Minimum heading level to include (1-6) */
  minLevel?: number;
  /** Whether to show nested structure */
  nested?: boolean;
  /** Whether to make TOC sticky */
  sticky?: boolean;
  /** Whether to show scroll progress indicator */
  showProgress?: boolean;
  /** Whether to highlight current section */
  highlightCurrent?: boolean;
  /** Custom offset for scroll position calculations */
  scrollOffset?: number;
  /** Whether to use smooth scrolling */
  smoothScroll?: boolean;
  /** Size variant affecting text and spacing */
  size?: 'sm' | 'md' | 'lg';
  /** Visual variant */
  variant?: 'default' | 'minimal' | 'outlined' | 'card';
  /** Position when sticky */
  stickyPosition?: 'top' | 'right' | 'left';
  /** Custom title for the TOC */
  title?: string;
  /** Whether to show title */
  showTitle?: boolean;
  /** Whether TOC is collapsible */
  collapsible?: boolean;
  /** Initial collapsed state */
  defaultCollapsed?: boolean;
  /** Loading state */
  loading?: boolean;
  /** Empty state content */
  emptyContent?: React.ReactNode;
  /** Click handler for heading items */
  onHeadingClick?: (heading: TOCHeading) => void;
  /** Callback when current heading changes */
  onCurrentHeadingChange?: (heading: TOCHeading | null) => void;
}

export interface TOCItemProps extends React.HTMLAttributes<HTMLLIElement> {
  /** Heading data */
  heading: TOCHeading;
  /** Whether this item is currently active */
  active?: boolean;
  /** Nesting level for indentation */
  level?: number;
  /** Size variant */
  size?: 'sm' | 'md' | 'lg';
  /** Click handler */
  onItemClick?: ((heading: TOCHeading) => void) | undefined;
  /** Function to check if a heading is active */
  isHeadingActive?: (heading: TOCHeading) => boolean;
}

// ===== UTILITY FUNCTIONS =====

const extractHeadingsFromContent = (
  contentSelector: string,
  minLevel: number = 1,
  maxLevel: number = 6
): TOCHeading[] => {
  const container = document.querySelector(contentSelector);
  if (!container) return [];

  const headingSelectors = Array.from(
    { length: maxLevel - minLevel + 1 },
    (_, i) => `h${minLevel + i}`
  ).join(', ');

  const headingElements = container.querySelectorAll(
    headingSelectors
  ) as NodeListOf<HTMLHeadingElement>;

  const headings: TOCHeading[] = [];

  for (const [index, element] of headingElements.entries()) {
    const level = Number.parseInt(element.tagName.slice(1));
    const text = element.textContent?.trim() || '';
    let id = element.id;

    // Generate ID if not present
    if (!id) {
      id = `heading-${level}-${index}`;
      element.id = id;
    }

    headings.push({
      id,
      text,
      level,
      element,
    });
  }

  return headings;
};

const buildNestedStructure = (headings: TOCHeading[]): TOCHeading[] => {
  // If headings already have nested structure, use it as-is
  const hasExistingStructure = headings.some(
    heading => heading.children && heading.children.length > 0
  );
  if (hasExistingStructure) {
    return headings;
  }

  // Create deep copies to avoid mutating the original objects
  const clonedHeadings: TOCHeading[] = headings.map(heading => {
    const cloned = { ...heading };
    // Remove children property to start fresh
    delete cloned.children;
    return cloned;
  });

  const nested: TOCHeading[] = [];
  const stack: TOCHeading[] = [];

  for (const heading of clonedHeadings) {
    // Pop stack until we find a parent with lower level
    while (stack.length > 0 && stack.at(-1).level >= heading.level) {
      stack.pop();
    }

    const parent = stack.at(-1);

    if (parent) {
      if (!parent.children) parent.children = [];
      parent.children.push(heading);
    } else {
      nested.push(heading);
    }

    stack.push(heading);
  }

  return nested;
};

const getIndentLevel = (level: number, minLevel: number): number => {
  return Math.max(0, level - minLevel);
};

const getTOCClasses = (
  variant: 'default' | 'minimal' | 'outlined' | 'card',
  size: 'sm' | 'md' | 'lg',
  sticky: boolean
) => {
  const baseClasses = 'toc-container';

  const variantClasses = {
    default: combineTokens(
      DESIGN_TOKENS.theme.light.surface.subtle,
      DESIGN_TOKENS.theme.light.border.subtle,
      DESIGN_TOKENS.theme.light.radius.lg
    ),
    minimal: '',
    outlined: combineTokens(
      DESIGN_TOKENS.theme.light.border.subtle,
      DESIGN_TOKENS.theme.light.radius.lg
    ),
    card: combineTokens(
      DESIGN_TOKENS.recipe.card.base,
      DESIGN_TOKENS.recipe.card.elevated
    ),
  }[variant];

  const sizeClasses = {
    sm: combineTokens(
      DESIGN_TOKENS.typography.body.xs,
      DESIGN_TOKENS.spacing.sm
    ),
    md: combineTokens(
      DESIGN_TOKENS.typography.body.small,
      DESIGN_TOKENS.spacing.md
    ),
    lg: combineTokens(
      DESIGN_TOKENS.typography.body.medium,
      DESIGN_TOKENS.spacing.lg
    ),
  }[size];

  const stickyClasses = sticky
    ? combineTokens(
        'sticky top-4', // Need to check if there's a DESIGN_TOKEN for this
        'max-h-[calc(100vh-2rem)]', // Custom calculation
        'overflow-y-auto' // Basic overflow
      )
    : '';

  const scrollClasses = sticky
    ? combineTokens(
        DESIGN_TOKENS.scroll.thinScrollbar,
        DESIGN_TOKENS.scroll.smooth
      )
    : '';

  return combineTokens(
    baseClasses,
    variantClasses,
    sizeClasses,
    stickyClasses,
    scrollClasses
  );
};

const getTOCItemClasses = (
  level: number,
  active: boolean,
  size: 'sm' | 'md' | 'lg'
) => {
  const baseClasses =
    'toc-item flex items-start transition-all duration-200 ease-out relative group';

  const levelClasses = `ml-${Math.min(level * 3, 12)}`;

  const activeClasses = active
    ? 'text-primary-700 dark:text-primary-300 font-medium bg-primary-50 dark:bg-primary-900/30'
    : 'text-slate-600 dark:text-slate-400 hover:text-slate-900 dark:hover:text-slate-200';

  const sizeClasses = {
    sm: 'py-1 px-2 text-xs rounded-md',
    md: 'py-1.5 px-3 text-sm rounded-md',
    lg: 'py-2 px-3 text-base rounded-lg',
  }[size];

  const interactiveClasses =
    'cursor-pointer hover:bg-slate-100 dark:hover:bg-slate-800/60 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2 focus:ring-offset-slate-50 dark:focus:ring-offset-slate-800';

  return combineTokens(
    baseClasses,
    levelClasses,
    activeClasses,
    sizeClasses,
    interactiveClasses
  );
};

// ===== LOADING SKELETON =====

const TOCSkeleton: React.FC<{ size?: 'sm' | 'md' | 'lg' }> = ({
  size = 'md',
}) => {
  const itemHeight = {
    sm: 'h-5',
    md: 'h-6',
    lg: 'h-7',
  }[size];

  return (
    <div className={combineTokens('animate-pulse', 'space-y-2')}>
      {Array.from({ length: 5 }, (_, i) => (
        <div
          key={i}
          className={combineTokens(
            'rounded bg-slate-200 dark:bg-slate-700',
            itemHeight
          )}
          style={{ width: `${80 - (i % 3) * 20}%` }}
        />
      ))}
    </div>
  );
};

// ===== TOC ITEM COMPONENT =====

const TOCItem = forwardRef<HTMLLIElement, TOCItemProps>(
  (
    {
      heading,
      active = false,
      level = 0,
      size = 'md',
      onItemClick,
      isHeadingActive,
      className,
      ...props
    },
    ref
  ) => {
    const itemClasses = getTOCItemClasses(level, active, size);

    const handleClick = (event: React.MouseEvent) => {
      event.preventDefault();
      onItemClick?.(heading);
    };

    const handleKeyDown = (event: React.KeyboardEvent) => {
      if (event.key === 'Enter' || event.key === ' ') {
        event.preventDefault();
        onItemClick?.(heading);
      }
    };

    return (
      <li
        ref={ref}
        className={combineTokens(itemClasses, className)}
        {...props}
      >
        <div
          className={combineTokens(
            DESIGN_TOKENS.recipe.listItemContent.base,
            'cursor-pointer'
          )}
          onClick={handleClick}
          onKeyDown={handleKeyDown}
          tabIndex={0}
          role='button'
          aria-label={`Navigate to ${heading.text}`}
        >
          {/* Current indicator */}
          {active && (
            <div
              className={combineTokens(
                'absolute left-0 top-1/2 -translate-y-1/2 rounded-r-full bg-primary-500',
                DESIGN_TOKENS.sizing.avatar.xs
              )}
            />
          )}{' '}
          {/* Heading text */}
          <div className={combineTokens('truncate')} title={heading.text}>
            {heading.text}
          </div>
          {/* Nested children */}
          {heading.children && heading.children.length > 0 && (
            <ul
              className={combineTokens(
                DESIGN_TOKENS.spacing.workspace.subtitleMargin,
                DESIGN_TOKENS.spacing.stackTight
              )}
            >
              {heading.children.map(child => (
                <TOCItem
                  key={child.id}
                  heading={child}
                  active={isHeadingActive ? isHeadingActive(child) : false}
                  level={level + 1}
                  size={size}
                  onItemClick={onItemClick}
                  {...(isHeadingActive ? { isHeadingActive } : {})}
                />
              ))}
            </ul>
          )}
        </div>
      </li>
    );
  }
);

TOCItem.displayName = 'TOCItem';

// ===== MAIN COMPONENT =====

const TableOfContents = forwardRef<HTMLElement, TableOfContentsProps>(
  (
    {
      headings: propHeadings,
      contentSelector,
      maxLevel = 6,
      minLevel = 1,
      nested = true,
      sticky = false,
      showProgress = false,
      highlightCurrent = true,
      scrollOffset = 80,
      smoothScroll = true,
      size = 'md',
      variant = 'default',
      stickyPosition = 'top',
      title = 'Table of Contents',
      showTitle = true,
      collapsible = false,
      defaultCollapsed = false,
      loading = false,
      emptyContent = 'No headings found',
      onHeadingClick,
      onCurrentHeadingChange,
      className,
      children,
      ...props
    },
    ref
  ) => {
    const [headings, setHeadings] = useState<TOCHeading[]>([]);
    const [currentHeading, setCurrentHeading] = useState<TOCHeading | null>(
      null
    );
    const [collapsed, setCollapsed] = useState(defaultCollapsed);
    const observerRef = useRef<IntersectionObserver | null>(null);
    const headingElementsRef = useRef<Map<string, HTMLElement>>(new Map());

    // Find heading by ID in nested structure
    const findHeadingById = React.useCallback(
      (headingList: TOCHeading[], id: string): TOCHeading | null => {
        for (const heading of headingList) {
          if (heading.id === id) return heading;
          if (heading.children) {
            const found = findHeadingById(heading.children, id);
            if (found) return found;
          }
        }
        return null;
      },
      []
    );

    // Extract headings from content or use prop headings
    useEffect(() => {
      let flatHeadings: TOCHeading[] = [];

      if (propHeadings) {
        flatHeadings = propHeadings;
      } else if (contentSelector) {
        flatHeadings = extractHeadingsFromContent(
          contentSelector,
          minLevel,
          maxLevel
        );

        // Store heading elements for intersection observer
        for (const heading of flatHeadings) {
          if (heading.element) {
            headingElementsRef.current.set(heading.id, heading.element);
          }
        }
      }

      // Build structure based on nested prop
      setHeadings(nested ? buildNestedStructure(flatHeadings) : flatHeadings);
    }, [propHeadings, contentSelector, minLevel, maxLevel, nested]);

    // Set up intersection observer for current heading tracking
    useEffect(() => {
      if (!highlightCurrent || headings.length === 0) return;

      const elements = [...headingElementsRef.current.values()];
      if (elements.length === 0) return;

      observerRef.current = new IntersectionObserver(
        entries => {
          const visibleEntries = entries.filter(entry => entry.isIntersecting);

          if (visibleEntries.length > 0) {
            // Find the heading closest to the top
            const sortedEntries = visibleEntries.sort(
              (a, b) => a.boundingClientRect.top - b.boundingClientRect.top
            );

            const activeElement = sortedEntries[0].target as HTMLElement;
            const activeHeading = [
              ...headingElementsRef.current.entries(),
            ].find(
              ([headingId]) =>
                headingElementsRef.current.get(headingId) === activeElement
            )?.[0];

            if (activeHeading) {
              const heading = findHeadingById(headings, activeHeading);
              if (heading && heading !== currentHeading) {
                setCurrentHeading(heading);
                onCurrentHeadingChange?.(heading);
              }
            }
          }
        },
        {
          rootMargin: `-${scrollOffset}px 0px -80% 0px`,
          threshold: 0,
        }
      );

      for (const element of elements) {
        observerRef.current?.observe(element);
      }

      return () => {
        observerRef.current?.disconnect();
      };
    }, [
      headings,
      highlightCurrent,
      scrollOffset,
      currentHeading,
      onCurrentHeadingChange,
      findHeadingById,
    ]);

    // Check if heading is active (current or ancestor of current)
    const isHeadingActive = (heading: TOCHeading): boolean => {
      if (!currentHeading) return false;

      if (heading.id === currentHeading.id) return true;

      // Check if this heading is an ancestor of current heading
      const isAncestor = (parent: TOCHeading, target: TOCHeading): boolean => {
        if (!parent.children) return false;

        for (const child of parent.children) {
          if (child.id === target.id) return true;
          if (isAncestor(child, target)) return true;
        }

        return false;
      };

      return isAncestor(heading, currentHeading);
    };

    // Handle heading click
    const handleHeadingClick = (heading: TOCHeading) => {
      onHeadingClick?.(heading);

      const element = heading.element || document.getElementById(heading.id);
      if (element) {
        const offsetTop = element.offsetTop - scrollOffset;

        if (smoothScroll) {
          window.scrollTo({
            top: offsetTop,
            behavior: 'smooth',
          });
        } else {
          window.scrollTo(0, offsetTop);
        }

        // Update URL hash
        if (heading.id) {
          globalThis.history.pushState(null, '', `#${heading.id}`);
        }
      } else {
        // Fallback for when element is not found (e.g., in tests)
        // Still scroll to simulate the behavior
        if (smoothScroll) {
          window.scrollTo({
            top: 0,
            behavior: 'smooth',
          });
        } else {
          window.scrollTo(0, 0);
        }

        // Update URL hash
        if (heading.id) {
          globalThis.history.pushState(null, '', `#${heading.id}`);
        }
      }
    };

    // Render headings - for nested structure, only render top-level items
    // TOCItem will handle recursive rendering of children
    const renderHeadings = (headingList: TOCHeading[]): React.ReactNode => {
      if (nested) {
        // Only render top-level headings - TOCItem handles children recursively
        return headingList.map(heading => (
          <TOCItem
            key={heading.id}
            heading={heading}
            active={isHeadingActive(heading)}
            level={0} // Always start at level 0 for top-level items in nested mode
            size={size}
            onItemClick={handleHeadingClick}
            isHeadingActive={isHeadingActive}
          />
        ));
      } else {
        // Render all headings flat with proper indentation
        return headingList.map(heading => (
          <TOCItem
            key={heading.id}
            heading={heading}
            active={isHeadingActive(heading)}
            level={getIndentLevel(heading.level, minLevel)}
            size={size}
            onItemClick={handleHeadingClick}
          />
        ));
      }
    };

    const tocClasses = getTOCClasses(variant, size, sticky, stickyPosition);

    // Handle loading state
    if (loading) {
      return (
        <nav
          ref={ref}
          className={combineTokens(tocClasses, className)}
          aria-label='Table of contents'
          {...props}
        >
          {showTitle && (
            <div className={DESIGN_TOKENS.spacing.workspace.sectionMargin}>
              <h3 className={DESIGN_TOKENS.typography.heading.h5}>{title}</h3>
            </div>
          )}
          <TOCSkeleton size={size} />
        </nav>
      );
    }

    // Handle empty state
    if (headings.length === 0) {
      return (
        <nav
          ref={ref}
          className={combineTokens(tocClasses, className)}
          aria-label='Table of contents'
          {...props}
        >
          {showTitle && (
            <div className={DESIGN_TOKENS.spacing.workspace.sectionMargin}>
              <h3 className={DESIGN_TOKENS.typography.heading.h5}>{title}</h3>
            </div>
          )}
          <div
            className={combineTokens(
              'py-6',
              'text-center',
              'text-slate-500',
              'dark:text-slate-400'
            )}
          >
            {emptyContent}
          </div>
        </nav>
      );
    }

    return (
      <nav
        ref={ref}
        className={combineTokens(tocClasses, className)}
        aria-label='Table of contents'
        {...props}
      >
        {/* Title with optional collapse toggle */}
        {showTitle && (
          <div
            className={combineTokens(
              DESIGN_TOKENS.layout.flexBetween,
              DESIGN_TOKENS.spacing.workspace.headerMargin
            )}
          >
            <h3 className={DESIGN_TOKENS.typography.heading.h5}>{title}</h3>
            {collapsible && (
              <button
                type='button'
                onClick={() => setCollapsed(!collapsed)}
                className={combineTokens(
                  'rounded',
                  'p-1',
                  'transition-colors',
                  'hover:bg-slate-100',
                  'dark:hover:bg-slate-700'
                )}
                aria-expanded={!collapsed}
                aria-label={
                  collapsed
                    ? 'Expand table of contents'
                    : 'Collapse table of contents'
                }
              >
                <svg
                  className={combineTokens(
                    DESIGN_TOKENS.sizing.avatar.xs,
                    'transition-transform duration-200',
                    collapsed ? 'rotate-0' : 'rotate-90'
                  )}
                  fill='none'
                  stroke='currentColor'
                  viewBox='0 0 24 24'
                >
                  <path
                    strokeLinecap='round'
                    strokeLinejoin='round'
                    strokeWidth={2}
                    d='M9 5l7 7-7 7'
                  />
                </svg>
              </button>
            )}
          </div>
        )}

        {/* Progress indicator */}
        {showProgress && !collapsed && (
          <div className={DESIGN_TOKENS.spacing.workspace.sectionMargin}>
            <div
              className={combineTokens(
                'overflow-hidden rounded-full bg-slate-200 dark:bg-slate-700',
                DESIGN_TOKENS.sizing.avatar.xs
              )}
            >
              <div
                className={combineTokens(
                  'h-full',
                  'bg-primary-500',
                  'transition-all',
                  'duration-300',
                  'ease-out'
                )}
                style={{
                  width: `${
                    currentHeading
                      ? ((headings.findIndex(h => h.id === currentHeading.id) +
                          1) /
                          headings.length) *
                        100
                      : 0
                  }%`,
                }}
              />
            </div>
          </div>
        )}

        {/* Headings list */}
        {!collapsed && (
          <ul className={DESIGN_TOKENS.spacing.stackTight}>
            {renderHeadings(headings)}
          </ul>
        )}

        {children}
      </nav>
    );
  }
);

TableOfContents.displayName = 'TableOfContents';

// ===== EXPORTS =====

export default TableOfContents;
export { TOCItem };

import React, { forwardRef, useMemo, useCallback } from 'react';

import { DESIGN_TOKENS, combineTokens } from '@/design/tokens';

// ===== TYPE DEFINITIONS =====

export type TagVariant =
  | 'default'
  | 'success'
  | 'warning'
  | 'danger'
  | 'info'
  | 'outline'
  | 'solid'
  | 'ghost'
  | 'accent';

export type TagSize = 'xs' | 'sm' | 'md' | 'lg';

export type TagStatus = 'success' | 'warning' | 'danger' | 'info';

export interface TagProps extends React.HTMLAttributes<HTMLSpanElement> {
  variant?: TagVariant;
  size?: TagSize;
  children: React.ReactNode;
  icon?: React.ReactNode;
  ariaLabel?: string;
  loading?: boolean;
  disabled?: boolean;
  status?: TagStatus;
  interactive?: boolean;
  selected?: boolean;
  onSelect?: () => void;
  removable?: boolean;
  onRemove?: () => void;
  counter?: number;
  maxCounter?: number;
  pulse?: boolean;
  truncate?: boolean;
}

// ===== MEMOIZED STATUS ICONS =====

const StatusIcon: React.FC<{ status: TagStatus; size: TagSize }> = React.memo(
  ({ status, size }) => {
    const iconSize = useMemo(() => {
      switch (size) {
        case 'xs': {
          return 'size-2.5';
        }
        case 'sm': {
          return 'size-3';
        }
        case 'md': {
          return 'size-3.5';
        }
        case 'lg': {
          return 'size-4';
        }
        default: {
          return 'size-3';
        }
      }
    }, [size]);

    const iconProps = {
      className: iconSize,
      fill: 'currentColor',
      viewBox: '0 0 20 20',
      'aria-hidden': 'true' as const,
      focusable: 'false' as const,
    };

    switch (status) {
      case 'success': {
        return (
          <svg {...iconProps}>
            <path
              fillRule='evenodd'
              d='M16.707 5.293a1 1 0 010 1.414l-8 8a1 1 0 01-1.414 0l-4-4a1 1 0 011.414-1.414L7 12.586l7.293-7.293a1 1 0 011.414 0z'
              clipRule='evenodd'
            />
          </svg>
        );
      }
      case 'warning': {
        return (
          <svg {...iconProps}>
            <path
              fillRule='evenodd'
              d='M8.257 3.099c.765-1.36 2.722-1.36 3.486 0l5.58 9.92c.75 1.334-.213 2.98-1.742 2.98H4.42c-1.53 0-2.493-1.646-1.743-2.98l5.58-9.92zM11 13a1 1 0 11-2 0 1 1 0 012 0zm-1-8a1 1 0 00-1 1v3a1 1 0 002 0V6a1 1 0 00-1-1z'
              clipRule='evenodd'
            />
          </svg>
        );
      }
      case 'danger': {
        return (
          <svg {...iconProps}>
            <path
              fillRule='evenodd'
              d='M18 10a8 8 0 11-16 0 8 8 0 0116 0zM8.707 7.293a1 1 0 00-1.414 1.414L8.586 10l-1.293 1.293a1 1 0 101.414 1.414L10 11.414l1.293 1.293a1 1 0 001.414-1.414L11.414 10l1.293-1.293a1 1 0 00-1.414-1.414L10 8.586 8.707 7.293z'
              clipRule='evenodd'
            />
          </svg>
        );
      }
      case 'info': {
        return (
          <svg {...iconProps}>
            <path
              fillRule='evenodd'
              d='M18 10a8 8 0 11-16 0 8 8 0 0116 0zm-7-4a1 1 0 11-2 0 1 1 0 012 0zM9 9a1 1 0 000 2v3a1 1 0 001 1h1a1 1 0 100-2v-3a1 1 0 00-1-1H9z'
              clipRule='evenodd'
            />
          </svg>
        );
      }
      default: {
        return null;
      }
    }
  }
);

StatusIcon.displayName = 'TagStatusIcon';

// ===== LOADING SKELETON =====
const TagSkeleton: React.FC<{ size?: TagSize }> = React.memo(
  ({ size = 'md' }) => (
    <span
      className={combineTokens(
        DESIGN_TOKENS.recipe.badge,
        DESIGN_TOKENS.recipe.badgeDefault,
        DESIGN_TOKENS.sizing.badge[size === 'xs' ? 'sm' : size],
        DESIGN_TOKENS.recipe.skeleton?.button ??
          'animate-pulse bg-slate-200 text-transparent dark:bg-slate-700'
      )}
      aria-hidden='true'
      data-variant='skeleton'
      data-size={size}
      data-testid='tag-skeleton'
    >
      <span className={combineTokens('inline-block', 'w-8')}>&nbsp;</span>
    </span>
  )
);

TagSkeleton.displayName = 'TagSkeleton';

// ===== MAIN TAG COMPONENT =====
const TagComponent = forwardRef<HTMLSpanElement, TagProps>(function Tag(
  {
    children,
    variant = 'default',
    size = 'md',
    icon,
    className,
    ariaLabel,
    loading = false,
    disabled = false,
    status,
    interactive = false,
    selected = false,
    onSelect,
    removable = false,
    onRemove,
    counter,
    maxCounter = 99,
    pulse = false,
    truncate = false,
    onClick,
    onKeyDown,
    ...props
  },
  ref
) {
  // Memoized variant mappings for O(1) lookups
  const variantClasses = useMemo(
    () => ({
      default: DESIGN_TOKENS.recipe.badgeDefault,
      success: DESIGN_TOKENS.recipe.badgeSuccess,
      warning: DESIGN_TOKENS.recipe.badgeWarning,
      danger: DESIGN_TOKENS.recipe.badgeDanger,
      info: DESIGN_TOKENS.recipe.badgeInfo,
      outline: DESIGN_TOKENS.recipe.badgeOutline,
      solid: DESIGN_TOKENS.recipe.badgeDefault,
      ghost: DESIGN_TOKENS.recipe.badgeOutline,
      accent: DESIGN_TOKENS.recipe.badgeInfo,
    }),
    []
  );

  // Memoized size classes
  const sizeClasses = useMemo(
    () => ({
      xs: DESIGN_TOKENS.sizing.badge.sm,
      sm: DESIGN_TOKENS.sizing.badge.sm,
      md: DESIGN_TOKENS.sizing.badge.md,
      lg: DESIGN_TOKENS.sizing.badge.lg,
    }),
    []
  );

  // Memoized status labels for accessibility
  const statusLabels = useMemo(
    () => ({
      success: 'Success',
      warning: 'Warning',
      danger: 'Error',
      info: 'Information',
    }),
    []
  );

  // Stable remove handler
  const handleRemove = useCallback(
    (event: React.MouseEvent | React.KeyboardEvent) => {
      event.stopPropagation();
      onRemove?.();
    },
    [onRemove]
  );

  // Stable select handler
  const handleSelect = useCallback(
    (event: React.MouseEvent | React.KeyboardEvent) => {
      if (disabled) return;
      onSelect?.();
      onClick?.(event as React.MouseEvent<HTMLSpanElement>);
    },
    [disabled, onSelect, onClick]
  );

  // Stable keyboard handler
  const handleKeyDown = useCallback(
    (event: React.KeyboardEvent<HTMLSpanElement>) => {
      if (disabled) return;

      if (interactive && (event.key === 'Enter' || event.key === ' ')) {
        event.preventDefault();
        handleSelect(event);
      }

      if (removable && (event.key === 'Delete' || event.key === 'Backspace')) {
        event.preventDefault();
        handleRemove(event);
      }

      onKeyDown?.(event);
    },
    [disabled, interactive, removable, handleSelect, handleRemove, onKeyDown]
  );

  const variantClass = variantClasses[variant];
  const sizeClass = sizeClasses[size];
  const hasStatus = Boolean(status);
  const hasRemove = removable && onRemove;
  const isSelectable = Boolean(onSelect) || interactive;
  const isInteractive = isSelectable || hasRemove;
  const hasCounter = typeof counter === 'number' && counter > 0;
  const displayCounter = hasCounter
    ? counter! > maxCounter
      ? `${maxCounter}+`
      : counter!.toString()
    : '';

  // Compute final className with memoized classes
  const tagClassName = useMemo(
    () =>
      combineTokens(
        DESIGN_TOKENS.recipe.badge,
        variantClass,
        sizeClass,
        selected && 'ring-2 ring-blue-500 ring-offset-1',
        disabled && 'opacity-50 cursor-not-allowed',
        isInteractive &&
          !disabled &&
          'cursor-pointer hover:opacity-80 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500',
        pulse && 'animate-pulse',
        truncate && 'max-w-xs truncate',
        className
      ),
    [
      variantClass,
      sizeClass,
      selected,
      disabled,
      isInteractive,
      pulse,
      truncate,
      className,
    ]
  );

  // Early return for loading state
  if (loading) {
    return <TagSkeleton size={size} />;
  }

  return (
    <span
      ref={ref}
      className={tagClassName}
      role={isSelectable ? 'button' : hasStatus ? 'status' : undefined}
      aria-live={hasStatus ? 'polite' : undefined}
      aria-busy={loading ? 'true' : undefined}
      aria-label={ariaLabel}
      aria-pressed={isSelectable ? selected : undefined}
      aria-disabled={disabled ? 'true' : undefined}
      tabIndex={isInteractive && !disabled ? 0 : undefined}
      data-variant={variant}
      data-size={size}
      data-status={status || undefined}
      data-interactive={isInteractive ? 'true' : 'false'}
      data-selected={selected ? 'true' : 'false'}
      data-disabled={disabled ? 'true' : 'false'}
      data-removable={hasRemove ? 'true' : 'false'}
      data-testid='tag'
      onClick={isSelectable && !disabled ? handleSelect : undefined}
      onKeyDown={isInteractive && !disabled ? handleKeyDown : undefined}
      title={truncate && typeof children === 'string' ? children : undefined}
      {...props}
    >
      {hasStatus && (
        <>
          <span
            className={combineTokens('mr-1', 'inline-flex', 'items-center')}
            aria-hidden='true'
          >
            <StatusIcon status={status!} size={size} />
          </span>
          {!ariaLabel && (
            <span className={combineTokens('sr-only')}>
              {statusLabels[status!]}
            </span>
          )}
        </>
      )}

      {icon && !hasStatus && (
        <span
          className={combineTokens('mr-1', 'inline-flex', 'items-center')}
          aria-hidden='true'
        >
          {icon}
        </span>
      )}

      <span
