/**
 * Convert ArrayBuffer to base64url string
 */
export function toB64u(bytes: ArrayBuffer): string {
  const uint8Array = new Uint8Array(bytes);
  let binary = '';
  for (const element of uint8Array) {
    binary += String.fromCharCode(element);
  }
  return btoa(binary)
    .replaceAll('+', '-')
    .replaceAll('/', '_')
    .replaceAll('=', '');
}

/**
 * Convert base64url string to ArrayBuffer
 */
export function fromB64u(s: string): ArrayBuffer {
  // Add padding to make length % 4 === 0
  const padLen = (4 - (s.length % 4)) % 4;
  const padded = s + '='.repeat(padLen);
  // Convert back to standard base64
  const base64 = padded.replaceAll('-', '+').replaceAll('_', '/');
  const binary = atob(base64);
  const bytes = new Uint8Array(binary.length);
  for (let i = 0; i < binary.length; i++) {
    bytes[i] = binary.charCodeAt(i);
  }
  return bytes.buffer;
}

/**
 * Ed25519 signing utilities for WebCrypto
 */

/**
 * Generate Ed25519 key pair
 */
export async function generateKeyPair(): Promise<{
  publicKey: CryptoKey;
  privateKey: CryptoKey;
}> {
  return await crypto.subtle.generateKey(
    {
      name: 'Ed25519',
      namedCurve: 'Ed25519',
    },
    true, // extractable
    ['sign', 'verify']
  );
}

/**
 * Export public key as base64url
 */
export async function exportPublicKeyB64u(
  publicKey: CryptoKey
): Promise<string> {
  const spki = await crypto.subtle.exportKey('spki', publicKey);
  return btoa(String.fromCharCode(...new Uint8Array(spki)))
    .replaceAll('+', '-')
    .replaceAll('/', '_')
    .replaceAll('=', '');
}

/**
 * Sign data with Ed25519 private key
 */
export async function sign(
  privateKey: CryptoKey,
  data: ArrayBuffer
): Promise<string> {
  const signature = await crypto.subtle.sign('Ed25519', privateKey, data);
  return btoa(String.fromCharCode(...new Uint8Array(signature)))
    .replaceAll('+', '-')
    .replaceAll('/', '_')
    .replaceAll('=', '');
}

/**
 * Verify Ed25519 signature
 */
export async function verify(
  publicKey: CryptoKey,
  data: ArrayBuffer,
  sigB64u: string
): Promise<boolean> {
  try {
    // Convert base64url to bytes
    const sigBase64 = sigB64u.replaceAll('-', '+').replaceAll('_', '/');
    const padding = '='.repeat((4 - (sigBase64.length % 4)) % 4);
    const sigBytes = Uint8Array.from(atob(sigBase64 + padding), c =>
      c.charCodeAt(0)
    );

    return await crypto.subtle.verify('Ed25519', publicKey, sigBytes, data);
  } catch {
    return false;
  }
}

/**
 * Import public key from base64url SPKI
 */
export async function importPublicKeyB64u(keyB64u: string): Promise<CryptoKey> {
  const keyBase64 = keyB64u.replaceAll('-', '+').replaceAll('_', '/');
  const padding = '='.repeat((4 - (keyBase64.length % 4)) % 4);
  const keyBytes = Uint8Array.from(atob(keyBase64 + padding), c =>
    c.charCodeAt(0)
  );

  return await crypto.subtle.importKey(
    'spki',
    keyBytes,
    { name: 'Ed25519', namedCurve: 'Ed25519' },
    false,
    ['verify']
  );
}

import { loadEvents } from '../task/eventlog';
import { TaskEventSchema } from '../task/events';

import type { Sparkpack, SparkpackMeta } from './types';

// tiny hash, no deps
const fnv1a = (s: string) => {
  let h = 0x81_1c_9d_c5 >>> 0;
  for (let i = 0; i < s.length; i++) {
    h ^= s.charCodeAt(i);
    h = Math.imul(h, 0x01_00_01_93) >>> 0;
  }
  return ('00000000' + h.toString(16)).slice(-8);
};

export function exportSparkpack(): string {
  const events = loadEvents();

  // Validate all events with schema
  const validatedEvents = events.map(event => {
    try {
      return TaskEventSchema.parse(event);
    } catch (error) {
      throw new Error(`Invalid event found during export: ${error}`);
    }
  });

  const rawEvents = validatedEvents.map(e => JSON.stringify(e)).join('\n');
  const meta: SparkpackMeta = {
    version: 1,
    format: 'sparkpack/1+json',
    createdAt: new Date().toISOString(),
    eventsCount: validatedEvents.length,
    eventsHash: fnv1a(rawEvents),
  };

  const sparkpack: Sparkpack = {
    meta,
    events: validatedEvents,
  };

  return JSON.stringify(sparkpack);
}

import { useTaskStore } from '../../stores/taskStore';
import { appendEvent, snapshotEvents, restoreEvents } from '../task/eventlog';
import { TaskEventSchema } from '../task/events';

import { planMerge } from './merge';
import type {
  Sparkpack,
  ImportPlan,
  ImportReport,
  MergePlan,
  MergePolicy,
} from './types';

// tiny hash, no deps
const fnv1a = (s: string) => {
  let h = 0x81_1c_9d_c5 >>> 0;
  for (let i = 0; i < s.length; i++) {
    h ^= s.charCodeAt(i);
    h = Math.imul(h, 0x01_00_01_93) >>> 0;
  }
  return ('00000000' + h.toString(16)).slice(-8);
};

export function planImport(raw: string): ImportPlan {
  const plan: ImportPlan = {
    valid: [],
    invalid: [],
  };

  try {
    const sparkpack: Sparkpack = JSON.parse(raw);

    // Validate pack integrity
    if (
      !sparkpack.meta ||
      sparkpack.meta.version !== 1 ||
      sparkpack.meta.format !== 'sparkpack/1+json'
    ) {
      plan.invalid.push({
        index: -1,
        error: 'Unsupported pack version/format',
      });
      return plan;
    }

    if (!sparkpack.events || !Array.isArray(sparkpack.events)) {
      plan.invalid.push({
        index: -1,
        error: 'Invalid sparkpack format: missing or invalid events array',
      });
      return plan;
    }

    if (sparkpack.meta.eventsCount !== sparkpack.events.length) {
      plan.invalid.push({ index: -1, error: 'eventsCount mismatch' });
      return plan;
    }

    const rawEvents = sparkpack.events.map(e => JSON.stringify(e)).join('\n');
    if (sparkpack.meta.eventsHash !== fnv1a(rawEvents)) {
      plan.invalid.push({ index: -1, error: 'eventsHash mismatch' });
      return plan;
    }

    // Validate individual events
    for (const [index, event] of sparkpack.events.entries()) {
      try {
        const validatedEvent = TaskEventSchema.parse(event);
        plan.valid.push(validatedEvent);
      } catch (error) {
        plan.invalid.push({
          index,
          error:
            error instanceof Error ? error.message : 'Unknown validation error',
        });
      }
    }
  } catch (error) {
    plan.invalid.push({
      index: -1,
      error: error instanceof Error ? error.message : 'Failed to parse JSON',
    });
  }

  return plan;
}

export function applyImport(
  plan: ImportPlan,
  opts?: { dryRun?: boolean }
): ImportReport {
  const report: ImportReport = {
    applied: 0,
    skipped: plan.invalid.length,
    errors: plan.invalid.map(inv => `Event ${inv.index}: ${inv.error}`),
  };

  if (opts?.dryRun) {
    report.applied = plan.valid.length;
    return report;
  }

  // Take snapshot for rollback
  const snapshot = snapshotEvents();

  try {
    // Apply all valid events
    for (const event of plan.valid) {
      appendEvent(event);
      report.applied++;
    }

    // Hydrate the store with new state
    useTaskStore.getState().hydrate();
  } catch (error) {
    // Restore snapshot on any error
    restoreEvents(snapshot);
    report.errors.push(
      `Import failed: ${error instanceof Error ? error.message : 'Unknown error'}`
    );
    report.applied = 0;
  }

  return report;
}

export function planMergeImport(
  raw: string,
  policy: MergePolicy = 'remapIds'
): MergePlan | { error: string } {
  try {
    // First, plan the basic import
    const importPlan = planImport(raw);
    if (importPlan.invalid.length > 0) {
      return {
        error: `Invalid events in sparkpack: ${importPlan.invalid.map(i => i.error).join(', ')}`,
      };
    }

    // Get current tasks for conflict detection
    const currentTasks = Object.values(useTaskStore.getState().byId);

    // Plan the merge
    const mergePlan = planMerge(currentTasks, importPlan.valid, policy);
    return mergePlan;
  } catch (error) {
    return { error: error instanceof Error ? error.message : 'Unknown error' };
  }
}

export function applyMerge(
  plan: MergePlan,
  opts?: { dryRun?: boolean }
): ImportReport {
  const report: ImportReport = {
    applied: 0,
    skipped: 0,
    errors: [],
  };

  if (opts?.dryRun) {
    report.applied = plan.applyEvents.length;
    return report;
  }

  // Take snapshot for rollback
  const snapshot = snapshotEvents();

  try {
    // Apply all planned events
    for (const event of plan.applyEvents) {
      appendEvent(event);
      report.applied++;
    }

    // Hydrate the store with new state
    useTaskStore.getState().hydrate();
  } catch (error) {
    // Restore snapshot on any error
    restoreEvents(snapshot);
    report.errors.push(
      `Merge failed: ${error instanceof Error ? error.message : 'Unknown error'}`
    );
    report.applied = 0;
  }

  return report;
}

/**
 * Sparkpack manifest with Ed25519 signing
 */

import {
  exportPublicKeyB64u,
  sign,
  verify,
  importPublicKeyB64u,
} from '../../crypto/ed25519';

import { type SparkpackMeta, type Sparkpack } from './types';

export interface Manifest {
  v: 1;
  createdAt: string;
  author: {
    pubkey: string; // base64url SPKI
  };
  content: {
    meta: SparkpackMeta;
    eventsHash: string; // SHA-256 of JSONL bytes
    bytes: number;
  };
  sig: string; // Ed25519 signature over canonical JSON
}

/**
 * Canonicalize object for signing (stable key order)
 */
function canonicalize(obj: any): string {
  if (obj === null || typeof obj !== 'object') {
    return JSON.stringify(obj);
  }

  if (Array.isArray(obj)) {
    return '[' + obj.map(canonicalize).join(',') + ']';
  }

  const keys = Object.keys(obj).sort();
  const pairs = keys.map(key => `"${key}":${canonicalize(obj[key])}`);
  return '{' + pairs.join(',') + '}';
}

/**
 * Create signed manifest for sparkpack
 */
export async function createManifest(
  pack: Sparkpack,
  privateKey: CryptoKey,
  publicKey?: CryptoKey
): Promise<Manifest> {
  // Use provided public key or generate a new pair for testing
  let pubKey: CryptoKey;
  if (publicKey) {
    pubKey = publicKey;
  } else {
    // For testing - generate a new key pair
    const keyPair = await crypto.subtle.generateKey(
      { name: 'Ed25519', namedCurve: 'Ed25519' },
      true,
      ['sign', 'verify']
    );
    pubKey = keyPair.publicKey;
  }

  const pubkeyB64u = await exportPublicKeyB64u(pubKey);

  // Hash events JSONL
  const eventsJsonl = pack.events.map(e => JSON.stringify(e)).join('\n');
  const eventsBytes = new TextEncoder().encode(eventsJsonl);
  const hashBuffer = await crypto.subtle.digest('SHA-256', eventsBytes);
  const eventsHash = btoa(String.fromCharCode(...new Uint8Array(hashBuffer)))
    .replaceAll('+', '-')
    .replaceAll('/', '_')
    .replaceAll('=', '');

  // Build unsigned manifest
  const unsigned = {
    v: 1 as const,
    createdAt: new Date().toISOString(),
    author: { pubkey: pubkeyB64u },
    content: {
      meta: pack.meta,
      eventsHash,
      bytes: eventsBytes.length,
    },
  };

  // Sign canonical JSON
  const canonical = canonicalize(unsigned);
  const canonicalBytes = new TextEncoder().encode(canonical);
  const signature = await sign(privateKey, canonicalBytes.buffer);

  return { ...unsigned, sig: signature };
}

/**
 * Verify manifest signature and content hash
 */
export async function verifyManifest(
  manifest: Manifest,
  pack: Sparkpack
): Promise<boolean> {
  try {
    // Import public key
    const publicKey = await importPublicKeyB64u(manifest.author.pubkey);

    // Verify content hash
    const eventsJsonl = pack.events.map(e => JSON.stringify(e)).join('\n');
    const eventsBytes = new TextEncoder().encode(eventsJsonl);
    const hashBuffer = await crypto.subtle.digest('SHA-256', eventsBytes);
    const expectedHash = btoa(
      String.fromCharCode(...new Uint8Array(hashBuffer))
    )
      .replaceAll('+', '-')
      .replaceAll('/', '_')
      .replaceAll('=', '');

    if (manifest.content.eventsHash !== expectedHash) {
      return false;
    }

    if (manifest.content.bytes !== eventsBytes.length) {
      return false;
    }

    // Verify signature
    const unsigned = {
      v: manifest.v,
      createdAt: manifest.createdAt,
      author: manifest.author,
      content: manifest.content,
    };

    const canonical = canonicalize(unsigned);
    const canonicalBytes = new TextEncoder().encode(canonical);

    return await verify(publicKey, canonicalBytes.buffer, manifest.sig);
  } catch {
    return false;
  }
}

import { type Task } from '../task/schema';
import { compareTasks } from '../task/sort';

import { type Query, type Page, type Result } from './types';

export function runQuery(
  tasks: Task[],
  q: Query,
  page: Page = { offset: 0, limit: 50 },
  opts: { now?: Date } = {}
): Result<Task> {
  const now = opts.now ?? new Date();
  let filtered = [...tasks];

  // By default, hide ARCHIVED unless explicitly requested in q.status
  if (!q.status || q.status.length === 0) {
    filtered = filtered.filter(t => t.status !== 'ARCHIVED');
  }

  const qTagsLower = q.tags?.map(t => t.toLowerCase());

  // Text filter: case-insensitive substring match on title and tags
  if (q.text && q.text.trim()) {
    const searchText = q.text.toLowerCase();
    filtered = filtered.filter(
      task =>
        task.title.toLowerCase().includes(searchText) ||
        task.tags.some(tag => tag.toLowerCase().includes(searchText))
    );
  }

  // Tags filter: task must have ALL provided tags
  if (qTagsLower && qTagsLower.length > 0) {
    filtered = filtered.filter(task =>
      qTagsLower!.every(reqTag =>
        task.tags.map(t => t.toLowerCase()).includes(reqTag)
      )
    );
  }

  // Status filter: inclusive
  if (q.status && q.status.length > 0) {
    filtered = filtered.filter(task =>
      q.status!.includes(task.status as 'TODAY' | 'LATER' | 'DONE' | 'ARCHIVED')
    );
  }

  // Priority filter: inclusive
  if (q.priority && q.priority.length > 0) {
    filtered = filtered.filter(task => q.priority!.includes(task.priority));
  }

  // Due date range filter (inclusive)
  if (q.dueFrom || q.dueTo) {
    filtered = filtered.filter(task => {
      if (!task.dueDate) return false;
      const dueDate = new Date(task.dueDate);

      if (q.dueFrom) {
        const fromDate = parseBoundedDate(q.dueFrom, 'from');
        if (dueDate < fromDate) return false;
      }

      if (q.dueTo) {
        const toDate = parseBoundedDate(q.dueTo, 'to');
        if (dueDate > toDate) return false;
      }

      return true;
    });
  }

  // Snooze filter
  if (q.snoozeActive !== undefined) {
    if (q.snoozeActive) {
      // Only tasks with active snooze (snoozeUntil > now)
      filtered = filtered.filter(
        task => task.snoozeUntil && new Date(task.snoozeUntil) > now
      );
    } else {
      // Only tasks without snooze or expired snooze
      filtered = filtered.filter(
        task => !task.snoozeUntil || new Date(task.snoozeUntil) <= now
      );
    }
  }

  // Created date range filter (inclusive)
  if (q.createdFrom || q.createdTo) {
    filtered = filtered.filter(task => {
      const createdDate = new Date(task.createdAt);

      if (q.createdFrom) {
        const fromDate = new Date(q.createdFrom);
        if (createdDate < fromDate) return false;
      }

      if (q.createdTo) {
        const toDate = new Date(q.createdTo);
        if (createdDate > toDate) return false;
      }

      return true;
    });
  }

  // Sort: use compareTasks for TODAY/LATER; for DONE sort by updatedAt desc
  filtered.sort((a, b) => {
    if (a.status === 'DONE' && b.status === 'DONE') {
      return new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime();
    }
    return compareTasks(a, b);
  });

  const total = filtered.length;

  // Apply pagination
  const safeOffset = Math.max(0, (page?.offset ?? 0) | 0);
  const safeLimit = Math.max(0, Math.min(1000, (page?.limit ?? 50) | 0));
  const items = filtered.slice(safeOffset, safeOffset + safeLimit);

  return {
    total,
    items,
  };
}

function parseBoundedDate(s: string, kind: 'from' | 'to'): Date {
  if (/^\d{4}-\d{2}-\d{2}$/.test(s)) {
    // Interpret day-only strings as whole-day bounds (UTC)
    return new Date(
      `${s}T${kind === 'from' ? '00:00:00.000Z' : '23:59:59.999Z'}`
    );
  }
  return new Date(s);
}

import type { TaskEvent } from '../task/events';
import type { Task } from '../task/schema';
import { compareTasks } from '../task/sort';

import type { SearchQuery, SearchResult } from './types';

export interface SearchIndex {
  build(tasks: Task[]): void;
  updateFromEvent(e: TaskEvent): void;
  search(q: SearchQuery): SearchResult;
}

interface IndexedTask {
  task: Task;
  tokens: {
    title: string[];
    tags: string[];
    notes: string[];
  };
}

// Tokenize text: lowercase, split on non-alphanum, min length 2
function tokenize(text: string): string[] {
  return text
    .toLowerCase()
    .split(/[^a-z0-9]+/)
    .filter(token => token.length >= 2);
}

// Handle quoted phrases: "exact phrase" becomes single token
function parseQuery(query: string): string[] {
  const tokens: string[] = [];
  const quotedRegex = /"([^"]+)"/g;
  let lastIndex = 0;
  let match;

  // Extract quoted phrases
  while ((match = quotedRegex.exec(query)) !== null) {
    // Add tokens before the quote
    const beforeQuote = query.slice(lastIndex, match.index);
    tokens.push(...tokenize(beforeQuote));

    // Add the quoted phrase as single token
    tokens.push(match[1].toLowerCase());

    lastIndex = quotedRegex.lastIndex;
  }

  // Add remaining tokens after last quote
  const remaining = query.slice(lastIndex);
  tokens.push(...tokenize(remaining));

  return tokens.filter(token => token.length >= 2);
}

class InMemorySearchIndex implements SearchIndex {
  private indexedTasks: Map<string, IndexedTask> = new Map();

  build(tasks: Task[]): void {
    this.indexedTasks.clear();
    for (const task of tasks) {
      this.indexTask(task);
    }
  }

  private indexTask(task: Task): void {
    const indexed: IndexedTask = {
      task,
      tokens: {
        title: tokenize(task.title),
        tags: task.tags.flatMap(tag => tokenize(tag)),
        notes: task.notes ? tokenize(task.notes) : [],
      },
    };
    this.indexedTasks.set(task.id, indexed);
  }

  updateFromEvent(e: TaskEvent): void {
    const taskId = e.payload.id;

    switch (e.type) {
      case 'TASK_CREATED': {
        const newTask: Task = {
          id: taskId,
          title: e.payload.title,
          status: e.payload.status,
          priority: e.payload.priority,
          tags: e.payload.tags,
          dueDate: e.payload.dueDate,
          notes: e.payload.notes,
          createdAt: e.timestamp,
          updatedAt: e.timestamp,
        };
        this.indexTask(newTask);
        break;
      }

      case 'TASK_UPDATED': {
        const existing = this.indexedTasks.get(taskId);
        if (existing) {
          const updatedTask: Task = {
            ...existing.task,
            updatedAt: e.timestamp,
          };

          // Apply only defined changes
          if (e.payload.changes.title !== undefined) {
            updatedTask.title = e.payload.changes.title;
          }
          if (e.payload.changes.dueDate !== undefined) {
            updatedTask.dueDate = e.payload.changes.dueDate;
          }
          if (e.payload.changes.tags !== undefined) {
            updatedTask.tags = e.payload.changes.tags;
          }
          if (e.payload.changes.notes !== undefined) {
            updatedTask.notes = e.payload.changes.notes;
          }

          this.indexTask(updatedTask);
        }
        break;
      }

      case 'TASK_MOVED': {
        const existing = this.indexedTasks.get(taskId);
        if (existing) {
          const updatedTask: Task = {
            ...existing.task,
            status: e.payload.toStatus,
            updatedAt: e.timestamp,
          };
          this.indexTask(updatedTask);
        }
        break;
      }

      case 'TASK_COMPLETED': {
        const existing = this.indexedTasks.get(taskId);
        if (existing) {
          const updatedTask: Task = {
            ...existing.task,
            status: 'DONE',
            updatedAt: e.timestamp,
          };
          this.indexTask(updatedTask);
        }
        break;
      }

      case 'TASK_SNOOZED': {
        const existing = this.indexedTasks.get(taskId);
        if (existing) {
          const updatedTask: Task = {
            ...existing.task,
            snoozeUntil: e.payload.snoozeUntil,
            updatedAt: e.timestamp,
          };
          this.indexTask(updatedTask);
        }
        break;
      }
    }
  }

  search(query: SearchQuery): SearchResult {
    const { q = '', tags, status, priority, limit = 20, offset = 0 } = query;

    // Parse search query tokens - filter out short queries unless using filters
    const queryTokens = q.trim() ? parseQuery(q) : [];
    const hasTextQuery = queryTokens.some(token => token.length >= 2);
    const hasFilters =
      (tags && tags.length > 0) ||
      (status && status.length > 0) ||
      (priority && priority.length > 0);

    // If we have a text query but no valid tokens (too short), return empty unless we have filters
    if (q.trim() && !hasTextQuery && !hasFilters) {
      return {
        total: 0,
        items: [],
        facets: { tags: {}, status: {}, priority: {} },
      };
    }

    // Score and filter tasks
    const scoredTasks: Array<{ task: Task; score: number }> = [];

    for (const indexed of this.indexedTasks.values()) {
      const { task } = indexed;

      // Apply filters
      if (tags && tags.length > 0) {
        const hasAllTags = tags.every(tag =>
          task.tags.some(taskTag =>
            taskTag.toLowerCase().includes(tag.toLowerCase())
          )
        );
        if (!hasAllTags) continue;
      }

      if (status && status.length > 0 && !status.includes(task.status)) {
        continue;
      }

      if (
        priority &&
        priority.length > 0 &&
        !priority.includes(task.priority)
      ) {
        continue;
      }

      // Calculate score for text search
      let score = 0;

      if (hasTextQuery) {
        // Check if all query tokens match (AND behavior)
        const allTokensMatch = queryTokens.every(queryToken => {
          let tokenMatched = false;

          // Check if this is a phrase (contains spaces) vs single token
          const isPhrase = queryToken.includes(' ');

          if (isPhrase) {
            // For phrases, check exact match in original text
            const phraseQuery = queryToken.toLowerCase();
            if (task.title.toLowerCase().includes(phraseQuery)) {
              score += 3;
              tokenMatched = true;
            }
            if (
              task.tags.some(tag => tag.toLowerCase().includes(phraseQuery))
            ) {
              score += 2;
              tokenMatched = true;
            }
            if (task.notes?.toLowerCase().includes(phraseQuery)) {
              score += 1;
              tokenMatched = true;
            }
          } else {
            // For single tokens, use tokenized matching
            // Title boost x3
            if (
              indexed.tokens.title.some(token => token.includes(queryToken))
            ) {
              score += 3;
              tokenMatched = true;
            }

            // Tags boost x2
            if (indexed.tokens.tags.some(token => token.includes(queryToken))) {
              score += 2;
              tokenMatched = true;
            }

            // Notes boost x1
            if (
              indexed.tokens.notes.some(token => token.includes(queryToken))
            ) {
              score += 1;
              tokenMatched = true;
            }
          }

          return tokenMatched;
        });

        if (!allTokensMatch) continue;
      }

      scoredTasks.push({ task, score });
    }

    // Sort by score (desc), then compareTasks, then by id for stability
    scoredTasks.sort((a, b) => {
      if (a.score !== b.score) return b.score - a.score;
      const taskComparison = compareTasks(a.task, b.task);
      if (taskComparison !== 0) return taskComparison;
      return a.task.id.localeCompare(b.task.id);
    });

    // Calculate facets from filtered results (before pagination)
    const facets = {
      tags: {} as Record<string, number>,
      status: {} as Record<string, number>,
      priority: {} as Record<string, number>,
    };

    for (const { task } of scoredTasks) {
      // Status facet
      facets.status[task.status] = (facets.status[task.status] || 0) + 1;

      // Priority facet
      facets.priority[task.priority] =
        (facets.priority[task.priority] || 0) + 1;

      // Tags facet
      for (const tag of task.tags) {
        facets.tags[tag] = (facets.tags[tag] || 0) + 1;
      }
    }

    // Apply pagination with bounds checking
    const safeOffset = Math.max(0, offset);
    const safeLimit = Math.max(1, Math.min(1000, limit)); // Reasonable upper bound
    const paginatedTasks = scoredTasks
      .slice(safeOffset, safeOffset + safeLimit)
      .map(({ task }) => task);

    return {
      total: scoredTasks.length,
      items: paginatedTasks,
      facets,
    };
  }
}

