  if (Array.isArray(obj)) return '[' + obj.map(canonicalize).join(',') + ']';
  const keys = Object.keys(obj).sort();
  return '{' + keys.map(k => `"${k}":${canonicalize(obj[k])}`).join(',') + '}';
}

/**
 * Plan anchor discovery from locators (M2)
 */
export async function planAnchorDiscovery(
  ns: string,
  locators: AnchorLocator[],
  transport: any // RemoteTransport - mock for now
): Promise<DiscPlan> {
  const pulls: DiscPlan['pulls'] = [];

  for (const locator of locators) {
    pulls.push({
      orgId: locator.orgId,
      ref: locator.ref,
      ...(locator.since && { nextSince: locator.since }),
    });
  }

  return { pulls };
}

/**
 * Verify AnchorPack signature (reuse from Task 21)
 */
async function verifyDiscoveredPack(
  pack: AnchorPack,
  ns: string,
  storage: StorageDriver
): Promise<{ ok: boolean; reason?: string }> {
  try {
    // Basic structure validation
    if (!pack.v || pack.v !== 1)
      return { ok: false, reason: 'invalid_version' };
    if (!pack.issuerOrg || !pack.sig)
      return { ok: false, reason: 'missing_fields' };

    const { kid, pubB64u, sigB64u } = pack.sig;
    if (!sigB64u) return { ok: false, reason: 'missing_signature' };

    // For discovery, we mainly verify via federated trust anchors
    if (pubB64u) {
      const trustAnchors = await listTrustAnchors(ns);
      const trustedAnchor = trustAnchors.find(
        a =>
          a.orgId === pack.issuerOrg &&
          a.pubB64u === pubB64u &&
          a.status === 'ACTIVE'
      );

      if (!trustedAnchor) {
        return { ok: false, reason: 'untrusted_issuer' };
      }

      // Verify signature
      const { sig, ...unsigned } = pack;
      const canonical = canonicalize(unsigned);
      const messageBytes = new TextEncoder().encode(canonical);
      const publicKeyBytes = fromB64u(pubB64u);
      const signatureBytes = fromB64u(sigB64u);

      const publicKey = await crypto.subtle.importKey(
        'spki',
        publicKeyBytes,
        { name: 'Ed25519' },
        false,
        ['verify']
      );

      const valid = await crypto.subtle.verify(
        'Ed25519',
        publicKey,
        signatureBytes,
        messageBytes
      );
      return valid ? { ok: true } : { ok: false, reason: 'invalid_signature' };
    }

    return { ok: false, reason: 'no_verification_method' };
  } catch (error) {
    return { ok: false, reason: `verification_error: ${error}` };
  }
}

/**
 * Apply discovered anchors to pending storage with conflict resolution
 */
async function applyDiscoveredAnchors(
  pack: AnchorPack,
  ns: string,
  storage: StorageDriver,
  source: { transportId: string; path: string },
  conflictResolution: ConflictResolution = 'PREFER_NEWER'
): Promise<{ added: number; conflicts: number; rewinds: number }> {
  const existing = await getPendingAnchors(ns, pack.issuerOrg, storage);
  const existingMap = new Map(existing.map(a => [a.kid, a]));

  let added = 0;
  let conflicts = 0;
  let rewinds = 0;
  const now = new Date().toISOString();

  // Apply anchors from pack to pending
  for (const anchor of pack.anchors) {
    const current = existingMap.get(anchor.kid);

    if (current) {
      // Handle conflict resolution
      const conflict = await resolveConflict(
        current,
        anchor,
        pack.seq,
        source,
        conflictResolution
      );

      if (conflict.action === 'UPDATE') {
        current.status = anchor.status;
        current.src.packSeq = pack.seq;
        if (conflict.rewind) {
          rewinds++;
          // Log rewind alert
          await AuditApi.log('FED_DISC_REWIND_ALERT', {
            namespace: ns,
            orgId: pack.issuerOrg,
            kid: anchor.kid,
            currentSeq: current.src.packSeq,
            newSeq: pack.seq,
            transportId: source.transportId,
            severity: 'HIGH',
          });
        }
      } else if (conflict.action === 'REJECT') {
        conflicts++;
        await AuditApi.log('FED_DISC_CONFLICT', {
          namespace: ns,
          orgId: pack.issuerOrg,
          kid: anchor.kid,
          resolution: conflictResolution,
          reason: conflict.reason,
        });
      }
    } else {
      // New pending anchor
      const pending: PendingAnchor = {
        orgId: anchor.orgId,
        kid: anchor.kid,
        pubB64u: anchor.pubB64u,
        status: anchor.status,
        seenAt: now,
        src: {
          transportId: source.transportId,
          path: source.path,
          packSeq: pack.seq,
        },
      };

      existingMap.set(anchor.kid, pending);
      added++;
    }
  }

  // Store updated pending anchors
  await setPendingAnchors(
    ns,
    pack.issuerOrg,
    [...existingMap.values()],
    storage
  );

  return { added, conflicts, rewinds };
}

/**
 * Resolve conflicts between existing and new anchor
 */
async function resolveConflict(
  existing: PendingAnchor,
  newAnchor: any,
  newSeq: number,
  source: { transportId: string; path: string },
  resolution: ConflictResolution
): Promise<{ action: 'UPDATE' | 'REJECT'; rewind?: boolean; reason?: string }> {
  // Check for sequence rewind (potential replay attack)
  const isRewind = newSeq < existing.src.packSeq;

  switch (resolution) {
    case 'REJECT': {
      return {
        action: 'REJECT',
        reason: 'Policy configured to reject conflicts',
      };
    }

    case 'PREFER_NEWER': {
      if (newSeq > existing.src.packSeq) {
        return { action: 'UPDATE' };
      } else if (isRewind) {
        return { action: 'UPDATE', rewind: true };
      }
      return {
        action: 'REJECT',
        reason: 'Existing anchor has newer or equal sequence',
      };
    }

    case 'PREFER_FIRST': {
      return { action: 'REJECT', reason: 'Keeping first-seen anchor' };
    }

    case 'PREFER_SOURCE': {
      const existingPriority = existing.src.priority || 0;
      const newPriority = (source as any).priority || 0;
      if (newPriority > existingPriority) {
        return { action: 'UPDATE', rewind: isRewind };
      }
      return {
        action: 'REJECT',
        reason: 'Existing anchor from higher priority source',
      };
    }

    default: {
      return {
        action: 'REJECT',
        reason: 'Unknown conflict resolution strategy',
      };
    }
  }
}

/**
 * Run anchor discovery (M2)
 */
export async function runAnchorDiscovery(
  ns: string,
  plan: DiscPlan,
  storage: StorageDriver,
  opts?: {
    policy?: any;
    audit?: boolean;
    autoPromote?: boolean;
    conflictResolution?: ConflictResolution;
    ttlMinutes?: number;
  }
): Promise<DiscResult> {
  const result: DiscResult = {
    pulled: 0,
    pendingAdded: 0,
    promoted: 0,
    rejected: 0,
    conflicts: 0,
    rewinds: 0,
    expired: 0,
    errors: [],
  };

  for (const pull of plan.pulls) {
    try {
      // Clean expired pending anchors first
      const cleanupResult = await cleanExpiredPendingAnchors(
        ns,
        pull.orgId,
        storage
      );
      result.expired += cleanupResult.expired;

      // Check policy for discovery pulls
      if (opts?.policy) {
        const policyCheck = await checkCrossOrgPolicy(
          ns,
          pull.orgId,
          'federation.discovery.pull'
        );
        if (!policyCheck.allowed) {
          result.rejected++;
          result.errors.push(
            `Policy denied pull from ${pull.orgId}: ${policyCheck.reason}`
          );
          continue;
        }
      }

      // Mock pack retrieval - in real implementation would fetch via transport
      const mockPack: AnchorPack = {
        v: 1,
        issuerOrg: pull.orgId,
        createdAt: new Date().toISOString(),
        seq: 1,
        anchors: [],
        sig: { pubB64u: 'mock', sigB64u: 'mock' },
      };

      // Verify discovered pack
      const verification = await verifyDiscoveredPack(mockPack, ns, storage);
      if (!verification.ok) {
        result.rejected++;
        result.errors.push(
          `Verification failed for ${pull.orgId}: ${verification.reason}`
        );
        continue;
      }

      // Apply to pending storage
      const applied = await applyDiscoveredAnchors(
        mockPack,
        ns,
        storage,
        pull.ref,
        opts?.conflictResolution || 'PREFER_NEWER'
      );
      result.pulled++;
      result.pendingAdded += applied.added;
      result.conflicts += applied.conflicts;
      result.rewinds += applied.rewinds;

      // Update discovery cursor
      if (pull.nextSince) {
        await setDiscoveryState(
          ns,
          pull.orgId,
          { since: pull.nextSince },
          storage
        );
      }
    } catch (error) {
      result.errors.push(`Discovery error for ${pull.orgId}: ${error}`);
    }
  }

  // Update metrics
  await updateDiscoveryMetrics(ns, storage, {
    totalPulls: result.pulled,
    totalPending: result.pendingAdded,
    totalRejected: result.rejected,
    totalConflicts: result.conflicts,
    totalRewinds: result.rewinds,
    totalExpired: result.expired,
    lastPullAt: new Date().toISOString(),
  });

  return result;
}

/**
 * Phase B - Task 22: Federated Anchor Promotion
 * Promote pending anchors to trusted status with policy control
 */

import { checkCrossOrgPolicy } from '../policy/engine';
import type { StorageDriver } from '../storage/types';

import {
  getPendingAnchors,
  setPendingAnchors,
  updateDiscoveryMetrics,
  cleanExpiredPendingAnchors,
} from './discovery-registry';
import type { PendingAnchor } from './discovery-types';
import { addTrustAnchor, listTrustAnchors } from './registry';
import type { TrustAnchor } from './types';

/**
 * Auto-promote pending anchors based on policy (M3)
 */
export async function autoPromotePendingAnchors(
  ns: string,
  orgId: string,
  storage: StorageDriver,
  opts?: { policy?: any; audit?: boolean; ttlMinutes?: number }
): Promise<{ promoted: number; expired: number }> {
  // Clean expired pending anchors first
  const cleanupResult = await cleanExpiredPendingAnchors(ns, orgId, storage);

  const pending = await getPendingAnchors(ns, orgId, storage);
  const existing = await listTrustAnchors(ns);
  const existingKeys = new Set(existing.map(a => `${a.orgId}:${a.pubB64u}`));

  let promoted = 0;

  for (const anchor of pending) {
    // Skip if already trusted (by pubkey since TrustAnchor has no kid)
    if (existingKeys.has(`${anchor.orgId}:${anchor.pubB64u}`)) continue;

    // Skip REVOKED anchors (no resurrection)
    if (anchor.status === 'REVOKED') continue;

    // Skip RETIRED anchors (TrustAnchor only supports ACTIVE/REVOKED)
    if (anchor.status === 'RETIRED') continue;

    // Check promotion policy
    if (opts?.policy) {
      const policyCheck = await checkCrossOrgPolicy(
        ns,
        orgId,
        'federation.discovery.promote'
      );
      if (!policyCheck.allowed) continue;
    }

    // Promote to trusted (only ACTIVE status)
    const trustAnchor: TrustAnchor = {
      orgId: anchor.orgId,
      pubB64u: anchor.pubB64u,
      status: 'ACTIVE',
      createdAt: anchor.seenAt,
    };

    await addTrustAnchor(ns, trustAnchor);
    promoted++;
  }

  // Update metrics
  const metricsUpdate: any = {
    totalPromoted: promoted,
    totalExpired: cleanupResult.expired,
  };
  if (promoted > 0) {
    metricsUpdate.lastPromotionAt = new Date().toISOString();
  }
  await updateDiscoveryMetrics(ns, storage, metricsUpdate);

  return { promoted, expired: cleanupResult.expired };
}

/**
 * Manual promotion of specific pending anchors (M3)
 */
export async function promotePendingAnchors(
  ns: string,
  storage: StorageDriver,
  orgId: string,
  kids?: string[]
): Promise<{ promoted: number }> {
  const pending = await getPendingAnchors(ns, orgId, storage);
  const toPromote = kids ? pending.filter(a => kids.includes(a.kid)) : pending;

  let promoted = 0;
  const remaining: PendingAnchor[] = [];

  for (const anchor of pending) {
    if (toPromote.includes(anchor) && anchor.status !== 'REVOKED') {
      // Only promote ACTIVE anchors (TrustAnchor doesn't support RETIRED)
      if (anchor.status !== 'ACTIVE') {
        remaining.push(anchor);
        continue;
      }

      // Promote this anchor
      const trustAnchor: TrustAnchor = {
        orgId: anchor.orgId,
        pubB64u: anchor.pubB64u,
        status: 'ACTIVE',
        createdAt: anchor.seenAt,
      };

      await addTrustAnchor(ns, trustAnchor);
      promoted++;
    } else {
      // Keep in pending
      remaining.push(anchor);
    }
  }

  // Update pending storage (remove promoted ones)
  await setPendingAnchors(ns, orgId, remaining, storage);

  return { promoted };
}

/**
 * Accept invite for headless device onboarding with role enforcement
 */
import * as AuditApi from '../audit/api';
import { fromB64u } from '../crypto/base64url';
import type { KeyringProvider } from '../crypto/keyring';
import type { BackupBundle } from '../crypto/keyringTypes';
import { enforcePolicy } from '../policy/engine';
import { isInviteRevoked, isSignerRevoked } from '../revoke/guard';
import type { StorageDriver } from '../storage/types';

import type { InviteEnvelope, Role, InviteRoleConfig } from './types';

// Configuration dependencies for role enforcement
let membershipApi: {
  addMember: (issuer: string, user: string, role: Role) => Promise<void>;
  assertPermission: (
    actor: string,
    action: 'INVITE_CREATE',
    context?: { targetRole?: Role }
  ) => Promise<void>;
} | null = null;

let roleConfig: InviteRoleConfig = {
  strictLegacy: false,
  verifyIssuerStillAuthorized: false,
};

export function configureMembershipDependency(api: typeof membershipApi): void {
  membershipApi = api;
}

export function configureRolePolicy(config: InviteRoleConfig): void {
  roleConfig = { ...roleConfig, ...config };
}

export async function acceptInvite(args: {
  envelope: InviteEnvelope;
  code: string;
  keyring: KeyringProvider;
  verify: (
    bytes: ArrayBuffer,
    sig: string,
    pubB64u: string
  ) => Promise<boolean>;
  isUsed: (inviteId: string) => Promise<boolean>;
  markUsed: (inviteId: string) => Promise<void>;
  actorId?: string; // NEW: for membership application and audit
  storage?: StorageDriver; // NEW: for policy enforcement
  now?: () => Date;
  skewMs?: number;
}): Promise<{ importedCount: number; rewrapped: boolean; appliedRole: Role }> {
  const {
    envelope,
    code,
    keyring,
    verify,
    isUsed,
    markUsed,
    actorId,
    storage,
    now = () => new Date(),
    skewMs = 300_000,
  } = args;

  // Extract invite ID from AAD
  const inviteId = envelope.aad.split(':')[1];
  if (!inviteId) throw new Error('Invalid AAD format');

  // Policy enforcement for invite acceptance
  if (storage && actorId) {
    const envelopeRole = (envelope as any).role as Role | undefined;
    const targetRole = envelopeRole || 'MEMBER'; // Default for legacy invites
    const currentRole = 'VIEWER'; // Default role for invite recipients

    await enforcePolicy(
      {
        ns: envelope.aad.split(':')[0],
        op: 'invite.accept',
        actorId,
        actorRole: currentRole,
        targetRole,
        nowISO: now().toISOString(),
      },
      storage,
      { audit: true, commitCap: true }
    );
  }

  // Check revocation status first (fail fast)
  if (await isInviteRevoked(inviteId)) throw new Error('Invite revoked');
  if (await isSignerRevoked(envelope.signerPubB64u))
    throw new Error('Signer revoked');

  // Build canonical manifest for verification (with role if present)
  const { signerPubB64u, sigB64u, ...manifest } = envelope;
  const canonical = JSON.stringify(manifest, Object.keys(manifest).sort());

  // Verify Ed25519 signature
  const isValid = await verify(
    new TextEncoder().encode(canonical).buffer,
    sigB64u,
    signerPubB64u
  );
  if (!isValid) throw new Error('Invalid signature');

  // Check expiry with clock-skew tolerance
  const expTime = new Date(envelope.exp).getTime();
  const nowTime = now().getTime();
  if (expTime + skewMs <= nowTime) throw new Error('Invite expired');

  // Check one-time use
  if (await isUsed(inviteId)) throw new Error('Invite already used');

  // Extract bound role from envelope
  let boundRole: Role;
  const envelopeRole = (envelope as any).role as Role | undefined;

  if (envelopeRole) {
    boundRole = envelopeRole;

    // Optional: verify issuer still authorized for this role
    if (roleConfig.verifyIssuerStillAuthorized && membershipApi) {
      try {
        await membershipApi.assertPermission(
          envelope.signerPubB64u,
          'INVITE_CREATE',
          { targetRole: boundRole }
        );
      } catch {
        throw new Error(
          `Issuer no longer authorized to issue ${boundRole} invites`
        );
      }
    }
  } else {
    // Legacy invite handling
    if (roleConfig.strictLegacy) {
      throw new Error('Legacy invites not allowed in strict mode');
    }
    boundRole = 'MEMBER'; // Default for legacy invites
  }

  // Derive session key
  const salt = fromB64u(envelope.saltB64u);
  const keyMaterial = await crypto.subtle.importKey(
    'raw',
    new TextEncoder().encode(code),
    'PBKDF2',
    false,
    ['deriveKey']
  );

  const sessionKey = await crypto.subtle.deriveKey(
    { name: 'PBKDF2', salt, iterations: envelope.iter, hash: 'SHA-256' },
    keyMaterial,
    { name: 'AES-GCM', length: 256 },
    false,
    ['decrypt']
  );

  // Decrypt backup (constant-time via GCM tag verification)
  const combined = fromB64u(envelope.ctB64u);
  const iv = combined.slice(0, 12);
  const ciphertext = combined.slice(12);

  let plaintext: ArrayBuffer;
  try {
    plaintext = await crypto.subtle.decrypt(
      {
        name: 'AES-GCM',
        iv,
        additionalData: new TextEncoder().encode(envelope.aad),
      },
      sessionKey,
      ciphertext
    );
  } catch {
    throw new Error('Invalid code or corrupted invite');
  }

  // Parse and import backup
  const backupJson = new TextDecoder().decode(plaintext);
  const backup: BackupBundle = JSON.parse(backupJson);

  await keyring.importBackup(backup);
  await markUsed(inviteId);

  // Apply membership role
  if (membershipApi && actorId) {
    await membershipApi.addMember(envelope.signerPubB64u, actorId, boundRole);

    // Emit audit event for invite acceptance
    AuditApi.log(
      'INVITE_ACCEPTED',
      { role: boundRole, inviteId, userId: actorId },
      actorId
    ).catch(error => console.error('Audit log failed:', error));
  }

  return {
    importedCount: backup.deks.length,
    rewrapped: true,
    appliedRole: boundRole,
  };
}

/**
 * Invite code validation helpers with checksums
 */

// Base32 alphabet (Crockford's Base32) - avoids confusing chars
const B32_ALPHABET = '0123456789ABCDEFGHJKMNPQRSTVWXYZ';

/**
 * Calculate 2-character checksum for invite code
 */
function calculateChecksum(code: string): string {
  let hash = 0;
  for (let i = 0; i < code.length; i++) {
    hash = ((hash << 5) - hash + code.charCodeAt(i)) & 0xff_ff_ff_ff;
  }
  const c1 = B32_ALPHABET[Math.abs(hash) % 32];
  const c2 = B32_ALPHABET[Math.abs(hash >> 5) % 32];
  return c1 + c2;
}

/**
 * Add checksum to invite code
 */
export function addCodeChecksum(code: string): string {
  return code + '-' + calculateChecksum(code);
}

/**
 * Verify and extract original code from checksummed code
 */
export function verifyCodeChecksum(codeWithChecksum: string): string {
  const lastDashIndex = codeWithChecksum.lastIndexOf('-');
  if (lastDashIndex === -1 || lastDashIndex === codeWithChecksum.length - 1) {
    throw new Error('Invalid code format');
  }

  const code = codeWithChecksum.slice(0, lastDashIndex);
  const checksum = codeWithChecksum.slice(lastDashIndex + 1);
  const expectedChecksum = calculateChecksum(code);

  if (checksum !== expectedChecksum) {
    throw new Error('Invalid code checksum');
  }

  return code;
}

