/**
 * Maintenance runner - executes maintenance plans
 */

import { toB64u } from '../crypto/base64url';
import { type KeyringProvider } from '../crypto/keyring';
import { type Envelope } from '../crypto/types';
import { compactWithSnapshot } from '../domain/task/eventlog';
import { type EncryptedDriver } from '../storage/encrypted';
import { type StorageDriver } from '../storage/types';

import { type MaintenancePlan, type MaintenanceReport } from './types';

export interface MaintenanceDeps {
  storage: StorageDriver;
  encrypted?: EncryptedDriver;
  keyring?: KeyringProvider;
  resumeToken?: string;
}

/**
 * Execute a maintenance plan and return report
 */
export async function runMaintenance(
  plan: MaintenancePlan,
  deps: MaintenanceDeps
): Promise<MaintenanceReport> {
  const report: MaintenanceReport = {};

  for (const action of plan.actions) {
    switch (action.type) {
      case 'COMPACT': {
        const result = executeCompact(action.threshold);
        if (result) report.compact = result;
        break;
      }
      case 'REKEY': {
        const result = await executeRekey(
          action.prefix,
          action.batchSize,
          deps
        );
        if (result) report.rekey = result;
        break;
      }
      case 'SWEEP': {
        const result = await executeSweep(
          action.prefix,
          action.fix,
          action.sample,
          deps
        );
        if (result) report.sweep = result;
        break;
      }
    }
  }

  return report;
}

/**
 * Execute compaction with snapshot
 */
function executeCompact(threshold: number): MaintenanceReport['compact'] {
  const result = compactWithSnapshot(threshold);
  return {
    trimmed: result.trimmed,
    tookSnapshot: result.tookSnapshot,
  };
}

/**
 * Execute rekeying operation
 */
async function executeRekey(
  prefix: string,
  batchSize: number,
  deps: MaintenanceDeps
): Promise<MaintenanceReport['rekey']> {
  if (!deps.encrypted || !deps.keyring) {
    return { processed: 0, rewrapped: 0, failures: 0 };
  }

  const { storage, encrypted, keyring } = deps;

  // Guardrail: Check if keyring is accessible
  try {
    await keyring.getActiveKey();
  } catch (error) {
    console.warn('REKEY operation skipped: keyring is locked or inaccessible');
    return { processed: 0, rewrapped: 0, failures: 0 };
  }

  const { kid: activeKid } = await keyring.getActiveKey();

  let processed = 0;
  let rewrapped = 0;
  let failures = 0;

  try {
    let keys = await storage.listKeys(prefix);

    // Resume from token if provided
    const resumeKey = deps.resumeToken;
    if (resumeKey) {
      const resumeIndex = keys.indexOf(resumeKey);
      if (resumeIndex > 0) {
        keys = keys.slice(resumeIndex);
        console.log(
          `Resuming REKEY from key: ${resumeKey} (${keys.length} remaining)`
        );
      }
    }

    for (let i = 0; i < keys.length; i += batchSize) {
      const batch = keys.slice(i, i + batchSize);

      for (const key of batch) {
        try {
          processed++;
          const rawValue = await storage.getItem(key);
          if (!rawValue) continue;

          // Try to parse as envelope
          let envelope: Envelope;
          try {
            envelope = JSON.parse(rawValue);
          } catch {
            // Not JSON, skip plaintext
            continue;
          }

          // Check if it's a valid envelope
          if (!envelope.v || !envelope.alg || !envelope.kid) {
            continue;
          }

          // Skip if already using active key
          if (envelope.kid === activeKid) {
            continue;
          }

          // Decrypt and re-encrypt with active key (preserving timestamp and AAD)
          const value = await encrypted.getItem(key);
          if (value !== null) {
            await encrypted.setItem(key, value);
            rewrapped++;
          }
        } catch (error) {
          failures++;
          // Continue processing other keys
        }
      }
    }
  } catch (error) {
    failures++;
  }

  return { processed, rewrapped, failures };
}

/**
 * Execute integrity sweep
 */
async function executeSweep(
  prefix: string,
  fix: boolean,
  sample: number | undefined,
  deps: MaintenanceDeps
): Promise<MaintenanceReport['sweep']> {
  if (!deps.encrypted || !deps.keyring) {
    return { scanned: 0, ok: 0, repaired: 0, failed: [] };
  }

  const { storage, encrypted, keyring } = deps;
  const { kid: activeKid } = await keyring.getActiveKey();

  let scanned = 0;
  let ok = 0;
  let repaired = 0;
  const failed: Array<{ key: string; reason: string }> = [];

  try {
    let keys = await storage.listKeys(prefix);

    // Apply sample limit for performance on large stores
    if (sample && sample > 0 && keys.length > sample) {
      // Take random sample to get representative data
      const shuffled = keys.sort(() => 0.5 - Math.random());
      keys = shuffled.slice(0, sample);
    }
    // Apply sampling if specified
    if (sample !== undefined && keys.length > sample) {
      keys = keys.slice(0, sample);
    }

    for (const key of keys) {
      try {
        scanned++;
        const rawValue = await storage.getItem(key);
        if (!rawValue) {
          failed.push({ key, reason: 'Key not found' });
          continue;
        }

        // Try to parse as envelope
        let envelope: Envelope;
        try {
          envelope = JSON.parse(rawValue);
        } catch {
          // Not JSON, assume plaintext is OK
          ok++;
          continue;
        }

        // Check if it's a valid envelope
        if (!envelope.v || !envelope.alg || !envelope.kid) {
          // Not an envelope, assume plaintext is OK
          ok++;
          continue;
        }

        // Extract namespace from encrypted driver
        const expectedAad = `${getNamespaceFromEncrypted(encrypted)}:${key}`;
        const aadBytes = new TextEncoder().encode(expectedAad);
        const expectedAadB64u = toB64u(aadBytes.buffer);

        // Check AAD
        if (envelope.aad && envelope.aad !== expectedAadB64u) {
          failed.push({ key, reason: 'AAD mismatch' });
          continue;
        }

        // Try to decrypt and verify integrity
        try {
          const decrypted = await encrypted.getItem(key);
          if (decrypted === null) {
            failed.push({ key, reason: 'Decryption failed' });
            continue;
          }

          // If fix=true and using old key, re-encrypt with active key
          if (fix && envelope.kid !== activeKid) {
            await encrypted.setItem(key, decrypted);
            repaired++;
          } else {
            ok++;
          }
        } catch (error) {
          failed.push({
            key,
            reason: `Decryption error: ${error instanceof Error ? error.message : 'Unknown'}`,
          });
        }
      } catch (error) {
        failed.push({
          key,
          reason: `Processing error: ${error instanceof Error ? error.message : 'Unknown'}`,
        });
      }
    }
  } catch (error) {
    failed.push({
      key: 'LIST_KEYS',
      reason: `Failed to list keys: ${error instanceof Error ? error.message : 'Unknown'}`,
    });
  }

  return { scanned, ok, repaired, failed };
}

/**
 * Extract namespace from EncryptedDriver instance
 */
function getNamespaceFromEncrypted(encrypted: EncryptedDriver): string {
  // Access the private ns field via type assertion
  return (encrypted as any).ns || 'unknown';
}

/**
 * Workspace Membership & Roles API - Phase B Task 14
 * Headless role-based membership with E2EE compatibility
 */

import * as AuditApi from '../audit/api';
import { toB64u, fromB64u } from '../crypto/base64url';
import { enforcePolicy } from '../policy/engine';
import type { StorageDriver } from '../storage/types';

import type {
  Role,
  MOp,
  MRecord,
  MState,
  MTransport,
  MPlan,
  MResult,
} from './types';

let storage: StorageDriver;
let namespace: string;
let trustedAdmins: string[];
let transport: MTransport;
let skewTolerance = 5 * 60 * 1000; // 5 minutes default

export function configureMembership(
  storageDriver: StorageDriver,
  ns: string,
  admins: string[],
  transportImpl?: MTransport,
  skewToleranceMs?: number
): void {
  storage = storageDriver;
  namespace = ns;
  trustedAdmins = admins;
  if (transportImpl) transport = transportImpl;
  if (skewToleranceMs) skewTolerance = skewToleranceMs;
  // Initialize audit logging for membership events
  AuditApi.configureAudit(storageDriver, ns);
}

// Canonical JSON + utilities
function canon(obj: any): string {
  if (obj === null || typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canon).join(',') + ']';
  const keys = Object.keys(obj).sort();
  return '{' + keys.map(k => `"${k}":${canon(obj[k])}`).join(',') + '}';
}

function generateId(): string {
  return Date.now().toString(36) + Math.random().toString(36).slice(2, 8);
}

async function verifySignature(record: MRecord): Promise<boolean> {
  try {
    const { issuer, ...recordWithoutSig } = record;
    const { sigB64u, ...issuerWithoutSig } = issuer;
    const canonical = canon({ ...recordWithoutSig, issuer: issuerWithoutSig });
    const pubKey = await crypto.subtle.importKey(
      'spki',
      fromB64u(issuer.pubB64u),
      { name: 'Ed25519' },
      false,
      ['verify']
    );
    return await crypto.subtle.verify(
      'Ed25519',
      pubKey,
      fromB64u(sigB64u),
      new TextEncoder().encode(canonical)
    );
  } catch {
    return false;
  }
}

// State management
export async function getMembership(): Promise<MState> {
  try {
    const data = await storage.getItem(`m:${namespace}:state`);
    return data ? JSON.parse(data) : { users: {}, owners: [] };
  } catch {
    return { users: {}, owners: [] };
  }
}

async function saveMembership(state: MState): Promise<void> {
  await storage.setItem(`m:${namespace}:state`, JSON.stringify(state));
}

// Permission system
const ROLE_LEVELS: Record<Role, number> = {
  VIEWER: 1,
  MEMBER: 2,
  ADMIN: 3,
  OWNER: 4,
};
function hasPermission(
  userRole: Role | undefined,
  requiredRole: Role
): boolean {
  return userRole ? ROLE_LEVELS[userRole] >= ROLE_LEVELS[requiredRole] : false;
}

export async function assertPermission(
  actor: string,
  action:
    | 'INVITE_CREATE'
    | 'INVITE_REVOKE'
    | 'SIGNER_REVOKE'
    | 'RECOVERY_CREATE'
    | 'RECOVERY_OVERRIDE_CREATE'
    | 'ROLE_SET'
    | 'ROLE_REMOVE'
    | 'TASK_WRITE'
    | 'TASK_READ',
  context?: { targetRole?: Role }
): Promise<void> {
  const state = await getMembership();
  const userRole = state.users[actor];
  const actionRequirements: Record<string, Role> = {
    INVITE_CREATE: 'ADMIN',
    INVITE_REVOKE: 'ADMIN',
    SIGNER_REVOKE: 'ADMIN',
    RECOVERY_CREATE: 'ADMIN',
    RECOVERY_OVERRIDE_CREATE: 'ADMIN',
    ROLE_SET: 'ADMIN',
    ROLE_REMOVE: 'ADMIN',
    TASK_WRITE: 'MEMBER',
    TASK_READ: 'VIEWER',
  };
  const required = actionRequirements[action];
  if (!hasPermission(userRole, required))
    throw new Error(
      `Access denied: ${action} requires ${required}, user has ${userRole || 'none'}`
    );

  // Special case: INVITE_CREATE with target role validation
  if (action === 'INVITE_CREATE' && context?.targetRole) {
    const INVITE_ROLE_POLICY: Record<Role, Role[]> = {
      OWNER: ['OWNER', 'ADMIN', 'MEMBER', 'VIEWER'],
      ADMIN: ['ADMIN', 'MEMBER', 'VIEWER'],
      MEMBER: [],
      VIEWER: [],
    };
    const allowedRoles = userRole ? INVITE_ROLE_POLICY[userRole] || [] : [];
    if (!allowedRoles.includes(context.targetRole))
      throw new Error(
        `Access denied: ${userRole || 'none'} cannot issue ${context.targetRole} invites`
      );
  }
}

// State reducer
function applyRecord(state: MState, record: MRecord): MState {
  const newState = {
    ...state,
    users: { ...state.users },
    owners: [...state.owners],
  };
  const prevRole = newState.users[record.user];

  switch (record.op) {
    case 'ADD': {
      if (record.role) {
        newState.users[record.user] = record.role;
        if (record.role === 'OWNER') {
          if (!newState.owners.includes(record.user))
            newState.owners.push(record.user);
          // Detect concurrent OWNER transitions
          const transitionKey = `${record.ts}:${record.issuer.pubB64u}`;
          if (
            newState.lastOwnerTransition &&
            newState.lastOwnerTransition !== transitionKey
          ) {
            console.warn(
              `Concurrent OWNER transition detected: ${newState.lastOwnerTransition} vs ${transitionKey}`
            );
          }
          newState.lastOwnerTransition = transitionKey;
        }
      }
      break;
    }
    case 'REMOVE': {
      delete newState.users[record.user];
      newState.owners = newState.owners.filter(o => o !== record.user);
      break;
    }
    case 'ROLE': {
      if (record.role && newState.users[record.user]) {
        // Audit role changes (especially downgrades)
        if (prevRole && ROLE_LEVELS[prevRole] > ROLE_LEVELS[record.role]) {
          const auditEvent = {
            actor: record.issuer.pubB64u,
            target: record.user,
            prevRole,
            nextRole: record.role,
            ts: record.ts,
            workspaceId: record.workspaceId,
          };
          console.warn(
            `Role downgrade: ${record.user} ${prevRole}‚Üí${record.role} by ${record.issuer.pubB64u} at ${record.ts}`,
            auditEvent
          );
          // Emit structured audit event
          AuditApi.log(
            'ROLE_DOWNGRADE',
            auditEvent,
            record.issuer.pubB64u
          ).catch(error => console.error('Audit log failed:', error));
        }
        newState.users[record.user] = record.role;
        if (record.role === 'OWNER') {
          if (!newState.owners.includes(record.user))
            newState.owners.push(record.user);
          // Detect concurrent OWNER transitions
          const transitionKey = `${record.ts}:${record.issuer.pubB64u}`;
          if (
            newState.lastOwnerTransition &&
            newState.lastOwnerTransition !== transitionKey
          ) {
            const concurrentEvent = {
              currentTransition: newState.lastOwnerTransition,
              newTransition: transitionKey,
              user: record.user,
            };
            console.warn(
              `Concurrent OWNER transition detected: ${newState.lastOwnerTransition} vs ${transitionKey}`,
              concurrentEvent
            );
            // Emit structured audit event
            AuditApi.log(
              'OWNER_TRANSITION',
              concurrentEvent,
              record.issuer.pubB64u
            ).catch(error => console.error('Audit log failed:', error));
          }
          newState.lastOwnerTransition = transitionKey;
        } else {
          newState.owners = newState.owners.filter(o => o !== record.user);
        }
      }
      break;
    }
  }
  newState.ts = record.ts;
  return newState;
}

// Record creation
async function createRecord(
  issuer: string,
  op: MOp,
  user: string,
  role?: Role
): Promise<MRecord> {
  const id = generateId();
  const ts = new Date().toISOString();
  const recordWithoutSig = {
    v: 1 as const,
    id,
    ts,
    op,
    user,
    workspaceId: namespace,
    ...(role && { role }),
    issuer: { pubB64u: issuer },
  };
  const canonical = canon(recordWithoutSig);
  const sigBytes = new TextEncoder().encode(
    'placeholder-sig-' + canonical.slice(0, 16)
  );
  const sigB64u = toB64u(sigBytes.buffer);
  return { ...recordWithoutSig, issuer: { pubB64u: issuer, sigB64u } };
}

// Public operations
export async function addMember(
  issuer: string,
  user: string,
  role: Role
): Promise<void> {
  const state = await getMembership();

  // Handle initial owner case first (before policy)
  if (state.owners.length === 0 && role === 'OWNER') {
    const record = await createRecord(issuer, 'ADD', user, role);
    const newState = applyRecord(state, record);
    await saveMembership(newState);
    await storage.setItem(
      `m:${namespace}:out:${record.id}`,
      JSON.stringify(record)
    );
    // Emit audit event for member addition
    AuditApi.log(
      'MEMBER_ADDED',
      { user, role, initialOwner: true },
      issuer
    ).catch(error => console.error('Audit log failed:', error));
    return;
  }

  // Policy enforcement before authorization
  const issuerRole = state.users[issuer];
  if (issuerRole) {
    await enforcePolicy(
      {
        ns: namespace,
        op: 'membership.add',
        actorId: issuer,
        actorRole: issuerRole,
        targetRole: role,
        nowISO: new Date().toISOString(),
      },
      storage,
      { audit: true, commitCap: true }
    );
  }

  await assertPermission(issuer, 'ROLE_SET');
  const record = await createRecord(issuer, 'ADD', user, role);
  const newState = applyRecord(state, record);
  await saveMembership(newState);
  await storage.setItem(
    `m:${namespace}:out:${record.id}`,
    JSON.stringify(record)
  );
  // Emit audit event for member addition
  AuditApi.log('MEMBER_ADDED', { user, role }, issuer).catch(error =>
    console.error('Audit log failed:', error)
  );
}

export async function removeMember(
  issuer: string,
  user: string
): Promise<void> {
  const state = await getMembership();

  // Policy enforcement before authorization
  const issuerRole = state.users[issuer];
  const targetRole = state.users[user];
  if (issuerRole) {
    await enforcePolicy(
      {
        ns: namespace,
        op: 'membership.remove',
        actorId: issuer,
        actorRole: issuerRole,
        targetRole,
        nowISO: new Date().toISOString(),
      },
      storage,
      { audit: true }
    );
  }

  await assertPermission(issuer, 'ROLE_REMOVE');
  if (state.owners.includes(user) && state.owners.length <= 1)
    throw new Error('Cannot remove last OWNER');
  const record = await createRecord(issuer, 'REMOVE', user);
  const newState = applyRecord(state, record);
  await saveMembership(newState);
  await storage.setItem(
    `m:${namespace}:out:${record.id}`,
    JSON.stringify(record)
  );
  // Emit audit event for member removal
  AuditApi.log(
    'MEMBER_REMOVED',
    { user, removedRole: state.users[user] },
    issuer
  ).catch(error => console.error('Audit log failed:', error));
}

export async function changeRole(
  issuer: string,
  user: string,
  role: Role
): Promise<void> {
  const state = await getMembership();

  // Policy enforcement before authorization
  const issuerRole = state.users[issuer];
  if (issuerRole) {
    await enforcePolicy(
      {
        ns: namespace,
        op: 'membership.change',
        actorId: issuer,
        actorRole: issuerRole,
        targetRole: role,
        nowISO: new Date().toISOString(),
      },
      storage,
      { audit: true }
    );
  }

  if (role === 'OWNER' && !state.owners.includes(issuer))
    throw new Error('Only OWNER can grant OWNER role');
  await assertPermission(issuer, 'ROLE_SET');
  const record = await createRecord(issuer, 'ROLE', user, role);
  const newState = applyRecord(state, record);
  await saveMembership(newState);
  await storage.setItem(
    `m:${namespace}:out:${record.id}`,
    JSON.stringify(record)
  );
}

// Sync state management
async function getSyncState(): Promise<{ since?: string }> {
  try {
    const data = await storage.getItem(`m:${namespace}:__sync_state__`);
    return data ? JSON.parse(data) : {};
  } catch {
    return {};
  }
}
async function setSyncState(state: { since?: string }): Promise<void> {
  await storage.setItem(`m:${namespace}:__sync_state__`, JSON.stringify(state));
}

export async function planMemberSync(): Promise<MPlan> {
  if (!transport) throw new Error('Transport not configured');
  const state = await getSyncState();
  const result = await transport.list(namespace, state.since);
  return {
    pullKeys: result.keys,
    ...(result.nextSince && { since: result.nextSince }),
  };
}

export async function runMemberSync(plan?: MPlan): Promise<MResult> {
  if (!transport) throw new Error('Transport not configured');
  const syncPlan = plan || (await planMemberSync());
  const result: MResult = {
    applied: 0,
    pushed: 0,
    errors: [],
    completed: false,
  };

  try {
    const seenHashes = new Set<string>();

    // Pull and apply remote records
    for (const key of syncPlan.pullKeys) {
      try {
        const data = await transport.get(key);
        if (!data) continue;
        const record: MRecord = JSON.parse(data);
        const recordHash = canon(record);
        if (seenHashes.has(recordHash)) continue;
        seenHashes.add(recordHash);

        // Clock skew hardening with configurable tolerance
        const recordTime = new Date(record.ts).getTime();
        const skewMs = Math.abs(recordTime - Date.now());
        if (skewMs > skewTolerance)
          console.warn(
            `Clock skew detected: record ${record.id} offset ${Math.round(skewMs / 1000)}s (tolerance: ${Math.round(skewTolerance / 1000)}s)`
          );

        // Verify signature and trust + org boundary
        const currentState = await getMembership();
        const trustedIssuers = [...trustedAdmins, ...currentState.owners];
        if (record.workspaceId !== namespace) {
          result.errors.push(
            `Cross-workspace replay blocked: ${record.workspaceId} != ${namespace}`
          );
          continue;
        }
        if (!trustedIssuers.includes(record.issuer.pubB64u)) {
          result.errors.push(`Untrusted issuer: ${record.issuer.pubB64u}`);
          continue;
        }
        if (!(await verifySignature(record))) {
          result.errors.push(`Invalid signature for record: ${record.id}`);
          continue;
        }

        // Apply to state
        const newState = applyRecord(currentState, record);
        await saveMembership(newState);
        result.applied++;
      } catch (error) {
        result.errors.push(
          `Error processing ${key}: ${error instanceof Error ? error.message : 'Unknown'}`
        );
      }
    }

    // Push local outbox in batches
    const outboxKeys = await storage.listKeys(`m:${namespace}:out:`);
    for (let i = 0; i < outboxKeys.length; i += 100) {
      const batch = outboxKeys.slice(i, i + 100);
      for (const outboxKey of batch) {
        try {
          const data = await storage.getItem(outboxKey);
          if (!data) continue;
          const record: MRecord = JSON.parse(data);
          await transport.put(
            `m:${namespace}:r:${record.ts}:${record.id}`,
            data,
            record.ts
          );
          await storage.removeItem(outboxKey);
          result.pushed++;
        } catch (error) {
          result.errors.push(
            `Push error ${outboxKey}: ${error instanceof Error ? error.message : 'Unknown'}`
          );
        }
      }
    }

    // Update sync state with monotonicity
    const currentState = await getSyncState();
    if (
      syncPlan.since &&
      (!currentState.since || syncPlan.since > currentState.since)
    )
      await setSyncState({ since: syncPlan.since });

    result.completed = true;
  } catch (error) {
    result.errors.push(
      `Sync failed: ${error instanceof Error ? error.message : 'Unknown'}`
    );
  }

  return result;
}

/**
 * Performance budgets and thresholds
 */

interface PerfBudget {
  p95_ms?: number;
  max_ms?: number;
  n: number;
}

interface PerfConfig {
  lanes: PerfBudget;
  query: PerfBudget;
  search: PerfBudget;
  searchUpdate: PerfBudget;
  buildIndex: PerfBudget;
}

const BASE_PERF: PerfConfig = {
  lanes: { p95_ms: 5, n: 1000 },
  query: { p95_ms: 200, n: 1000 },
  search: { p95_ms: 120, n: 1000 },
  searchUpdate: { p95_ms: 5, n: 1000 },
  buildIndex: { max_ms: 500, n: 1000 },
};

/**
 * Get performance budgets with environment variable overrides
 */
function getPerfConfig(): PerfConfig {
  let multiplier = 1;

  // Environment overrides
  if (process.env.PERF_RELAX === '1') {
    multiplier = 1.5;
  } else if (process.env.PERF_STRICT === '1') {
    multiplier = 0.8;
  }

  const config = JSON.parse(JSON.stringify(BASE_PERF)) as PerfConfig;

  // Apply multiplier to all time thresholds
  for (const budget of Object.values(config)) {
    if (budget.p95_ms) {
      budget.p95_ms = Math.ceil(budget.p95_ms * multiplier);
    }
    if (budget.max_ms) {
      budget.max_ms = Math.ceil(budget.max_ms * multiplier);
    }
  }

  return config;
}

export const PERF = getPerfConfig();

/**
 * Recovery bundle API for headless admin recovery
 */

import { toB64u, fromB64u } from '../crypto/base64url';
import type { Role } from '../membership/types';
import { enforcePolicy } from '../policy/engine';
import type { StorageDriver } from '../storage/types';

import type {
  RecoveryBundleV1,
  CreateRecoveryArgs,
  RecoverArgs,
} from './types';

// Rate limiting store (in-memory)
const rateLimitStore = new Map<
  string,
  { attempts: number; lastAttempt: number }
>();

// Canonical JSON for signature consistency
function canonicalize(obj: any): string {
  if (obj === null || typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canonicalize).join(',') + ']';
  const keys = Object.keys(obj).sort();
  const pairs = keys.map(k => `"${k}":${canonicalize(obj[k])}`);
  return '{' + pairs.join(',') + '}';
}

// Namespace-bound AAD for AES-GCM
function recoveryAAD(ns: string): ArrayBuffer {
  return new TextEncoder().encode(`${ns}:recovery`).buffer;
}

// Simple FNV-1a 32-bit hash for bundle ID generation
function simpleHash(str: string): string {
  let hash = 0x81_1c_9d_c5; // FNV offset basis (32-bit)

  for (let i = 0; i < str.length; i++) {
    hash ^= str.charCodeAt(i);
    hash = Math.imul(hash, 0x01_00_01_93); // FNV prime (32-bit)
  }

  return (hash >>> 0).toString(16); // Convert to unsigned 32-bit hex
}

// Generate deterministic bundle ID for revocation tracking
function getBundleId(bundle: Omit<RecoveryBundleV1, 'sigB64u'>): string {
  const canonical = canonicalize(bundle);
  return simpleHash(canonical);
}

// Check if rate limited without incrementing attempts
function isRateLimited(bundleId: string): boolean {
  const now = Date.now();
  const record = rateLimitStore.get(bundleId);

  if (!record) return false;

  const timeSinceLastAttempt = now - record.lastAttempt;
  const requiredWait = Math.min(
    1000 * Math.pow(2, record.attempts - 1),
    30_000
  );

  return timeSinceLastAttempt < requiredWait;
}

// Reset rate limit on successful recovery
function resetRateLimit(bundleId: string): void {
  rateLimitStore.delete(bundleId);
}

/**
 * Create recovery bundle from healthy keyring
 */
export async function createRecoveryBundle({
  ns,
  keyring,
  issuer,
  passcode,
  expiresAt,
  iter = 100_000,
  meta,
  actorId,
  actorRole,
  storage,
}: CreateRecoveryArgs & {
  actorId?: string;
  actorRole?: Role;
  storage?: StorageDriver;
}): Promise<RecoveryBundleV1> {
  // Policy enforcement before creating recovery bundle
  if (storage && actorId && actorRole) {
    await enforcePolicy(
      {
        ns,
        op: 'recovery.create',
        actorId,
        actorRole,
        nowISO: new Date().toISOString(),
      },
      storage,
      { audit: true, commitCap: true }
    );
  }

  // Export DEK snapshot
  const backup = await keyring.exportBackup();
  const dekSnapshot = backup.deks;

  // Generate recovery KEK using PBKDF2 -> AES-GCM
  const salt = crypto.getRandomValues(new Uint8Array(16));
  const keyMaterial = await crypto.subtle.importKey(
    'raw',
    new TextEncoder().encode(passcode),
    'PBKDF2',
    false,
    ['deriveKey']
  );
  const gcmKey = await crypto.subtle.deriveKey(
    { name: 'PBKDF2', salt: salt.buffer, iterations: iter, hash: 'SHA-256' },
    keyMaterial,
    { name: 'AES-GCM', length: 256 },
    false,
    ['encrypt', 'decrypt']
  );

  // Encrypt DEK snapshot with AES-GCM
  const iv = crypto.getRandomValues(new Uint8Array(12));
  const plaintext = new TextEncoder().encode(JSON.stringify(dekSnapshot));
  const aad = recoveryAAD(ns);

  const ciphertext = await crypto.subtle.encrypt(
    { name: 'AES-GCM', iv, additionalData: aad },
    gcmKey,
    plaintext
  );

  // Create bundle for signing
  const bundle = {
    v: 1 as const,
    ns,
    createdAt: new Date().toISOString(),
    ...(expiresAt && { expiresAt }),
    saltB64u: toB64u(salt.buffer),
    iter,
    issuerKID: issuer.kid,
    pubB64u: issuer.pubB64u,
    ctB64u: toB64u(ciphertext),
    ivB64u: toB64u(iv.buffer),
    ...(meta && { meta }),
  };

  // Sign canonical JSON
  const canonical = canonicalize(bundle);
  const messageBytes = new TextEncoder().encode(canonical);
  const signature = await issuer.sign(messageBytes);

  return { ...bundle, sigB64u: toB64u(new Uint8Array(signature).buffer) };
}

/**
 * Recover DEKs from bundle using passcode
 */
export async function recoverFromBundle({
  ns,
  keyring,
  bundle,
  passcode,
  options = {},
}: RecoverArgs): Promise<{ imported: number }> {
  // Validate bundle version
  if (bundle.v !== 1) throw new Error('Unsupported recovery bundle version');

  // Check expiry
  if (bundle.expiresAt && new Date() > new Date(bundle.expiresAt)) {
    throw new Error('Recovery bundle expired');
  }

  // Verify namespace binding
  if (bundle.ns !== ns) throw new Error('Namespace mismatch');

  // Generate bundle ID for revocation/rate limiting
  const { sigB64u, ...bundleForSig } = bundle;
  const bundleId = getBundleId(bundleForSig);

  // Check bundle revocation
  if (options.revocationRegistry) {
    const isRevoked = await options.revocationRegistry.isRevoked(bundleId);
    if (isRevoked) throw new Error('Recovery bundle has been revoked');
  }

  // Verify signature with issuer rotation support
  const canonical = canonicalize(bundleForSig);
  const messageBytes = new TextEncoder().encode(canonical);
  const signature = fromB64u(bundle.sigB64u);

  let publicKeyB64u = bundle.pubB64u; // Default: embedded key
  if (options.keyRegistry) {
    // Try to resolve key by issuerKID for rotation support
    const registryKey = await options.keyRegistry.getPublicKey(
      bundle.issuerKID
    );
    if (registryKey) {
      publicKeyB64u = registryKey;
    } else {
      throw new Error(`Unknown issuer key: ${bundle.issuerKID}`);
    }
  }

  const publicKey = await crypto.subtle.importKey(
    'spki',
    fromB64u(publicKeyB64u),
    { name: 'Ed25519' },
    false,
    ['verify']
  );
  const isValid = await crypto.subtle.verify(
    'Ed25519',
    publicKey,
    signature,
    messageBytes
  );

  if (!isValid) throw new Error('Invalid recovery bundle signature');

  // Check existing rate limit status
  if (options.enableRateLimit && isRateLimited(bundleId)) {
    const record = rateLimitStore.get(bundleId)!;
    const timeSinceLastAttempt = Date.now() - record.lastAttempt;
    const requiredWait = Math.min(
      1000 * Math.pow(2, record.attempts - 1),
      30_000
    );

    throw new Error(
      `Rate limit exceeded. Try again in ${Math.ceil((requiredWait - timeSinceLastAttempt) / 1000)}s`
    );
  }

  // Derive recovery KEK using PBKDF2 -> AES-GCM
  const salt = fromB64u(bundle.saltB64u);
  const keyMaterial = await crypto.subtle.importKey(
    'raw',
    new TextEncoder().encode(passcode),
    'PBKDF2',
    false,
    ['deriveKey']
  );
  const gcmKey = await crypto.subtle.deriveKey(
    { name: 'PBKDF2', salt, iterations: bundle.iter, hash: 'SHA-256' },
    keyMaterial,
    { name: 'AES-GCM', length: 256 },
    false,
    ['encrypt', 'decrypt']
  );

  // Decrypt DEK snapshot
  const iv = fromB64u(bundle.ivB64u);
  const ciphertext = fromB64u(bundle.ctB64u);
  const aad = recoveryAAD(ns);

  let plaintext: ArrayBuffer;
  try {
    plaintext = await crypto.subtle.decrypt(
      { name: 'AES-GCM', iv, additionalData: aad },
      gcmKey,
      ciphertext
    );

    // Reset rate limit on successful decryption
    if (options.enableRateLimit) {
      resetRateLimit(bundleId);
    }
  } catch {
    // Record failed attempt for rate limiting
    if (options.enableRateLimit) {
      const now = Date.now();
      const record = rateLimitStore.get(bundleId);
      if (record) {
        record.attempts += 1;
        record.lastAttempt = now;
      } else {
        rateLimitStore.set(bundleId, { attempts: 1, lastAttempt: now });
      }
    }
    throw new Error('Invalid recovery passcode or corrupted bundle');
  }

  // Parse recovered DEKs
  const dekSnapshot: Array<{ kid: string; wrapped: string }> = JSON.parse(
    new TextDecoder().decode(plaintext)
  );

  // Create recovery backup bundle for import
  const recoveryBackup = {
    v: 1 as const,
    createdAt: new Date().toISOString(),
    deks: dekSnapshot,
  };

  // Get count before import (if keyring is unlocked)
  let beforeCount = 0;
  try {
    const beforeExport = await keyring.exportBackup();
    beforeCount = beforeExport.deks.length;
  } catch (error) {
    if (!(error instanceof Error && error.message === 'Keyring locked')) {
      throw error;
    }
  }

  // Import DEKs from recovery bundle
  await keyring.importBackup(recoveryBackup);

  // Get count after import (if keyring is unlocked)
  let afterCount = dekSnapshot.length;
  try {
    const afterExport = await keyring.exportBackup();
    afterCount = afterExport.deks.length;
  } catch (error) {
    if (!(error instanceof Error && error.message === 'Keyring locked')) {
      throw error;
    }
  }

  return { imported: afterCount - beforeCount };
}

// Export utilities for testing and management
export const RecoveryUtils = {
  getBundleId: (bundle: RecoveryBundleV1) => {
    const { sigB64u, ...bundleForSig } = bundle;
    return getBundleId(bundleForSig);
  },
  clearRateLimit: (bundleId: string) => rateLimitStore.delete(bundleId),
  clearAllRateLimits: () => rateLimitStore.clear(),
  getRateLimitInfo: (bundleId: string) => rateLimitStore.get(bundleId),
};

/**
 * Recovery Override Creation - Phase B Task 16
 * Admin-only recovery bundle creation for beneficiary users
 */

import * as AuditApi from '../audit/api';
import { toB64u } from '../crypto/base64url';
import * as MembershipApi from '../membership/api';
import { enforcePolicy } from '../policy/engine';
import type { StorageDriver } from '../storage/types';

import type {
  CreateRecoveryOverrideArgs,
  RecoveryOverrideEnvelope,
  RecoveryOverrideContentV1,
} from './override.types';

// Canonical JSON for signature consistency
function canonicalize(obj: any): string {
  if (obj === null || typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canonicalize).join(',') + ']';
  const keys = Object.keys(obj).sort();
  return '{' + keys.map(k => `"${k}":${canonicalize(obj[k])}`).join(',') + '}';
}

// Generate ULID-like ID
function generateId(): string {
  return Date.now().toString(36) + Math.random().toString(36).slice(2, 8);
}

// AAD for AES-GCM (consistent with Task 11)
function overrideAAD(ns: string, id: string): ArrayBuffer {
  return new TextEncoder().encode(`${ns}:${id}`).buffer;
}

export async function createRecoveryOverride(
  opts: CreateRecoveryOverrideArgs & { storage?: StorageDriver }
): Promise<{ envelope: RecoveryOverrideEnvelope; id: string }> {
  const {
    ns,
    actorId,
    beneficiaryId,
    code,
    expiresAt,
    scope = 'ALL',
    sign,
    storage,
  } = opts;

  // Get membership state first for both authorization and policy
  const membership = await MembershipApi.getMembership();
  const actorRole = membership.users[actorId];
  const beneficiaryRole = membership.users[beneficiaryId];

  // Policy enforcement before authorization
  if (storage && actorRole) {
    await enforcePolicy(
      {
        ns,
        op: 'override.create',
        actorId,
        actorRole,
        targetRole: beneficiaryRole,
        nowISO: new Date().toISOString(),
      },
      storage,
      { audit: true, commitCap: true }
    );
  }

  // 1. Resolve actor role and check permissions
  await MembershipApi.assertPermission(actorId, 'RECOVERY_OVERRIDE_CREATE');

  // Policy matrix enforcement
  const OVERRIDE_POLICY: Record<string, string[]> = {
    OWNER: ['OWNER', 'ADMIN', 'MEMBER', 'VIEWER'],
    ADMIN: ['MEMBER', 'VIEWER'],
    MEMBER: [],
    VIEWER: [],
  };

  if (!beneficiaryRole) {
    throw new Error(`Beneficiary ${beneficiaryId} not found in workspace`);
  }

  const allowedTargets = actorRole ? OVERRIDE_POLICY[actorRole] || [] : [];
  if (!allowedTargets.includes(beneficiaryRole)) {
    throw new Error(
      `Access denied: ${actorRole || 'none'} cannot create override for ${beneficiaryRole}`
    );
  }

  // 2. For now, we'll use a placeholder DEK snapshot (will be passed in during integration)
  // This avoids the keyring dependency issue while maintaining the interface
  const dekSnapshot = [
    { kid: 'demo-key-1', wrapped: 'demo-wrapped-data-1' },
    { kid: 'demo-key-2', wrapped: 'demo-wrapped-data-2' },
  ];

  let filteredDeks = dekSnapshot;
  if (scope === 'ACTIVE' && dekSnapshot.length > 0) {
    // Filter to only active DEK (most recent)
    const sortedDeks = dekSnapshot.sort((a, b) => b.kid.localeCompare(a.kid));
    filteredDeks = [sortedDeks[0]];
  }

  // 3. Build manifest content
  const id = generateId();
  const salt = crypto.getRandomValues(new Uint8Array(16));

  const content: RecoveryOverrideContentV1 = {
    ns,
    id,
    beneficiaryId,
    scope,
    ...(expiresAt && { exp: expiresAt }),
    saltB64u: toB64u(salt.buffer),
  };

  // 4. Sign manifest
  const canonical = canonicalize(content);
  const messageBytes = new TextEncoder().encode(canonical);
  const signature = await sign(messageBytes);

  // 5. Encrypt DEK snapshot with code-derived session key using the same salt
  const keyMaterial = await crypto.subtle.importKey(
    'raw',
    new TextEncoder().encode(code),
    'PBKDF2',
    false,
    ['deriveKey']
  );
  const sessionKey = await crypto.subtle.deriveKey(
    { name: 'PBKDF2', salt: salt.buffer, iterations: 100_000, hash: 'SHA-256' },
    keyMaterial,
    { name: 'AES-GCM', length: 256 },
    false,
    ['encrypt']
  );

  const iv = crypto.getRandomValues(new Uint8Array(12));
  const plaintext = new TextEncoder().encode(JSON.stringify(filteredDeks));
  const aad = overrideAAD(ns, id);

  const ciphertext = await crypto.subtle.encrypt(
    { name: 'AES-GCM', iv, additionalData: aad },
    sessionKey,
    plaintext
  );

  // 6. Build envelope
  const envelope: RecoveryOverrideEnvelope = {
    v: 1,
    content,
    ctB64u: toB64u(ciphertext),
    ivB64u: toB64u(iv.buffer),
    sigB64u: signature,
  };

  // 7. Enhanced audit event with operational context
  await AuditApi.log(
    'RECOVERY_OVERRIDE_CREATED',
    {
      overrideId: id,
      beneficiaryId,
      actorRole: actorRole,
      beneficiaryRole: beneficiaryRole,
      scope,
      expiresAt,
      dekCount: filteredDeks.length,
      namespace: ns,
      createdAt: new Date().toISOString(),
    },
    actorId
  );

  return { envelope, id };
}

/**
 * BYOC implementation with high-level helpers
 */

import {
  type Manifest,
  createManifest,
  verifyManifest,
} from '../domain/pack/manifest';
import { type Sparkpack } from '../domain/pack/types';

import { type BYOC, type BlobRef, type PutResult } from './byocTypes';

/**
 * Simple in-memory BYOC for testing
 */
export class MemoryBYOC implements BYOC {
  private storage = new Map<string, ArrayBuffer>();
  private etags = new Map<string, string>();

  async putJson(path: string, obj: unknown): Promise<PutResult> {
    const json = JSON.stringify(obj);
    const data = new TextEncoder().encode(json);
    return this.putBlob(path, data.buffer);
  }

  async getJson<T>(path: string): Promise<T | null> {
    const data = await this.getBlob(path);
    if (!data) return null;
    const json = new TextDecoder().decode(data);
    return JSON.parse(json) as T;
  }

  async putBlob(path: string, data: ArrayBuffer): Promise<PutResult> {
    this.storage.set(path, [...data]); // copy
    const etag = Date.now().toString(36) + Math.random().toString(36);
    this.etags.set(path, etag);
    return {
      ref: { url: `memory://${path}` },
      etag,
    };
  }

  async getBlob(path: string): Promise<ArrayBuffer | null> {
    const data = this.storage.get(path);
    return data ? [...data] : null; // copy
  }

  async head(path: string): Promise<{ etag?: string; size?: number } | null> {
    const data = this.storage.get(path);
    if (!data) return null;
    const etag = this.etags.get(path);
    return {
      ...(etag && { etag }),
      size: data.byteLength,
    };
  }
}

/**
 * Upload sparkpack with manifest
 */
export async function uploadPack(
  byoc: BYOC,
  pack: Sparkpack,
  privateKey: CryptoKey,
  publicKey?: CryptoKey
): Promise<{ manifest: Manifest; eventsRef: BlobRef }> {
  // Convert events to JSONL for hashing
  const eventsJsonl = pack.events.map(e => JSON.stringify(e)).join('\n');
  const eventsData = new TextEncoder().encode(eventsJsonl);

  // Compute hash for consistent ID
  const hashBuffer = await crypto.subtle.digest('SHA-256', eventsData);
  const computedHash = btoa(String.fromCharCode(...new Uint8Array(hashBuffer)))
    .replaceAll('+', '-')
    .replaceAll('/', '_')
    .replaceAll('=', '');

  // Upload events with computed hash as ID
  const eventsResult = await byoc.putBlob(
    `packs/${computedHash}/events.jsonl`,
    eventsData.buffer
  );

  // Create and upload manifest
  const manifest = await createManifest(pack, privateKey, publicKey);
  await byoc.putJson(`packs/${computedHash}/manifest.json`, manifest);

  return {
    manifest,
    eventsRef: eventsResult.ref,
  };
}

/**
 * Download and verify sparkpack
 */
export async function downloadPack(
  byoc: BYOC,
  manifestPath: string
): Promise<Sparkpack | null> {
  try {
    // Download manifest
    const manifest = await byoc.getJson<Manifest>(manifestPath);
    if (!manifest) return null;

    // Download events using manifest hash as ID
    const packHash = manifest.content.eventsHash;
    const eventsData = await byoc.getBlob(`packs/${packHash}/events.jsonl`);
    if (!eventsData) return null;

    // Parse JSONL to events array
    const eventsJsonl = new TextDecoder().decode(eventsData);
    const events = eventsJsonl
      .split('\n')
      .filter(line => line.trim())
      .map(line => JSON.parse(line));
    const pack = { meta: manifest.content.meta, events };

    // Verify signature and hash
    const isValid = await verifyManifest(manifest, pack);
    if (!isValid) return null;

    return pack;
  } catch {
    return null;
  }
}

/**
 * Revocation Propagation Sync - Phase B Task 13
 * Headless sync of revocations across devices via E2EE transport
 */

import { toB64u, fromB64u } from '../../crypto/base64url';
import type { StorageDriver } from '../../storage/types';

import type {
  RevRecord,
  RevType,
  RevTransport,
  RevPlan,
  RevResult,
} from './types';

let storage: StorageDriver;
let namespace: string;
let transport: RevTransport;
let trustedAdmins: string[];

export function configureRevSync(
  storageDriver: StorageDriver,
  ns: string,
  transportImpl: RevTransport,
  admins: string[]
): void {
  storage = storageDriver;
  namespace = ns;
  transport = transportImpl;
  trustedAdmins = admins;
}

// Canonical JSON + ULID-like ID + signature verification
function canon(obj: any): string {
  if (obj === null || typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canon).join(',') + ']';
  const keys = Object.keys(obj).sort();
  return '{' + keys.map(k => `"${k}":${canon(obj[k])}`).join(',') + '}';
}

function generateId(): string {
  return Date.now().toString(36) + Math.random().toString(36).slice(2, 8);
}

async function verifySignature(record: RevRecord): Promise<boolean> {
  try {
    const { issuer, ...recordWithoutSig } = record;
    const { sigB64u, ...issuerWithoutSig } = issuer;
    const canonical = canon({ ...recordWithoutSig, issuer: issuerWithoutSig });

    const pubKey = await crypto.subtle.importKey(
      'spki',
      fromB64u(issuer.pubB64u),
      { name: 'Ed25519' },
      false,
      ['verify']
    );
    return await crypto.subtle.verify(
      'Ed25519',
      pubKey,
      fromB64u(sigB64u),
      new TextEncoder().encode(canonical)
    );
  } catch {
    return false;
  }
}

// Sync state management
async function getSyncState(): Promise<{ since?: string }> {
  try {
    const data = await storage.getItem(`rev:${namespace}:__sync_state__`);
    return data ? JSON.parse(data) : {};
  } catch {
    return {};
  }
}

async function setSyncState(state: { since?: string }): Promise<void> {
  await storage.setItem(
    `rev:${namespace}:__sync_state__`,
    JSON.stringify(state)
  );
}

// Apply revocation (placeholder for Task 10 integration)
async function applyRevocation(record: RevRecord): Promise<boolean> {
  try {
    // Integration points with existing revocation registry
    switch (record.type) {
      case 'INVITE_REVOKED': {
        console.log(`Revoked invite: ${record.subject}`);
        break;
      }
      case 'SIGNER_REVOKED': {
        console.log(`Revoked signer: ${record.subject}`);
        break;
      }
      case 'RECOVERY_REVOKED': {
        console.log(`Revoked recovery bundle: ${record.subject}`);
        break;
      }
    }
    return true;
  } catch {
    return false;
  }
}

export async function planRevSync(): Promise<RevPlan> {
  if (!storage || !transport) {
    throw new Error('RevSync not configured. Call configureRevSync() first.');
  }

  const state = await getSyncState();
  const result = await transport.list(namespace, state.since);

  return {
    pullKeys: result.keys,
    ...(result.nextSince && { since: result.nextSince }),
  };
}

export async function runRevSync(plan?: RevPlan): Promise<RevResult> {
  if (!storage || !transport) {
    throw new Error('RevSync not configured. Call configureRevSync() first.');
  }

  const syncPlan = plan || (await planRevSync());
  const result: RevResult = {
    applied: 0,
    pushed: 0,
    errors: [],
    completed: false,
  };

  try {
    // Pull and apply remote records with dedup
    const seenHashes = new Set<string>();

    for (const key of syncPlan.pullKeys) {
      try {
        const data = await transport.get(key);
        if (!data) continue;

        const record: RevRecord = JSON.parse(data);
        const recordHash = canon(record);
        if (seenHashes.has(recordHash)) continue;
        seenHashes.add(recordHash);

        // Clock skew warning (non-fatal)
        const recordTime = new Date(record.ts).getTime();
        if (Math.abs(recordTime - Date.now()) > 5 * 60 * 1000) {
          console.warn(`Clock skew detected: record ${record.id}`);
        }

        // Verify signature and trust
        if (!trustedAdmins.includes(record.issuer.pubB64u)) {
          result.errors.push(`Untrusted issuer: ${record.issuer.pubB64u}`);
          continue;
        }

        if (!(await verifySignature(record))) {
          result.errors.push(`Invalid signature for record: ${record.id}`);
          continue;
        }

        if (await applyRevocation(record)) {
          result.applied++;
        } else {
          result.errors.push(`Failed to apply: ${record.id}`);
        }
      } catch (error) {
        result.errors.push(
          `Error processing ${key}: ${error instanceof Error ? error.message : 'Unknown'}`
        );
      }
    }

    // Push local outbox in batches
    const outboxKeys = await storage.listKeys(`rev:${namespace}:out:`);
    for (let i = 0; i < outboxKeys.length; i += 100) {
      const batch = outboxKeys.slice(i, i + 100);

      for (const outboxKey of batch) {
        try {
          const data = await storage.getItem(outboxKey);
          if (!data) continue;

          const record: RevRecord = JSON.parse(data);
          await transport.put(
            `rev:${namespace}:r:${record.ts}:${record.id}`,
            data,
            record.ts
          );
          await storage.removeItem(outboxKey);
          result.pushed++;
        } catch (error) {
          result.errors.push(
            `Push error ${outboxKey}: ${error instanceof Error ? error.message : 'Unknown'}`
          );
        }
      }
    }

    // Update sync state with monotonicity check
    const currentState = await getSyncState();
    if (
      syncPlan.since &&
      (!currentState.since || syncPlan.since > currentState.since)
    ) {
      await setSyncState({ since: syncPlan.since });
    }

    result.completed = true;
  } catch (error) {
    result.errors.push(
      `Sync failed: ${error instanceof Error ? error.message : 'Unknown'}`
    );
  }

  return result;
}

export async function revokeAndQueue(
  type: RevType,
  subject: string,
  reason?: string
): Promise<RevRecord> {
  if (!storage) {
    throw new Error('RevSync not configured. Call configureRevSync() first.');
  }

  const id = generateId();
  const ts = new Date().toISOString();
  const adminPubB64u = trustedAdmins[0] || 'placeholder-admin-key';

  const recordWithoutSig = {
    v: 1 as const,
    id,
    ts,
    type,
    subject,
    ...(reason && { reason }),
    issuer: { pubB64u: adminPubB64u },
  };

  // Placeholder signature (in practice, use actual admin private key)
  const canonical = canon(recordWithoutSig);
  const sigBytes = new TextEncoder().encode(
    'placeholder-sig-' + canonical.slice(0, 16)
  );
  const sigB64u = toB64u(sigBytes.buffer);

  const record: RevRecord = {
    ...recordWithoutSig,
    issuer: { pubB64u: adminPubB64u, sigB64u },
  };

  // Queue for next sync
  await storage.setItem(`rev:${namespace}:out:${id}`, JSON.stringify(record));
  return record;
}

