  return index.search(query);
};

export const selectToday = (state: TaskStore): Task[] =>
  Object.values(state.byId).filter(isToday).sort(compareTasks);

export const selectLater = (state: TaskStore): Task[] =>
  Object.values(state.byId).filter(isLater).sort(compareTasks);

export const selectDone = (state: TaskStore): Task[] =>
  Object.values(state.byId)
    .filter(isDone)
    .sort(
      (a, b) =>
        new Date(b.updatedAt).getTime() - new Date(a.updatedAt).getTime()
    );

export const selectQuery = (
  state: TaskStore,
  q: Query,
  page?: Page
): Result<Task> => runQuery(Object.values(state.byId), q, page);

function generateId(): string {
  return Date.now().toString(36) + Math.random().toString(36).slice(2);
}

export const useTaskStore = create<TaskStore>((set, get) => ({
  byId: {},
  undoStack: [],
  redoStack: [],

  addTask: (input: unknown) => {
    const validatedInput = CreateTaskInputSchema.parse(input);
    const id = generateId();
    const timestamp = new Date().toISOString();

    const event: TaskEvent = {
      type: 'TASK_CREATED',
      timestamp,
      payload: {
        id,
        ...validatedInput,
        tags: validatedInput.tags || [],
      },
    };

    const undoEvent = deriveUndo(event);
    const newTask: Task = {
      ...validatedInput,
      id,
      tags: validatedInput.tags || [],
      createdAt: timestamp,
      updatedAt: timestamp,
    };

    if (undoEvent) {
      const undoEntry: UndoEntry = { do: event, undo: undoEvent };
      const state = get();
      const newUndoStack = [...state.undoStack, undoEntry].slice(-50); // Cap at 50
      set(state => ({
        byId: { ...state.byId, [id]: newTask },
        undoStack: newUndoStack,
        redoStack: [],
      }));
    } else {
      set(state => ({
        byId: { ...state.byId, [id]: newTask },
      }));
    }

    appendEvent(event);

    // Update search index
    if (searchIndexInstance) {
      searchIndexInstance.updateFromEvent(event);
    }
  },

  updateTask: (id: string, patch: Partial<Task>) => {
    const existingTask = get().byId[id];
    if (!existingTask) return;

    const timestamp = new Date().toISOString();
    const pendingUndoEntries: UndoEntry[] = [];

    // Handle status changes separately with TASK_MOVED
    if (patch.status && patch.status !== existingTask.status) {
      const moveEvent: TaskEvent = {
        type: 'TASK_MOVED',
        timestamp,
        payload: {
          id,
          fromStatus: existingTask.status,
          toStatus: patch.status,
        },
      };

      const undoEvent = deriveUndo(moveEvent, existingTask);
      if (undoEvent) {
        const undoEntry: UndoEntry = { do: moveEvent, undo: undoEvent };
        pendingUndoEntries.push(undoEntry);
      }

      appendEvent(moveEvent);
    }

    // Handle other changes with TASK_UPDATED
    const { status, id: _, createdAt, updatedAt, ...changes } = patch;
    const filteredChanges = Object.fromEntries(
      Object.entries(changes).filter(
        ([key, value]) =>
          value !== undefined &&
          ['title', 'dueDate', 'tags', 'notes'].includes(key)
      )
    );

    if (Object.keys(filteredChanges).length > 0) {
      const updateEvent: TaskEvent = {
        type: 'TASK_UPDATED',
        timestamp,
        payload: { id, changes: filteredChanges },
      };

      const undoEvent = deriveUndo(updateEvent, existingTask);
      if (undoEvent) {
        const undoEntry: UndoEntry = { do: updateEvent, undo: undoEvent };
        pendingUndoEntries.push(undoEntry);
      }

      appendEvent(updateEvent);

      // Update search index
      if (searchIndexInstance) {
        searchIndexInstance.updateFromEvent(updateEvent);
      }
    }

    const updatedTask = { ...existingTask, ...patch, updatedAt: timestamp };

    // Apply all changes in a single set call
    if (pendingUndoEntries.length > 0) {
      const state = get();
      const newUndoStack = [...state.undoStack, ...pendingUndoEntries].slice(
        -50
      );
      set(state => ({
        byId: { ...state.byId, [id]: updatedTask },
        undoStack: newUndoStack,
        redoStack: [],
      }));
    } else {
      set(state => ({
        byId: { ...state.byId, [id]: updatedTask },
      }));
    }
  },

  moveTask: (id: TaskId, status: TaskStatus) => {
    const currentTask = get().byId[id];
    if (!currentTask) return;

    const timestamp = new Date().toISOString();
    const event: TaskEvent = {
      type: 'TASK_MOVED',
      timestamp,
      payload: {
        id,
        fromStatus: currentTask.status,
        toStatus: status,
      },
    };

    const undoEvent = deriveUndo(event, currentTask);
    appendEvent(event);

    // Update search index
    if (searchIndexInstance) {
      searchIndexInstance.updateFromEvent(event);
    }

    if (undoEvent) {
      const undoEntry: UndoEntry = { do: event, undo: undoEvent };
      const state = get();
      const newUndoStack = [...state.undoStack, undoEntry].slice(-50);
      set(state => ({
        byId: {
          ...state.byId,
          [id]: { ...state.byId[id], status, updatedAt: timestamp },
        },
        undoStack: newUndoStack,
        redoStack: [],
      }));
    } else {
      set(state => ({
        byId: {
          ...state.byId,
          [id]: { ...state.byId[id], status, updatedAt: timestamp },
        },
      }));
    }
  },

  completeTask: (id: TaskId) => {
    const currentTask = get().byId[id];
    if (!currentTask) return;

    const timestamp = new Date().toISOString();
    const event: TaskEvent = {
      type: 'TASK_COMPLETED',
      timestamp,
      payload: { id },
    };

    const undoEvent = deriveUndo(event, currentTask);
    appendEvent(event);

    // Update search index
    if (searchIndexInstance) {
      searchIndexInstance.updateFromEvent(event);
    }

    if (undoEvent) {
      const undoEntry: UndoEntry = { do: event, undo: undoEvent };
      const state = get();
      const newUndoStack = [...state.undoStack, undoEntry].slice(-50);
      set(state => ({
        byId: {
          ...state.byId,
          [id]: { ...state.byId[id], status: 'DONE', updatedAt: timestamp },
        },
        undoStack: newUndoStack,
        redoStack: [],
      }));
    } else {
      set(state => ({
        byId: {
          ...state.byId,
          [id]: { ...state.byId[id], status: 'DONE', updatedAt: timestamp },
        },
      }));
    }
  },

  snoozeTask: (id: TaskId, until: string) => {
    const currentTask = get().byId[id];
    if (!currentTask) return;

    const timestamp = new Date().toISOString();
    const event: TaskEvent = {
      type: 'TASK_SNOOZED',
      timestamp,
      payload: { id, snoozeUntil: until },
    };

    const undoEvent = deriveUndo(event, currentTask);
    appendEvent(event);

    // Update search index
    if (searchIndexInstance) {
      searchIndexInstance.updateFromEvent(event);
    }

    if (undoEvent) {
      const undoEntry: UndoEntry = { do: event, undo: undoEvent };
      const state = get();
      const newUndoStack = [...state.undoStack, undoEntry].slice(-50);
      set(state => ({
        byId: {
          ...state.byId,
          [id]: { ...state.byId[id], snoozeUntil: until, updatedAt: timestamp },
        },
        undoStack: newUndoStack,
        redoStack: [],
      }));
    } else {
      set(state => ({
        byId: {
          ...state.byId,
          [id]: { ...state.byId[id], snoozeUntil: until, updatedAt: timestamp },
        },
      }));
    }
  },

  quickAdd: (line: string, now?: Date) => {
    const quickAddResult = parseQuickAdd(line, now);
    const input = toCreateTaskInput(quickAddResult);
    const validatedInput = CreateTaskInputSchema.parse(input);
    const id = generateId();
    const timestamp = new Date().toISOString();

    const event: TaskEvent = {
      type: 'TASK_CREATED',
      timestamp,
      payload: {
        id,
        ...validatedInput,
        tags: validatedInput.tags || [],
      },
    };

    const undoEvent = deriveUndo(event);
    const newTask: Task = {
      ...validatedInput,
      id,
      tags: validatedInput.tags || [],
      createdAt: timestamp,
      updatedAt: timestamp,
    };

    if (undoEvent) {
      const undoEntry: UndoEntry = { do: event, undo: undoEvent };
      const state = get();
      const newUndoStack = [...state.undoStack, undoEntry].slice(-50);
      set(state => ({
        byId: { ...state.byId, [id]: newTask },
        undoStack: newUndoStack,
        redoStack: [],
      }));
    } else {
      set(state => ({
        byId: { ...state.byId, [id]: newTask },
      }));
    }

    appendEvent(event);

    // Update search index
    if (searchIndexInstance) {
      searchIndexInstance.updateFromEvent(event);
    }

    return { id };
  },

  undo: () => {
    const state = get();
    if (state.undoStack.length === 0) return;

    const undoEntry = state.undoStack.at(-1);
    const newUndoStack = state.undoStack.slice(0, -1);
    const newRedoStack = [...state.redoStack, undoEntry].slice(-50);

    // Apply the undo event to the current state
    const currentState = get().byId;
    const event = undoEntry.undo;

    switch (event.type) {
      case 'TASK_CREATED': {
        const { id } = event.payload;
        const { [id]: removedTask, ...remainingTasks } = currentState;
        set(() => ({
          byId: remainingTasks,
          undoStack: newUndoStack,
          redoStack: newRedoStack,
        }));
        break;
      }
      case 'TASK_UPDATED': {
        const { id, changes } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          const updatedTask = {
            ...existingTask,
            ...Object.fromEntries(
              Object.entries(changes).filter(
                ([_, value]) => value !== undefined
              )
            ),
            updatedAt: event.timestamp,
          };
          set(() => ({
            byId: { ...currentState, [id]: updatedTask },
            undoStack: newUndoStack,
            redoStack: newRedoStack,
          }));
        }
        break;
      }
      case 'TASK_MOVED': {
        const { id, toStatus } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          // Special case: moving to ARCHIVED during undo means deletion (task creation reversal)
          if (toStatus === 'ARCHIVED') {
            const { [id]: removedTask, ...remainingTasks } = currentState;
            set(() => ({
              byId: remainingTasks,
              undoStack: newUndoStack,
              redoStack: newRedoStack,
            }));
          } else {
            const updatedTask = {
              ...existingTask,
              status: toStatus,
              updatedAt: event.timestamp,
            };
            set(() => ({
              byId: { ...currentState, [id]: updatedTask },
              undoStack: newUndoStack,
              redoStack: newRedoStack,
            }));
          }
        }
        break;
      }
      case 'TASK_COMPLETED': {
        // This reverses completion, so we need to restore the previous status
        const { id } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          const originalEvent = undoEntry.do;
          let previousStatus: TaskStatus = 'TODAY';

          if (originalEvent.type === 'TASK_COMPLETED') {
            // Find the previous status from task history or default to TODAY
            previousStatus = 'TODAY';
          }

          const updatedTask = {
            ...existingTask,
            status: previousStatus,
            updatedAt: event.timestamp,
          };
          set(() => ({
            byId: { ...currentState, [id]: updatedTask },
            undoStack: newUndoStack,
            redoStack: newRedoStack,
          }));
        }
        break;
      }
      case 'TASK_SNOOZED': {
        const { id } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          const { snoozeUntil, ...taskWithoutSnooze } = existingTask;
          const updatedTask = {
            ...taskWithoutSnooze,
            updatedAt: event.timestamp,
          };
          set(() => ({
            byId: { ...currentState, [id]: updatedTask },
            undoStack: newUndoStack,
            redoStack: newRedoStack,
          }));
        }
        break;
      }
    }
  },

  redo: () => {
    const state = get();
    if (state.redoStack.length === 0) return;

    const redoEntry = state.redoStack.at(-1);
    const newRedoStack = state.redoStack.slice(0, -1);
    const newUndoStack = [...state.undoStack, redoEntry].slice(-50);

    // Apply the redo event (which is the original 'do' event)
    const currentState = get().byId;
    const event = redoEntry.do;

    switch (event.type) {
      case 'TASK_CREATED': {
        const { id, ...taskData } = event.payload;
        const newTask: Task = {
          ...taskData,
          id,
          createdAt: event.timestamp,
          updatedAt: event.timestamp,
        };
        set(() => ({
          byId: { ...currentState, [id]: newTask },
          undoStack: newUndoStack,
          redoStack: newRedoStack,
        }));
        break;
      }
      case 'TASK_UPDATED': {
        const { id, changes } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          const updatedTask = {
            ...existingTask,
            ...Object.fromEntries(
              Object.entries(changes).filter(
                ([_, value]) => value !== undefined
              )
            ),
            updatedAt: event.timestamp,
          };
          set(() => ({
            byId: { ...currentState, [id]: updatedTask },
            undoStack: newUndoStack,
            redoStack: newRedoStack,
          }));
        }
        break;
      }
      case 'TASK_MOVED': {
        const { id, toStatus } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          const updatedTask = {
            ...existingTask,
            status: toStatus,
            updatedAt: event.timestamp,
          };
          set(() => ({
            byId: { ...currentState, [id]: updatedTask },
            undoStack: newUndoStack,
            redoStack: newRedoStack,
          }));
        }
        break;
      }
      case 'TASK_COMPLETED': {
        const { id } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          const updatedTask = {
            ...existingTask,
            status: 'DONE' as const,
            updatedAt: event.timestamp,
          };
          set(() => ({
            byId: { ...currentState, [id]: updatedTask },
            undoStack: newUndoStack,
            redoStack: newRedoStack,
          }));
        }
        break;
      }
      case 'TASK_SNOOZED': {
        const { id, snoozeUntil } = event.payload;
        const existingTask = currentState[id];
        if (existingTask) {
          const updatedTask = {
            ...existingTask,
            snoozeUntil,
            updatedAt: event.timestamp,
          };
          set(() => ({
            byId: { ...currentState, [id]: updatedTask },
            undoStack: newUndoStack,
            redoStack: newRedoStack,
          }));
        }
        break;
      }
    }
  },

  hydrate: () => {
    const snapshot = loadSnapshot();

    if (snapshot) {
      // Validate base snapshot first (before tail)
      const baseHash = computeStateHash(snapshot.tasks);
      if (baseHash !== snapshot.meta.stateHash) {
        deleteSnapshot();
        const allEvents = loadEvents();
        const fullTasks = reduce(allEvents);

        // Enforce invariants
        for (const task of Object.values(fullTasks)) {
          if (new Date(task.createdAt) > new Date(task.updatedAt)) {
            task.updatedAt = task.createdAt;
          }

          if (task.status === 'DONE' && !task.updatedAt) {
            task.updatedAt = task.createdAt;
          }
        }

        set({ byId: fullTasks });

        // Rebuild search index after hydration
        if (searchIndexInstance) {
          searchIndexInstance.build(Object.values(fullTasks));
        }
        return;
      }

      // Snapshot seems valid ‚Äî continue with tail
      const events = loadEvents();

      // Determine tail start index. If the log has been compacted after the snapshot,
      // the current log length may be < baseEventCount (we trimmed the head).
      // In that case, treat the entire current log as the tail.
      const tailStart =
        events.length < snapshot.meta.baseEventCount
          ? 0
          : snapshot.meta.baseEventCount;
      const tailEvents = events.slice(tailStart);

      // Start with snapshot state
      let finalTasks = { ...snapshot.tasks };

      if (tailEvents.length > 0) {
        // Apply tail events starting from snapshot state
        finalTasks = reduce(tailEvents, snapshot.tasks);
      }

      // Enforce invariants
      for (const task of Object.values(finalTasks)) {
        if (new Date(task.createdAt) > new Date(task.updatedAt)) {
          task.updatedAt = task.createdAt;
        }

        if (task.status === 'DONE' && !task.updatedAt) {
          task.updatedAt = task.createdAt;
        }
      }

      set({ byId: finalTasks });
    } else {
      // Fallback to full event log reduction
      const events = loadEvents();
      const tasks = reduce(events);

      // Enforce invariants
      for (const task of Object.values(tasks)) {
        if (new Date(task.createdAt) > new Date(task.updatedAt)) {
          task.updatedAt = task.createdAt;
        }

        if (task.status === 'DONE' && !task.updatedAt) {
          task.updatedAt = task.createdAt;
        }
      }

      set({ byId: tasks });
    }

    // Rebuild search index after hydration
    if (searchIndexInstance) {
      searchIndexInstance.build(Object.values(get().byId));
    }
  },

  // New: async hydration for encrypted/remote-backed storage
  hydrateAsync: async () => {
    // Try snapshot-first using async storage
    const snap = await loadSnapshotAsync();
    if (snap) {
      const allEvents = await loadEventsAsync();
      const base = snap.meta.baseEventCount ?? 0;
      const tail = base <= allEvents.length ? allEvents.slice(base) : [];
      const tasks = reduceAsyncReducer(tail, snap.tasks);

      // Enforce invariants (same as sync path)
      for (const t of Object.values(tasks)) {
        if (new Date(t.createdAt) > new Date(t.updatedAt))
          t.updatedAt = t.createdAt;
        if (t.status === 'DONE' && !t.updatedAt) t.updatedAt = t.createdAt;
      }
      set({ byId: tasks });
    } else {
      // Fallback: reduce all events
      const events = await loadEventsAsync();
      const tasks = reduceAsyncReducer(events);
      for (const t of Object.values(tasks)) {
        if (new Date(t.createdAt) > new Date(t.updatedAt))
          t.updatedAt = t.createdAt;
        if (t.status === 'DONE' && !t.updatedAt) t.updatedAt = t.createdAt;
      }
      set({ byId: tasks });
    }

    // Rebuild search index after hydration
    if (searchIndexInstance) {
      const state = get();
      searchIndexInstance.build(Object.values(state.byId));
    }
  },
}));

// Optional: small helper to wire the async storage once (used by E2EE bootstrap)
export function configureAsyncEventlogStorage(
  driver: import('../domain/task/eventlog.async').StorageDriver
) {
  setAsyncEventlogStorage(driver);
}

/**
 * Attested Sparkpacks - Phase B Task 18 & 19
 * Ed25519 attestation & verification with signer lifecycle management
 */

import { log } from '../audit/api';
import { toB64u, fromB64u } from '../crypto/base64url';
import type { Sparkpack, SparkpackMeta } from '../domain/pack/types';
import { listTrustAnchors } from '../federation/registry';
import { checkCrossOrgPolicy } from '../policy/engine';

import { listSigners, type SignerRecord } from './signer-registry';

export interface AttestedPack {
  v: 1;
  manifest: PackManifestV1;
  att: Attestation;
}

export interface DualAttestedPack {
  v: 1;
  manifest: PackManifestV1;
  att: [Attestation, Attestation]; // new + old
}

export interface Attestation {
  alg: 'Ed25519';
  signer: string; // base64url SPKI
  sig: string; // base64url over canonical(manifest)
  ts: string; // ISO timestamp
  kid?: string; // Task 19: key identifier
}

export interface PackManifestV1 {
  content: {
    eventsHash: string;
    eventsCount: number;
  };
  bytes: number;
  meta: SparkpackMeta;
}

export interface TrustOptions {
  allowUnsigned?: boolean;
  allowedSigners?: string[];
  ns?: string; // Task 19: namespace for registry lookup
  graceSecs?: number; // Task 19: grace period for expired signers
  operation?: string; // Task 20: operation for policy gating (e.g., 'sync.import')
}

export type VerifyResult = { ok: true } | { ok: false; reason: string };

// Canonical JSON for stable signatures
function canonicalize(obj: any): string {
  if (obj === null || typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canonicalize).join(',') + ']';
  const keys = Object.keys(obj).sort();
  return '{' + keys.map(k => `"${k}":${canonicalize(obj[k])}`).join(',') + '}';
}

// Convert Sparkpack to attestable manifest
function packToManifest(pack: Sparkpack): PackManifestV1 {
  const packStr = JSON.stringify(pack);
  return {
    content: {
      eventsHash: pack.meta.eventsHash,
      eventsCount: pack.meta.eventsCount,
    },
    bytes: packStr.length,
    meta: pack.meta,
  };
}

export async function attestPack(
  pack: Sparkpack | PackManifestV1,
  signer: CryptoKey | CryptoKeyPair,
  opts?: { dualSignUntil?: string; ns?: string }
): Promise<AttestedPack | DualAttestedPack> {
  // For backward compatibility - if pack is Sparkpack, convert to manifest
  const manifest = 'meta' in pack ? packToManifest(pack) : pack;

  let activeSigner: SignerRecord | null = null;
  let retiredSigner: SignerRecord | null = null;

  // If namespace provided, use registry-based signing
  if (opts?.ns) {
    try {
      const signers = await listSigners(opts.ns);
      activeSigner = signers.find(s => s.status === 'ACTIVE') || null;

      // Check for dual-sign opportunity
      if (opts.dualSignUntil && new Date() < new Date(opts.dualSignUntil)) {
        // Find most recent RETIRED signer within overlap window
        const retiredCandidates = signers
          .filter(
            s =>
              s.status === 'RETIRED' &&
              (!s.expiresAt || new Date() < new Date(s.expiresAt))
          )
          .sort(
            (a, b) =>
              new Date(b.createdAt).getTime() - new Date(a.createdAt).getTime()
          );

        retiredSigner = retiredCandidates[0] || null;
      }
    } catch {
      // Registry not available - fallback to direct signing
    }
  }

  // Create primary attestation
  const primaryAtt = await createAttestation(
    manifest,
    signer,
    activeSigner?.kid
  );

  // Check if dual-signing is needed and possible
  if (retiredSigner && opts?.dualSignUntil) {
    const secondaryAtt = await createAttestation(
      manifest,
      signer,
      retiredSigner.kid
    );
    await log('PACK_DUAL_SIGNED', {
      ns: opts.ns,
      active: activeSigner?.kid,
      retired: retiredSigner.kid,
    });

    return {
      v: 1,
      manifest,
      att: [primaryAtt, secondaryAtt],
    };
  }

  return {
    v: 1,
    manifest,
    att: primaryAtt,
  };
}

async function createAttestation(
  manifest: PackManifestV1,
  signer: CryptoKey | CryptoKeyPair,
  kid?: string
): Promise<Attestation> {
  const canonical = canonicalize(manifest);
  const messageBytes = new TextEncoder().encode(canonical);

  // Handle both CryptoKey and CryptoKeyPair
  const privateKey = 'privateKey' in signer ? signer.privateKey : signer;
  const publicKey =
    'publicKey' in signer
      ? signer.publicKey
      : await crypto.subtle.importKey(
          'spki',
          await crypto.subtle.exportKey('spki', signer),
          { name: 'Ed25519' },
          false,
          ['verify']
        );

  const signature = await crypto.subtle.sign(
    'Ed25519',
    privateKey,
    messageBytes
  );
  const signerSpki = await crypto.subtle.exportKey('spki', publicKey);

  return {
    alg: 'Ed25519',
    signer: toB64u(signerSpki),
    sig: toB64u(signature),
    ts: new Date().toISOString(),
    ...(kid && { kid }),
  };
}

export async function verifyPackAttestation(
  attested: AttestedPack | DualAttestedPack,
  trust: TrustOptions
): Promise<VerifyResult> {
  const { ns, allowUnsigned = false, graceSecs = 0, allowedSigners } = trust;
  const nowISO = new Date().toISOString();

  // Legacy unsigned pack handling
  if (!attested.att) {
    return allowUnsigned ? { ok: true } : { ok: false, reason: 'unsigned' };
  }

  // Handle dual-attested packs
  const attestations = Array.isArray(attested.att)
    ? attested.att
    : [attested.att];

  // If no attestations, handle as unsigned
  if (attestations.length === 0) {
    return allowUnsigned ? { ok: true } : { ok: false, reason: 'unsigned' };
  }

  // Registry-based verification with federation support
  if (ns) {
    try {
      const signers = await listSigners(ns);
      const anchors = await listTrustAnchors(ns);
      const localByKid = new Map(signers.map(r => [r.kid, r]));
      const federatedByPub = new Map(anchors.map(a => [a.pubB64u, a]));

      for (const att of attestations) {
        // Check local signer registry first (Task 19) - requires kid
        if (att.kid) {
          const localRec = localByKid.get(att.kid);
          if (localRec) {
            // Check local signer status before verifying signature
            if (localRec.status === 'REVOKED') {
              await log('ATTEST_VERIFY_DENY', {
                ns,
                kid: att.kid,
                reason: 'revoked',
              });
              return { ok: false, reason: 'revoked' };
            }

            // Verify signature first
            if (!(await verifySingleSignature(att, attested.manifest)))
              continue;

            // Check status allows verification
            if (statusAllows(localRec, nowISO, graceSecs)) {
              await log('ATTEST_VERIFY_ALLOW', { ns, kid: att.kid });
              return { ok: true };
            } else if (localRec.status === 'RETIRED') {
              await log('ATTEST_VERIFY_DENY', {
                ns,
                kid: att.kid,
                reason: 'expired beyond grace',
              });
              return {
                ok: false,
                reason: `Signer ${att.kid} expired beyond grace period`,
              };
            }
            continue;
          }
        }

        // Check federated trust anchors (Task 20) - by signer public key (works with or without kid)
        const federatedAnchor = federatedByPub.get(att.signer);
        if (federatedAnchor) {
          // Check anchor status before verifying signature
          if (federatedAnchor.status === 'REVOKED') {
            await log('ATTEST_VERIFY_CROSS_ORG_DENY', {
              ns,
              kid: att.kid,
              orgId: federatedAnchor.orgId,
              reason: 'federated_revoked',
            });
            return { ok: false, reason: 'federated_revoked' };
          }

          // Verify signature
          if (await verifySingleSignature(att, attested.manifest)) {
            // Check cross-org policy (Task 20)
            const policyCheck = await checkCrossOrgPolicy(
              ns,
              federatedAnchor.orgId,
              trust.operation
            );
            if (!policyCheck.allowed) {
              await log('ATTEST_VERIFY_CROSS_ORG_DENY', {
                ns,
                kid: att.kid,
                orgId: federatedAnchor.orgId,
                reason: policyCheck.reason,
              });
              return {
                ok: false,
                reason: policyCheck.reason || 'cross_org_policy_denied',
              };
            }

            await log('ATTEST_VERIFY_CROSS_ORG_ALLOW', {
              ns,
              kid: att.kid,
              orgId: federatedAnchor.orgId,
            });
            return { ok: true };
          }
        }

        // Handle legacy/unsigned (no kid and not federated)
        if (!att.kid && allowUnsigned) {
          await log('ATTEST_VERIFY_ALLOW', {
            ns,
            reason: 'unsigned_allowed',
          });
          return { ok: true };
        }
        // Continue to check other attestations before failing
      }

      // Check if any attestation had no kid (legacy in registry mode)
      const hasLegacy = attestations.some(att => !att.kid);
      if (hasLegacy) {
        await log('ATTEST_VERIFY_DENY', {
          ns,
          reason: 'legacy_pack_without_kid',
        });
        return { ok: false, reason: 'Legacy pack without kid' };
      }

      await log('ATTEST_VERIFY_DENY', { ns, reason: 'no_valid_attestation' });
      return { ok: false, reason: 'no_valid_attestation' };
    } catch (error) {
      return { ok: false, reason: `Registry error: ${error}` };
    }
  }

  // Legacy allowlist verification (Task 18 compatibility)
  for (const att of attestations) {
    if (allowedSigners && !allowedSigners.includes(att.signer)) {
      continue;
    }

    if (await verifySingleSignature(att, attested.manifest)) {
      return { ok: true };
    }
  }

  // Check if we had unsigned attestations but no allowUnsigned
  const hasUnsigned = attestations.some(att => !att.kid);
  if (hasUnsigned && !allowUnsigned) {
    await log('ATTEST_VERIFY_DENY', { ns, reason: 'unsigned_not_allowed' });
    return { ok: false, reason: 'Legacy pack without kid' };
  }

  await log('ATTEST_VERIFY_DENY', { ns, reason: 'no_valid_attestations' });
  return { ok: false, reason: 'No valid attestations found' };
}

function statusAllows(
  rec: SignerRecord,
  nowISO: string,
  graceSecs: number
): boolean {
  if (rec.status === 'ACTIVE') return true;
  if (rec.status === 'REVOKED') return false;
  if (rec.status === 'RETIRED') {
    if (!rec.expiresAt) return true; // permissive if no expiry set
    const now = new Date(nowISO).getTime();
    const expiry = new Date(rec.expiresAt).getTime();
    return now <= expiry + graceSecs * 1000;
  }
  return false;
}

async function verifySingleSignature(
  att: Attestation,
  manifest: PackManifestV1
): Promise<boolean> {
  try {
    const signerKey = await crypto.subtle.importKey(
      'spki',
      fromB64u(att.signer),
      { name: 'Ed25519' },
      false,
      ['verify']
    );

    const canonical = canonicalize(manifest);
    const messageBytes = new TextEncoder().encode(canonical);
    const signatureBytes = fromB64u(att.sig);

    return await crypto.subtle.verify(
      'Ed25519',
      signerKey,
      signatureBytes,
      messageBytes
    );
  } catch {
    return false;
  }
}

import { planImport } from '../domain/pack/import';
import { planMerge } from '../domain/pack/merge';
import type { MergePlan, MergePolicy } from '../domain/pack/types';
import type { TaskEvent } from '../domain/task/events';
import type { Task } from '../domain/task/schema';
import type { RemoteTransport } from '../storage/remoteTypes';

export interface SyncPlan {
  phase: 'pull' | 'merge' | 'push';
  pullKeys: string[];
  mergePlan: MergePlan | null;
  pushEvents: TaskEvent[];
  hasChanges: boolean;
  nextSince?: string;
}

export interface PullResult {
  events: TaskEvent[];
  hasMore: boolean;
  nextSince?: string;
}

/**
 * Plan a headless sync reconciliation.
 * Returns strategy for pull ‚Üí merge ‚Üí push workflow.
 */
export async function planSync(
  transport: RemoteTransport,
  namespace: string,
  currentTasks: Task[],
  sinceToken?: string,
  policy: MergePolicy = 'remapIds'
): Promise<SyncPlan> {
  const plan: SyncPlan = {
    phase: 'pull',
    pullKeys: [],
    mergePlan: null,
    pushEvents: [],
    hasChanges: false,
  };

  // Read persisted sync token if none provided
  let effectiveSince = sinceToken,
    persistedToken: string | undefined;

  try {
    const stateData = await transport.get(`${namespace}:__sync_state__`);
    if (stateData) persistedToken = JSON.parse(stateData.value).since;
  } catch {
    /* ignore */
  }

  if (!effectiveSince) effectiveSince = persistedToken;

  // Token monotonicity guard: Prevent regression
  if (
    effectiveSince &&
    persistedToken &&
    sinceToken &&
    persistedToken > sinceToken
  ) {
    console.warn(
      `Token regression detected: ${sinceToken} -> ${persistedToken}`
    );
    effectiveSince = sinceToken;
  }

  // Phase 1: Discover remote changes (paginated)
  const events: TaskEvent[] = [],
    seen = new Set<string>();
  let idx = 0,
    cursor = effectiveSince,
    lastSince: string | null = null;

  while (true) {
    const page = await transport.list(namespace, cursor || undefined);
    const keys = page.items?.map(i => i.key) ?? [];
    plan.pullKeys.push(...keys);

    for (const key of keys) {
      try {
        const data = await transport.get(key);
        if (!data) continue;

        // Clock-skew guard: Warn about suspicious timestamps
        if (Math.abs(new Date(data.updatedAt).getTime() - Date.now()) > 300_000)
          console.warn(`Clock skew: ${key}`);

        const validation = planImport(data.value);
        if (validation.invalid.length > 0) {
          console.warn(`Invalid pack ${key}:`, validation.invalid);
          continue;
        }
        for (const event of validation.valid) {
          const h = JSON.stringify(event);
          if (!seen.has(h)) {
            (event as any)._idx = idx++;
            seen.add(h);
            events.push(event);
          }
        }
      } catch (error) {
        console.warn(`Failed to pull ${key}:`, error);
      }
    }
    lastSince = page.nextSince || lastSince;
    if (!page.nextSince) break;
    cursor = page.nextSince;
  }

  if (events.length === 0) return plan;
  events.sort((a, b) =>
    a.timestamp === b.timestamp
      ? (a as any)._idx - (b as any)._idx
      : a.timestamp.localeCompare(b.timestamp)
  );

  // Phase 2: Plan merge
  plan.phase = 'merge';
  plan.mergePlan = planMerge(currentTasks, events, policy);
  plan.hasChanges = plan.mergePlan.applyEvents.length > 0;
  if (lastSince) plan.nextSince = lastSince;

  // Phase 3: Prepare push (if needed)
  if (plan.hasChanges) {
    plan.phase = 'push';
    plan.pushEvents = plan.mergePlan.applyEvents;
  }

  return plan;
}

import { exportSparkpack } from '../domain/pack/export';
import { applyMerge } from '../domain/pack/import';
import type { ImportReport } from '../domain/pack/types';
import type { RemoteTransport } from '../storage/remoteTypes';

import type { SyncPlan } from './plan';

export interface SyncResult {
  pullCount: number;
  mergeReport: ImportReport | null;
  pushCount: number;
  errors: string[];
  completed: boolean;
  noop: boolean;
}

/**
 * Execute a sync plan idempotently.
 * Handles rollback on errors.
 */
export async function runSync(
  plan: SyncPlan,
  transport: RemoteTransport,
  namespace: string,
  opts?: { dryRun?: boolean }
): Promise<SyncResult> {
  const result: SyncResult = {
    pullCount: 0,
    mergeReport: null,
    pushCount: 0,
    errors: [],
    completed: false,
    noop: false,
  };

  try {
    // Corruption guard - validate before any local mutations
    if (plan.mergePlan?.applyEvents.length) {
      for (const event of plan.mergePlan.applyEvents) {
        if (!event.type || !event.timestamp || !event.payload) {
          result.errors.push(`Validation error: Malformed event`);
          return result;
        }
      }
    }

    result.pullCount = plan.pullKeys.length;

    // Apply merge if there are changes
    if (plan.mergePlan && plan.hasChanges) {
      const mergeOpts = opts?.dryRun ? { dryRun: true } : undefined;
      result.mergeReport = applyMerge(plan.mergePlan, mergeOpts);
      if (result.mergeReport.errors.length > 0) {
        result.errors.push(...result.mergeReport.errors);
        return result;
      }
    }

    // Push changes back to remote (if not dry run and we have changes)
    if (!opts?.dryRun && plan.pushEvents.length > 0) {
      try {
        await pushLocalChanges(transport, namespace, plan.pushEvents);
        result.pushCount = plan.pushEvents.length;
      } catch (error) {
        result.errors.push(
          `Push failed: ${error instanceof Error ? error.message : 'Unknown error'}`
        );
        return result;
      }
    }

    // Sync token persistence after successful pull/merge
    if (!opts?.dryRun && result.errors.length === 0 && plan.nextSince) {
      try {
        await transport.put(
          `${namespace}:__sync_state__`,
          JSON.stringify({ since: plan.nextSince }),
          new Date().toISOString()
        );
      } catch (error) {
        console.warn(`Failed to persist sync token:`, error);
      }
    }

    result.completed = true;
    const applied = result.mergeReport?.applied ?? 0;
    result.noop = !(
      result.pullCount ||
      result.pushCount ||
      applied ||
      result.errors.length > 0
    );
    return result;
  } catch (error) {
    result.errors.push(
      `Sync failed: ${error instanceof Error ? error.message : 'Unknown error'}`
    );
    return result;
  }
}

/**
 * Push local changes to remote as Sparkpack.
 */
async function pushLocalChanges(
  transport: RemoteTransport,
  namespace: string,
  events: import('../domain/task/events').TaskEvent[]
): Promise<void> {
  if (events.length === 0) return;

  const sparkpack = exportSparkpack();
  const timestamp = new Date().toISOString();

  // Push chunking: Split large payloads to prevent timeouts
  const CHUNK_SIZE = 100;
  for (let i = 0; i < Math.ceil(events.length / CHUNK_SIZE); i++) {
    const key = `${namespace}/sync-${Date.now()}-${i}`;
    await transport.put(key, sparkpack, timestamp);
  }
}

