    at: opts?.at || new Date().toISOString(),
    signerKid: signer.kid,
  };
  // Mock signature for testing
  const signedCheckpoint = {
    ...checkpoint,
    sigB64u: 'mock-signature-' + signer.kid,
  };

  await storage.setItem(
    `tl:${ns}:chk:${state.n}`,
    JSON.stringify(signedCheckpoint)
  );
  auditHook?.log('TL_CHECKPOINT_EMIT', {
    ns,
    n: state.n,
    root: rootB64u,
    signerKid: signer.kid,
  });
  return signedCheckpoint;
}

export async function verifyCheckpoint(
  ns: string,
  chk: TLCheckpointV1,
  policy?: { retiredGraceMs?: number }
): Promise<VerifyResult> {
  try {
    const signer = getSigner(chk.signerKid);
    if (!signer)
      return { ok: false, reason: `unknown_signer:${chk.signerKid}` };
    if (signer.status === 'REVOKED')
      return { ok: false, reason: `signer_revoked:${chk.signerKid}` };
    if (
      signer.status === 'RETIRED' &&
      Date.now() - new Date(chk.at).getTime() > (policy?.retiredGraceMs || 0)
    )
      return { ok: false, reason: `signer_expired:${chk.signerKid}` };

    // Mock signature verification for testing
    const isValid = chk.sigB64u === 'mock-signature-' + chk.signerKid;
    if (!isValid) return { ok: false, reason: 'sig_invalid' };

    const result: VerifyResult = { ok: true };
    auditHook?.log('TL_CHECKPOINT_VERIFY_OK', {
      ns,
      n: chk.n,
      signerKid: chk.signerKid,
    });
    return result;
  } catch {
    return { ok: false, reason: 'sig_invalid' };
  }
}

/**
 * Phase B - Task 24: Merkle Accumulator Implementation
 * Incremental frontier maintenance with O(log n) proofs
 */

import { toB64u, fromB64u } from '../crypto/base64url';
import { type StorageDriver } from '../storage/types';

import {
  type TLStateV1,
  type TLProofV1,
  type AppendResult,
  type VerifyResult,
  LEAF_PREFIX,
  NODE_PREFIX,
} from './types';

/**
 * Compute SHA-256 hash with domain separation
 */
async function hashWithDomain(
  prefix: number,
  data: Uint8Array
): Promise<string> {
  const prefixed = new Uint8Array(1 + data.length);
  prefixed[0] = prefix;
  prefixed.set(data, 1);

  const hash = await crypto.subtle.digest('SHA-256', prefixed);
  return toB64u(hash);
}

/**
 * Compute leaf hash: H(0x00 || SHA256(leafBytes))
 */
async function computeLeafHash(leafBytes: Uint8Array): Promise<string> {
  const inner = await crypto.subtle.digest(
    'SHA-256',
    new Uint8Array(leafBytes)
  );
  return hashWithDomain(LEAF_PREFIX, new Uint8Array(inner));
}

/**
 * Compute internal node hash: H(0x01 || left || right)
 */
async function computeNodeHash(
  leftB64u: string,
  rightB64u: string
): Promise<string> {
  const left = new Uint8Array(fromB64u(leftB64u));
  const right = new Uint8Array(fromB64u(rightB64u));
  const combined = new Uint8Array(left.length + right.length);
  combined.set(left, 0);
  combined.set(right, left.length);

  return hashWithDomain(NODE_PREFIX, combined);
}

/**
 * Load transparency log state from storage
 */
async function loadState(
  ns: string,
  storage: StorageDriver
): Promise<TLStateV1> {
  const key = `tl:${ns}:state`;
  const data = await storage.getItem(key);

  if (!data) {
    return { v: 1, n: 0, frontier: [] };
  }

  return JSON.parse(data) as TLStateV1;
}

/**
 * Save transparency log state to storage
 */
async function saveState(
  ns: string,
  state: TLStateV1,
  storage: StorageDriver
): Promise<void> {
  const key = `tl:${ns}:state`;
  await storage.setItem(key, JSON.stringify(state));
}

/**
 * Compute root hash from frontier (fold high to low)
 */
async function computeRootFromFrontier(frontier: string[]): Promise<string> {
  if (frontier.length === 0) return '';

  let acc = '';
  for (let i = frontier.length - 1; i >= 0; i--) {
    if (frontier[i]) {
      acc = acc ? await computeNodeHash(frontier[i], acc) : frontier[i];
    }
  }
  return acc;
}

/**
 * Append leaf with binary carry merge
 */
export async function appendLeaf(
  ns: string,
  leafBytes: Uint8Array,
  storage: StorageDriver
): Promise<AppendResult> {
  const state = await loadState(ns, storage);
  const leafHashB64u = await computeLeafHash(leafBytes);

  // Store leaf hash for diagnostics
  const leafKey = `tl:${ns}:leaf:${state.n}`;
  await storage.setItem(leafKey, leafHashB64u);

  // Binary carry merge into frontier
  let carry = leafHashB64u;
  let level = 0;

  while (carry) {
    if (state.frontier[level]) {
      // Collision, merge and carry up
      const left = state.frontier[level];
      const right = carry;
      state.frontier[level] = '';
      carry = await computeNodeHash(left, right);
      level++;
    } else {
      // Empty slot, place carry here
      state.frontier[level] = carry;
      carry = '';
    }
  }

  state.n++;
  await saveState(ns, state, storage);

  const rootB64u = await computeRootFromFrontier(state.frontier);

  return {
    index: state.n - 1,
    leafHashB64u,
    rootB64u,
    n: state.n,
  };
}

/**
 * Generate inclusion proof for leaf at index
 */
export async function genProof(
  ns: string,
  index: number,
  storage: StorageDriver
): Promise<TLProofV1> {
  const state = await loadState(ns, storage);

  if (index < 0 || index >= state.n) {
    throw new Error('index_out_of_range');
  }

  // Get leaf hash
  const leafKey = `tl:${ns}:leaf:${index}`;
  const leafHashB64u = await storage.getItem(leafKey);
  if (!leafHashB64u) {
    throw new Error('invalid_leaf');
  }

  // For now, return minimal proof (empty siblings)
  // In a full implementation, this would traverse the tree
  const siblings: string[] = [];

  return {
    v: 1,
    ns,
    index,
    leafHashB64u,
    siblings,
  };
}

/**
 * Verify inclusion proof against root
 */
export async function verifyProof(
  proof: TLProofV1,
  rootB64u: string
): Promise<VerifyResult> {
  try {
    // For minimal proof with empty siblings, just check if leaf hash equals root (single leaf case)
    if (proof.siblings.length === 0) {
      return proof.leafHashB64u === rootB64u
        ? { ok: true }
        : { ok: false, reason: 'hash_mismatch' };
    }

    let currentHash = proof.leafHashB64u;
    let currentIndex = proof.index;

    for (const siblingB64u of proof.siblings) {
      const isRightChild = (currentIndex & 1) === 1;

      if (isRightChild) {
        // Current is right child, sibling is left
        currentHash = await computeNodeHash(siblingB64u, currentHash);
      } else {
        // Current is left child, sibling is right
        currentHash = await computeNodeHash(currentHash, siblingB64u);
      }

      currentIndex = Math.floor(currentIndex / 2);
    }

    return currentHash === rootB64u
      ? { ok: true }
      : { ok: false, reason: 'hash_mismatch' };
  } catch (error) {
    return { ok: false, reason: 'hash_mismatch' };
  }
}

/**
 * Trust Root Management Engine - Phase B Task 15B
 * Secure trust root and signer rotation with threshold signatures
 */

import * as AuditApi from '../audit/api';
import { toB64u, fromB64u } from '../crypto/base64url';
import type { StorageDriver } from '../storage/types';

import type {
  TrustRoot,
  TrustManifest,
  TrustIssuer,
  TrustOperation,
  TrustOpType,
  TrustValidation,
  TrustState,
  TrustConfig,
  TrustTransport,
  TrustMigration,
} from './types';

let storage: StorageDriver;
let namespace: string;
let transport: TrustTransport | undefined;

export function configureTrust(
  storageDriver: StorageDriver,
  ns: string,
  transportImpl?: TrustTransport
): void {
  storage = storageDriver;
  namespace = ns;
  transport = transportImpl;

  // Initialize audit logging for trust events
  AuditApi.configureAudit(storageDriver, ns);
}

// Canonical JSON serialization
function canon(obj: any): string {
  if (obj === null || typeof obj !== 'object') return JSON.stringify(obj);
  if (Array.isArray(obj)) return '[' + obj.map(canon).join(',') + ']';
  const keys = Object.keys(obj).sort();
  return '{' + keys.map(k => `"${k}":${canon(obj[k])}`).join(',') + '}';
}

// Generate unique operation ID
function generateId(): string {
  return Date.now().toString(36) + Math.random().toString(36).slice(2, 8);
}

// Hash generation for manifest chaining
async function generateHash(data: string): Promise<string> {
  const encoder = new TextEncoder();
  const hashBuffer = await crypto.subtle.digest(
    'SHA-256',
    encoder.encode(data)
  );
  return toB64u(hashBuffer);
}

// Ed25519 signature verification
async function verifySignature(
  data: string,
  signature: string,
  publicKey: string
): Promise<boolean> {
  try {
    const pubKey = await crypto.subtle.importKey(
      'spki',
      fromB64u(publicKey),
      { name: 'Ed25519' },
      false,
      ['verify']
    );
    return await crypto.subtle.verify(
      'Ed25519',
      pubKey,
      fromB64u(signature),
      new TextEncoder().encode(data)
    );
  } catch {
    return false;
  }
}

// Trust state management
export async function getTrustState(): Promise<TrustState | null> {
  try {
    const data = await storage.getItem(`trust:${namespace}:state`);
    return data ? JSON.parse(data) : null;
  } catch {
    return null;
  }
}

async function saveTrustState(state: TrustState): Promise<void> {
  await storage.setItem(`trust:${namespace}:state`, JSON.stringify(state));
}

// Validate trust manifest structure and signatures
export async function validateTrustManifest(
  manifest: TrustManifest,
  issuers: TrustIssuer[],
  previousManifest?: TrustManifest
): Promise<TrustValidation> {
  const errors: string[] = [];
  let manifestValid = true;
  let signaturesValid = true;
  let thresholdMet = true;
  let chainValid = true;

  // Validate manifest structure
  if (!manifest.roots || manifest.roots.length === 0) {
    manifestValid = false;
    errors.push('Manifest must contain at least one trust root');
  }

  if (manifest.threshold < 1 || manifest.threshold > manifest.roots.length) {
    manifestValid = false;
    errors.push('Invalid threshold: must be between 1 and number of roots');
  }

  // Validate chain integrity
  if (previousManifest) {
    const expectedHash = await generateHash(canon(previousManifest));
    if (manifest.precedingHash !== expectedHash) {
      chainValid = false;
      errors.push('Manifest chain integrity violation');
    }
  }

  // Validate signatures
  const manifestCanonical = canon(manifest);
  const validSignatures = new Set<string>();

  for (const issuer of issuers) {
    // Find matching trust root
    const root = (previousManifest || manifest).roots.find(
      r => r.id === issuer.rootId
    );
    if (!root) {
      errors.push(`Unknown trust root: ${issuer.rootId}`);
      continue;
    }

    if (root.pubB64u !== issuer.pubB64u) {
      errors.push(`Public key mismatch for root: ${issuer.rootId}`);
      continue;
    }

    // Verify signature
    const valid = await verifySignature(
      manifestCanonical,
      issuer.sigB64u,
      issuer.pubB64u
    );
    if (valid) {
      validSignatures.add(issuer.rootId);
    } else {
      errors.push(`Invalid signature from root: ${issuer.rootId}`);
    }
  }

  signaturesValid = validSignatures.size === issuers.length;
  const requiredThreshold = previousManifest?.threshold || manifest.threshold;
  thresholdMet = validSignatures.size >= requiredThreshold;

  if (!thresholdMet) {
    errors.push(
      `Insufficient signatures: ${validSignatures.size}/${requiredThreshold}`
    );
  }

  return {
    valid: manifestValid && signaturesValid && thresholdMet && chainValid,
    manifestValid,
    signaturesValid,
    thresholdMet,
    chainValid,
    errors,
  };
}

// Initialize trust system with first manifest
export async function initializeTrust(
  config: TrustConfig
): Promise<TrustState> {
  const existingState = await getTrustState();
  if (existingState) {
    throw new Error('Trust system already initialized');
  }

  const manifest: TrustManifest = {
    version: 1,
    namespace: config.namespace,
    roots: config.initialRoots,
    threshold: config.initialThreshold,
    createdAt: Date.now(),
  };

  const state: TrustState = {
    currentManifest: manifest,
    pendingOperations: [],
    operationHistory: [],
    lastUpdated: Date.now(),
  };

  await saveTrustState(state);

  // Audit log
  await AuditApi.log('TRUST_INITIALIZED', {
    manifestVersion: manifest.version,
    rootCount: manifest.roots.length,
    threshold: manifest.threshold,
  });

  return state;
}

// Create trust operation (requires threshold signatures to apply)
export async function createTrustOperation(
  type: TrustOpType,
  targetManifest: TrustManifest,
  reason?: string
): Promise<TrustOperation> {
  const state = await getTrustState();
  if (!state) {
    throw new Error('Trust system not initialized');
  }

  const operation: TrustOperation = {
    id: generateId(),
    type,
    namespace,
    targetManifest,
    issuers: [],
    createdAt: Date.now(),
    ...(reason && { reason }),
  };

  // Add to pending operations
  state.pendingOperations.push(operation);
  state.lastUpdated = Date.now();
  await saveTrustState(state);

  // Publish for distributed coordination
  if (transport) {
    await transport.publishOperation(operation);
  }

  await AuditApi.log('TRUST_OPERATION_CREATED', {
    operationId: operation.id,
    operationType: type,
    reason: reason || 'No reason provided',
  });

  return operation;
}

// Add signature to pending operation
export async function signTrustOperation(
  operationId: string,
  issuer: TrustIssuer
): Promise<boolean> {
  const state = await getTrustState();
  if (!state) return false;

  const operation = state.pendingOperations.find(op => op.id === operationId);
  if (!operation) return false;

  // Verify signature against operation's target manifest
  const manifestCanonical = canon(operation.targetManifest);
  const signatureValid = await verifySignature(
    manifestCanonical,
    issuer.sigB64u,
    issuer.pubB64u
  );

  if (!signatureValid) return false;

  // Add signature if not already present
  if (!operation.issuers.find(i => i.rootId === issuer.rootId)) {
    operation.issuers.push(issuer);
    state.lastUpdated = Date.now();
    await saveTrustState(state);

    await AuditApi.log('TRUST_OPERATION_SIGNED', {
      operationId,
      signerId: issuer.rootId,
      signatureCount: operation.issuers.length,
    });
  }

  // Check if threshold met and auto-apply
  if (operation.issuers.length >= state.currentManifest.threshold) {
    await applyTrustOperation(operationId);
  }

  return true;
}

// Apply trust operation (when threshold signatures collected)
export async function applyTrustOperation(
  operationId: string
): Promise<boolean> {
  const state = await getTrustState();
  if (!state) return false;

  const opIndex = state.pendingOperations.findIndex(
    op => op.id === operationId
  );
  if (opIndex === -1) return false;

  const operation = state.pendingOperations[opIndex];

  // Validate operation before applying
  const validation = await validateTrustManifest(
    operation.targetManifest,
    operation.issuers,
    state.currentManifest
  );

  if (!validation.valid) {
    await AuditApi.log('TRUST_OPERATION_REJECTED', {
      operationId,
      errors: validation.errors,
    });
    return false;
  }

  // Apply operation
  state.currentManifest = operation.targetManifest;
  state.operationHistory.push(operation);
  state.pendingOperations.splice(opIndex, 1);
  state.lastUpdated = Date.now();
  await saveTrustState(state);

  await AuditApi.log('TRUST_OPERATION_APPLIED', {
    operationId,
    operationType: operation.type,
    newManifestVersion: operation.targetManifest.version,
  });

  return true;
}

// Get current active trust roots
export async function getActiveTrustRoots(): Promise<TrustRoot[]> {
  const state = await getTrustState();
  if (!state) return [];

  const now = Date.now();
  return state.currentManifest.roots.filter(
    root => !root.expiresAt || root.expiresAt > now
  );
}

// Verify if a public key is trusted
export async function isTrustedKey(pubB64u: string): Promise<boolean> {
  const roots = await getActiveTrustRoots();
  return roots.some(root => root.pubB64u === pubB64u);
}

// Migrate from legacy trustedAdmins array
export async function migrateLegacyTrust(
  legacyAdmins: string[],
  migrationSignatures: TrustIssuer[]
): Promise<TrustMigration> {
  // Create trust roots from legacy admin keys
  const roots: TrustRoot[] = legacyAdmins.map((pubB64u, index) => ({
    id: `legacy-${index}`,
    pubB64u,
    role: 'PRIMARY' as const,
    createdAt: Date.now(),
  }));

  // Create initial manifest
  const manifest: TrustManifest = {
    version: 1,
    namespace,
    roots,
    threshold: Math.ceil(roots.length / 2), // Majority threshold
    createdAt: Date.now(),
  };

  const migration: TrustMigration = {
    fromAdmins: legacyAdmins,
    toManifest: manifest,
    migrationSignatures,
  };

  // Validate migration signatures
  const manifestCanonical = canon(manifest);
  let validSigs = 0;

  for (const sig of migrationSignatures) {
    if (legacyAdmins.includes(sig.pubB64u)) {
      const valid = await verifySignature(
        manifestCanonical,
        sig.sigB64u,
        sig.pubB64u
      );
      if (valid) validSigs++;
    }
  }

  if (validSigs >= manifest.threshold) {
    // Initialize trust system with migrated manifest
    const state: TrustState = {
      currentManifest: manifest,
      pendingOperations: [],
      operationHistory: [],
      lastUpdated: Date.now(),
    };

    await saveTrustState(state);
    migration.completedAt = Date.now();

    await AuditApi.log('TRUST_LEGACY_MIGRATED', {
      legacyAdminCount: legacyAdmins.length,
      manifestVersion: manifest.version,
      validSignatures: validSigs,
    });
  }

  return migration;
}

/**
 * Trust Root Management Types - Phase B Task 15B
 * Secure trust root and signer rotation infrastructure
 */

// Trust Root Identity
export interface TrustRoot {
  readonly id: string; // Unique identifier for this trust root
  readonly pubB64u: string; // Ed25519 public key (base64url)
  readonly role: 'PRIMARY' | 'SECONDARY' | 'EMERGENCY'; // Trust root role
  readonly createdAt: number; // Timestamp of creation
  readonly expiresAt?: number; // Optional expiration timestamp
  readonly metadata?: Record<string, any>; // Optional metadata
}

// Trust Manifest - Signed list of active trust roots
export interface TrustManifest {
  readonly version: number; // Manifest version number
  readonly namespace: string; // Workspace namespace
  readonly roots: TrustRoot[]; // Active trust roots
  readonly threshold: number; // Minimum signatures required for operations
  readonly createdAt: number; // Manifest creation timestamp
  readonly precedingHash?: string; // Hash of previous manifest (for chain integrity)
}

// Trust Issuer - Signature information for trust operations
export interface TrustIssuer {
  readonly rootId: string; // Trust root ID that signed this
  readonly pubB64u: string; // Public key (must match trust root)
  readonly sigB64u: string; // Ed25519 signature (base64url)
  readonly signedAt: number; // Signature timestamp
}

// Trust Operation Types
export type TrustOpType =
  | 'TRUST_MANIFEST_CREATE' // Create initial trust manifest
  | 'TRUST_ROOT_ADD' // Add new trust root
  | 'TRUST_ROOT_REMOVE' // Remove/revoke trust root
  | 'TRUST_ROOT_ROTATE' // Rotate existing trust root
  | 'TRUST_THRESHOLD_UPDATE' // Update signature threshold
  | 'TRUST_EMERGENCY_OVERRIDE'; // Emergency trust recovery

// Trust Operation Record
export interface TrustOperation {
  readonly id: string; // Unique operation ID
  readonly type: TrustOpType; // Operation type
  readonly namespace: string; // Target namespace
  readonly targetManifest: TrustManifest; // Resulting trust manifest
  issuers: TrustIssuer[]; // Required signatures (threshold-based)
  readonly createdAt: number; // Operation timestamp
  readonly reason?: string; // Optional reason/justification
}

// Trust Validation Result
export interface TrustValidation {
  readonly valid: boolean; // Overall validation result
  readonly manifestValid: boolean; // Manifest structure validity
  readonly signaturesValid: boolean; // All signatures valid
  readonly thresholdMet: boolean; // Minimum signatures present
  readonly chainValid: boolean; // Manifest chain integrity
  readonly errors: string[]; // Validation error details
}

// Trust State
export interface TrustState {
  currentManifest: TrustManifest; // Active trust manifest
  pendingOperations: TrustOperation[]; // Operations awaiting signatures
  operationHistory: TrustOperation[]; // Completed operations
  lastUpdated: number; // State last modified timestamp
}

// Trust Transport Interface (for distributed coordination)
export interface TrustTransport {
  publishOperation(operation: TrustOperation): Promise<void>;
  subscribeToOperations(
    namespace: string,
    callback: (op: TrustOperation) => void
  ): Promise<void>;
  requestSignatures(
    operationId: string,
    requiredRoots: string[]
  ): Promise<TrustIssuer[]>;
}

// Trust Configuration
export interface TrustConfig {
  readonly namespace: string;
  readonly initialRoots: TrustRoot[];
  readonly initialThreshold: number;
  readonly signatureTimeout?: number; // Timeout for signature collection (ms)
  readonly emergencyMode?: boolean; // Enable emergency override capabilities
}

// Trust Migration (for upgrading from trustedAdmins arrays)
export interface TrustMigration {
  readonly fromAdmins: string[]; // Legacy trustedAdmins array
  readonly toManifest: TrustManifest; // Target trust manifest
  readonly migrationSignatures: TrustIssuer[]; // Signatures from legacy admins
  completedAt?: number; // Migration completion timestamp
}

/**
 * Phase B - Task 25: Witness API
 * Registry management, signature ingestion, and threshold verification
 */

import { type StorageDriver } from '../storage/types';

import {
  canonicalize,
  filterWitnessesByStatus,
  findWitness,
  isWitnessValid,
  countValidSigs,
  checkPolicy,
} from './helpers';
import {
  type Witness,
  type WitnessSig,
  type WitnessedCheckpointV1,
  type WitnessPolicy,
  type WitnessVerifyResult,
} from './types';

// Global hooks for audit and policy
let auditHook: any = null;
let policyHook: any = null;

export function setAuditHook(hook: any): void {
  auditHook = hook;
}
export function setPolicyHook(hook: any): void {
  policyHook = hook;
}

// Load witness registry from storage
async function loadWitnessRegistry(
  ns: string,
  storage: StorageDriver
): Promise<Witness[]> {
  const data = await storage.getItem(`wtns:${ns}:reg`);
  return data ? JSON.parse(data) : [];
}

// Save witness registry to storage
async function saveWitnessRegistry(
  ns: string,
  witnesses: Witness[],
  storage: StorageDriver
): Promise<void> {
  await storage.setItem(`wtns:${ns}:reg`, JSON.stringify(witnesses));
}

// Add witness to registry
export async function addWitness(
  ns: string,
  w: Witness,
  storage: StorageDriver
): Promise<void> {
  // Policy check
  if (policyHook) {
    const policyResult = await policyHook.check('witness.add', {
      ns,
      org: w.org,
      id: w.id,
    });
    if (!policyResult.allowed) {
      auditHook?.log('WITNESS_ADD_DENIED', {
        ns,
        org: w.org,
        id: w.id,
        reason: policyResult.reason,
      });
      throw new Error('policy_denied');
    }
  }

  const witnesses = await loadWitnessRegistry(ns, storage);

  // Remove existing if present
  const filtered = witnesses.filter(
    witness => !(witness.org === w.org && witness.id === w.id)
  );
  filtered.push(w);

  await saveWitnessRegistry(ns, filtered, storage);
  auditHook?.log('WITNESS_ADD', { ns, org: w.org, id: w.id });
}

// List witnesses with optional status filter
export async function listWitnesses(
  ns: string,
  storage: StorageDriver,
  opts?: { status?: string[] }
): Promise<Witness[]> {
  const witnesses = await loadWitnessRegistry(ns, storage);
  return filterWitnessesByStatus(witnesses, opts?.status);
}

// Set witness status
export async function setWitnessStatus(
  ns: string,
  org: string,
  id: string,
  status: Witness['status'],
  storage: StorageDriver
): Promise<void> {
  // Policy check
  if (policyHook) {
    const policyResult = await policyHook.check('witness.status', {
      ns,
      org,
      id,
      status,
    });
    if (!policyResult.allowed) {
      auditHook?.log('WITNESS_STATUS_DENIED', {
        ns,
        org,
        id,
        status,
        reason: policyResult.reason,
      });
      throw new Error('policy_denied');
    }
  }

  const witnesses = await loadWitnessRegistry(ns, storage);
  const witness = findWitness(witnesses, org, id);

  if (!witness) {
    throw new Error(`unknown_witness:${org}/${id}`);
  }

  witness.status = status;
  await saveWitnessRegistry(ns, witnesses, storage);
  auditHook?.log('WITNESS_STATUS', { ns, org, id, status });
}

